  INSERT INTO words (word, word_type, description)VALUES  (    'Banishment', 'n.', 'The act of banishing, or the state of being banished.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banister', 'n.', 'A stringed musical instrument having a head and neck like\n   the guitar, and its body like a tambourine. It has five strings, and is\n   played with the fingers and hands.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A mound, pile, or ridge of earth, raised above the\n   surrounding level; hence, anything shaped like a mound or ridge of\n   earth; as, a bank of clouds; a bank of snow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A steep acclivity, as the slope of a hill, or the side of a\n   ravine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The margin of a watercourse; the rising ground bordering a\n   lake, river, or sea, or forming the edge of a cutting, or other hollow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'An elevation, or rising ground, under the sea; a shoal,\n   shelf, or shallow; as, the banks of Newfoundland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The face of the coal at which miners are working.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A deposit of ore or coal, worked by excavations above water\n   level.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The ground at the top of a shaft; as, ores are brought to\n   bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banked', 'imp. & p. p.', 'of Bank');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banking', 'p. pr. & vb. n.', 'of Bank');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. t.', 'To raise a mound or dike about; to inclose, defend, or\n   fortify with a bank; to embank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. t.', 'To heap or pile up; as, to bank sand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. t.', 'To pass by the banks of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A bench, as for rowers in a galley; also, a tier of oars.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The bench or seat upon which the judges sit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The regular term of a court of law, or the full court sitting\n   to hear arguments upon questions of law, as distinguished from a\n   sitting at Nisi Prius, or a court held for jury trials. See Banc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A sort of table used by printers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A bench, or row of keys belonging to a keyboard, as in an\n   organ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'An establishment for the custody, loan, exchange, or issue,\n   of money, and for facilitating the transmission of funds by drafts or\n   bills of exchange; an institution incorporated for performing one or\n   more of such functions, or the stockholders (or their representatives,\n   the directors); acting in their corporate capacity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The building or office used for banking purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'A fund from deposits or contributions, to be used in\n   transacting business; a joint stock or capital.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'The sum of money or the checks which the dealer or banker has\n   as a fund, from which to draw his stakes and pay his losses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'n.', 'In certain games, as dominos, a fund of pieces from which the\n   players are allowed to draw.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. t.', 'To deposit in a bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. i.', 'To keep a bank; to carry on the business of a banker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank', 'v. i.', 'To deposit money in a bank; to have an account with a\n   banker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankable', 'a.', 'Receivable at a bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank bill', '', 'In America (and formerly in England); a promissory note of\n   a bank payable to the bearer on demand, and used as currency; a bank\n   note.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank bill', '', 'In England, a note, or a bill of exchange, of a bank,\n   payable to order, and usually at some future specified time. Such bills\n   are negotiable, but form, in the strict sense of the term, no part of\n   the currency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank book', '', 'A book kept by a depositor, in which an officer of a bank\n   enters the debits and credits of the depositors account with the bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'One who conducts the business of banking; one who,\n   individually, or as a member of a company, keeps an establishment for\n   the deposit or loan of money, or for traffic in money, bills of\n   exchange, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'A money changer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'The dealer, or one who keeps the bank in a gambling house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'A vessel employed in the cod fishery on the banks of\n   Newfoundland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'A ditcher; a drain digger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banker', 'n.', 'The stone bench on which masons cut or square their work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankeress', 'n.', 'A female banker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banking', 'n.', 'The business of a bank or of a banker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank note', '', 'A promissory note issued by a bank or banking company,\n   payable to bearer on demand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank note', '', 'Formerly, a promissory note made by a banker, or banking\n   company, payable to a specified person at a fixed date; a bank bill.\n   See Bank bill, 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank note', '', 'A promissory note payable at a bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'n.', 'A trader who secretes himself, or does certain other acts\n   tending to defraud his creditors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'n.', 'A trader who becomes unable to pay his debts; an\n   insolvent trader; popularly, any person who is unable to pay his debts;\n   an insolvent person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'n.', 'A person who, in accordance with the terms of a law\n   relating to bankruptcy, has been judicially declared to be unable to\n   meet his liabilities.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'a.', 'Being a bankrupt or in a condition of bankruptcy; unable\n   to pay, or legally discharged from paying, ones debts; as, a bankrupt\n   merchant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'a.', 'Depleted of money; not having the means of meeting\n   pecuniary liabilities; as, a bankrupt treasury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'a.', 'Relating to bankrupts and bankruptcy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'a.', 'Destitute of, or wholly wanting (something once\n   possessed, or something one should possess).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupted', 'imp. & p. p.', 'of Bankrupt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupting', 'p. pr. & vb. n.', 'of Bankrupt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankrupt', 'v. t.', 'To make bankrupt; to bring financial ruin upon; to\n   impoverish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankruptcies', 'pl. ', 'of Bankruptcy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankruptcy', 'n.', 'The state of being actually or legally bankrupt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankruptcy', 'n.', 'The act or process of becoming a bankrupt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankruptcy', 'n.', 'Complete loss; -- followed by of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bankside', 'n.', 'The slope of a bank, especially of the bank of a steam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank-sided', 'a.', 'Having sides inclining inwards, as a ship; -- opposed\n   to wall-sided.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bank swallow', '', 'See under 1st Bank, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banlieue', 'n.', 'The territory without the walls, but within the legal\n   limits, of a town or city.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banner', 'n.', 'A kind of flag attached to a spear or pike by a crosspiece,\n   and used by a chief as his standard in battle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banner', 'n.', 'A large piece of silk or other cloth, with a device or\n   motto, extended on a crosspiece, and borne in a procession, or\n   suspended in some conspicuous place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banner', 'n.', 'Any flag or standard; as, the star-spangled banner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bannered', 'a.', 'Furnished with, or bearing, banners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banneret', 'n.', 'Originally, a knight who led his vassals into the field\n   under his own banner; -- commonly used as a title of rank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banneret', 'n.', 'A title of rank, conferred for heroic deeds, and hence,\n   an order of knighthood; also, the person bearing such title or rank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banneret', 'n.', 'A civil officer in some Swiss cantons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banneret', 'n.', 'A small banner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bannerol', 'n.', 'A banderole; esp. a banner displayed at a funeral\n   procession and set over the tomb. See Banderole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bannition', 'n.', 'The act of expulsion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bannock', 'n.', 'A kind of cake or bread, in shape flat and roundish,\n   commonly made of oatmeal or barley meal and baked on an iron plate, or\n   griddle; -- used in Scotland and the northern counties of England.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banns', 'n. pl.', 'Notice of a proposed marriage, proclaimed in a church,\n   or other place prescribed by law, in order that any person may object,\n   if he knows of just cause why the marriage should not take place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquet', 'n.', 'A feast; a sumptuous entertainment of eating and drinking;\n   often, a complimentary or ceremonious feast, followed by speeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquet', 'n.', 'A dessert; a course of sweetmeats; a sweetmeat or\n   sweetmeats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banqueted', 'imp. & p. p.', 'of Banquet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banqueting', 'p. pr. & vb. n.', 'of Banquet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquet', 'v. t.', 'To treat with a banquet or sumptuous entertainment of\n   food; to feast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquet', 'v. i.', 'To regale ones self with good eating and drinking; to\n   feast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquet', 'v. i.', 'To partake of a dessert after a feast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquetter', 'n.', 'One who banquets; one who feasts or makes feasts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquette', 'n.', 'A raised way or foot bank, running along the inside of a\n   parapet, on which musketeers stand to fire upon the enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banquette', 'n.', 'A narrow window seat; a raised shelf at the back or the\n   top of a buffet or dresser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banshee', 'n.', 'Alt. of Banshie');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banshie', 'n.', 'A supernatural being supposed by the Irish and Scotch\n   peasantry to warn a family of the speedy death of one of its members,\n   by wailing or singing in a mournful voice under the windows of the\n   house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banstickle', 'n.', 'A small fish, the three-spined stickleback.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantam', 'n.', 'A variety of small barnyard fowl, with feathered legs,\n   probably brought from Bantam, a district of Java.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantam work', '', 'Carved and painted work in imitation of Japan ware.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banteng', 'n.', 'The wild ox of Java (Bibos Banteng).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantered', 'imp. & p. p.', 'of Banter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantering', 'p. pr. & vb. n.', 'of Banter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banter', 'v. t.', 'To address playful good-natured ridicule to, -- the\n   person addressed, or something pertaining to him, being the subject of\n   the jesting; to rally; as, he bantered me about my credulity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banter', 'v. t.', 'To jest about; to ridicule in speaking of, as some\n   trait, habit, characteristic, and the like.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banter', 'v. t.', 'To delude or trick, -- esp. by way of jest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banter', 'v. t.', 'To challenge or defy to a match.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banter', 'n.', 'The act of bantering; joking or jesting; humorous or\n   good-humored raillery; pleasantry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banterer', 'n.', 'One who banters or rallies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantingism', 'n.', 'A method of reducing corpulence by avoiding food\n   containing much farinaceous, saccharine, or oily matter; -- so called\n   from William Banting of London.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bantling', 'n.', 'A young or small child; an infant. [Slightly contemptuous\n   or depreciatory.]');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banxring', 'n.', 'An East Indian insectivorous mammal of the genus Tupaia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Banyan', 'n.', 'A tree of the same genus as the common fig, and called the\n   Indian fig (Ficus Indica); whose branches send shoots to the ground,\n   which take root and become additional trunks, until it may be the tree\n   covers some acres of ground and is able to shelter thousands of men.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baobab', 'n.', 'A gigantic African tree (Adansonia digitata); also\n   naturalized in India. See Adansonia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baphomet', 'n.', 'An idol or symbolical figure which the Templars were\n   accused of using in their mysterious rites.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptism', 'v. i.', 'The act of baptizing; the application of water to a\n   person, as a sacrament or religious ceremony, by which he is initiated\n   into the visible church of Christ. This is performed by immersion,\n   sprinkling, or pouring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptismal', 'a.', 'Pertaining to baptism; as, baptismal vows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptismally', 'adv.', 'In a baptismal manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptist', 'n.', 'One who administers baptism; -- specifically applied to\n   John, the forerunner of Christ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptist', 'n.', 'One of a denomination of Christians who deny the validity\n   of infant baptism and of sprinkling, and maintain that baptism should\n   be administered to believers alone, and should be by immersion. See\n   Anabaptist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptisteries', 'pl. ', 'of Baptistry');
  INSERT INTO words (word, word_type, description)VALUES  (    '-tries', 'pl. ', 'of Baptistry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptistery', 'n.', 'Alt. of Baptistry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptistry', 'n.', 'In early times, a separate building, usually polygonal,\n   used for baptismal services. Small churches were often changed into\n   baptisteries when larger churches were built near.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptistry', 'n.', 'A part of a church containing a font and used for\n   baptismal services.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptistic', 'a.', 'Of or for baptism; baptismal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptistical', 'a.', 'Baptistic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptizable', 'a.', 'Capable of being baptized; fit to be baptized.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptization', 'n.', 'Baptism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptized', 'imp. & p. p.', 'of Baptize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptizing', 'p. pr. & vb. n.', 'of Baptize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptize', 'v. t.', 'To administer the sacrament of baptism to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptize', 'v. t.', 'To christen ( because a name is given to infants at\n   their baptism); to give a name to; to name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptize', 'v. t.', 'To sanctify; to consecrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptizement', 'n.', 'The act of baptizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baptizer', 'n.', 'One who baptizes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A piece of wood, metal, or other material, long in proportion\n   to its breadth or thickness, used as a lever and for various other\n   purposes, but especially for a hindrance, obstruction, or fastening;\n   as, the bars of a fence or gate; the bar of a door.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'An indefinite quantity of some substance, so shaped as to be\n   long in proportion to its breadth and thickness; as, a bar of gold or\n   of lead; a bar of soap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'Anything which obstructs, hinders, or prevents; an\n   obstruction; a barrier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A bank of sand, gravel, or other matter, esp. at the mouth of\n   a river or harbor, obstructing navigation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'Any railing that divides a room, or office, or hall of\n   assembly, in order to reserve a space for those having special\n   privileges; as, the bar of the House of Commons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'The railing that incloses the place which counsel occupy in\n   courts of justice. Hence, the phrase at the bar of the court signifies\n   in open court.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'The place in court where prisoners are stationed for\n   arraignment, trial, or sentence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'The whole body of lawyers licensed in a court or district; the\n   legal profession.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A special plea constituting a sufficient answer to plaintiffs\n   action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'Any tribunal; as, the bar of public opinion; the bar of God.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A barrier or counter, over which liquors and food are passed\n   to customers; hence, the portion of the room behind the counter where\n   liquors for sale are kept.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'An ordinary, like a fess but narrower, occupying only one\n   fifth part of the field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A broad shaft, or band, or stripe; as, a bar of light; a bar\n   of color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A vertical line across the staff. Bars divide the staff into\n   spaces which represent measures, and are themselves called measures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'The space between the tusks and grinders in the upper jaw of a\n   horse, in which the bit is placed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'The part of the crust of a horses hoof which is bent inwards\n   towards the frog at the heel on each side, and extends into the center\n   of the sole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A drilling or tamping rod.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A vein or dike crossing a lode.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A gatehouse of a castle or fortified town.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'A slender strip of wood which divides and supports the glass\n   of a window; a sash bar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barred', 'imp. & p. p.', 'of Bar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barring', 'p. pr. & vb. n.', 'of Bar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'To fasten with a bar; as, to bar a door or gate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'To restrict or confine, as if by a bar; to hinder; to\n   obstruct; to prevent; to prohibit; as, to bar the entrance of evil;\n   distance bars our intercourse; the statute bars my right; the right is\n   barred by time; a release bars the plaintiffs recovery; -- sometimes\n   with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'To except; to exclude by exception.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar', 'n.', 'To cross with one or more stripes or lines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'Beard, or that which resembles it, or grows in the place of\n   it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'A muffler, worn by nuns and mourners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'Paps, or little projections, of the mucous membrane, which\n   mark the opening of the submaxillary glands under the tongue in horses\n   and cattle. The name is mostly applied when the barbs are inflamed and\n   swollen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'The point that stands backward in an arrow, fishhook, etc.,\n   to prevent it from being easily extracted. Hence: Anything which stands\n   out with a sharp point obliquely or crosswise to something else.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'A bit for a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'One of the side branches of a feather, which collectively\n   constitute the vane. See Feather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'A southern name for the kingfishes of the eastern and\n   southeastern coasts of the United States; -- also improperly called\n   whiting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'n.', 'A hair or bristle ending in a double hook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbed', 'imp. & p. p.', 'of Barb');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbing', 'p. pr. & vb. n.', 'of Barb');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barb', 'v. t.', 'To shave or dress the beard of.');
