  INSERT INTO words (word, word_type, description)VALUES  (    'Barbara', 'n.', 'The first word in certain mnemonic lines which represent\n   the various forms of the syllogism. It indicates a syllogism whose\n   three propositions are universal affirmatives.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbaresque', 'a.', 'Barbaric in form or style; as, barbaresque\n   architecture.');
