  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarian', 'n.', 'A cruel, savage, brutal man; one destitute of pity or\n   humanity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarian', 'a.', 'Of, or pertaining to, or resembling, barbarians; rude;\n   uncivilized; barbarous; as, barbarian governments or nations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbaic', 'a.', 'Of, or from, barbarian nations; foreign; -- often with\n   reference to barbarous nations of east.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbaic', 'a.', 'Of or pertaining to, or resembling, an uncivilized person\n   or people; barbarous; barbarian; destitute of refinement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarism', 'n.', 'An uncivilized state or condition; rudeness of manners;\n   ignorance of arts, learning, and literature; barbarousness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarism', 'n.', 'A barbarous, cruel, or brutal action; an outrage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarism', 'n.', 'An offense against purity of style or language; any form\n   of speech contrary to the pure idioms of a particular language. See\n   Solecism.');
