  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarous', 'a.', 'Cruel; ferocious; inhuman; merciless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarous', 'a.', 'Contrary to the pure idioms of a language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarously', 'adv.', 'In a barbarous manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbarousness', 'n.', 'The quality or state of being barbarous; barbarity;\n   barbarism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbary', 'n.', 'The countries on the north coast of Africa from Egypt to\n   the Atlantic. Hence: A Barbary horse; a barb. [Obs.] Also, a kind of\n   pigeon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbastel', 'n.', 'A European bat (Barbastellus communis); with hairy lips.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbate', 'a.', 'Bearded; beset with long and weak hairs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbated', 'a.', 'Having barbed points.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecue', 'n.', 'A hog, ox, or other large animal roasted or broiled whole\n   for a feast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecue', 'n.', 'A social entertainment, where many people assemble,\n   usually in the open air, at which one or more large animals are roasted\n   or broiled whole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecue', 'n.', 'A floor, on which coffee beans are sun-dried.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecued', 'imp. & p. p.', 'of Barbecue');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecuing', 'p. pr. & vb. n.', 'of Barbecue');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecue', 'v. t.', 'To dry or cure by exposure on a frame or gridiron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbecue', 'v. t.', 'To roast or broil whole, as an ox or hog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbed', 'a.', 'Accoutered with defensive armor; -- said of a horse. See\n   Barded ( which is the proper form.)');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbed', 'a.', 'Furnished with a barb or barbs; as, a barbed arrow; barbed\n   wire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbel', 'n.', 'A slender tactile organ on the lips of certain fished.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbel', 'n.', 'A large fresh-water fish ( Barbus vulgaris) found in many\n   European rivers. Its upper jaw is furnished with four barbels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbel', 'n.', 'Barbs or paps under the tongued of horses and cattle. See\n   1st Barb, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbellate', 'a.', 'Having short, stiff hairs, often barbed at the point.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbellulate', 'a.', 'Barbellate with diminutive hairs or barbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barber', 'n.', 'One whose occupation it is to shave or trim the beard, and\n   to cut and dress the hair of his patrons.');
