  INSERT INTO words (word, word_type, description)VALUES  (    'Barberry', 'n.', 'A shrub of the genus Berberis, common along roadsides and\n   in neglected fields. B. vulgaris is the species best known; its oblong\n   red berries are made into a preserve or sauce, and have been deemed\n   efficacious in fluxes and fevers. The bark dyes a fine yellow, esp. the\n   bark of the root.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbet', 'n.', 'A variety of small dog, having long curly hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbet', 'n.', 'A bird of the family Bucconidae, allied to the Cuckoos,\n   having a large, conical beak swollen at the base, and bearded with five\n   bunches of stiff bristles; the puff bird. It inhabits tropical America\n   and Africa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbet', 'n.', 'A larva that feeds on aphides.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbette', 'n.', 'A mound of earth or a platform in a fortification, on\n   which guns are mounted to fire over the parapet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbican', 'n.', 'Alt. of Barbacan');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbacan', 'n.', 'A tower or advanced work defending the entrance to a\n   castle or city, as at a gate or bridge. It was often large and strong,\n   having a ditch and drawbridge of its own.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbacan', 'n.', 'An opening in the wall of a fortress, through which\n   missiles were discharged upon an enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbicanage', 'n.', 'Alt. of Barbacanage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbacanage', 'n.', 'Money paid for the support of a barbican.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbicel', 'n.', 'One of the small hooklike processes on the barbules of\n   feathers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbiers', 'n.', 'A variety of paralysis, peculiar to India and the Malabar\n   coast; -- considered by many to be the same as beriberi in chronic\n   form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbigerous', 'a.', 'Having a beard; bearded; hairy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbiton', 'n.', 'An ancient Greek instrument resembling a lyre.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbituric acid', '', 'A white, crystalline substance, CH2(CO.NH)2.CO,\n   derived from alloxantin, also from malonic acid and urea, and regarded\n   as a substituted urea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barble', 'n.', 'See Barbel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbotine', 'n.', 'A paste of clay used in decorating coarse pottery in\n   relief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbre', 'a.', 'Barbarian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barbule', 'n.', 'A very minute barb or beard.');
