  INSERT INTO words (word, word_type, description)VALUES  (    'Barcarolle', 'n.', 'A popular song or melody sung by Venetian gondoliers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barcarolle', 'n.', 'A piece of music composed in imitation of such a song.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barcon', 'n.', 'A vessel for freight; -- used in Mediterranean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bard', 'n.', 'A professional poet and singer, as among the ancient Celts,\n   whose occupation was to compose and sing verses in honor of the heroic\n   achievements of princes and brave men.');
