  INSERT INTO words (word, word_type, description)VALUES  (    'Barde', 'pl.', 'A thin slice of fat bacon used to cover any meat or game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bard', 'v. t.', 'To cover (meat or game) with a thin slice of fat bacon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barded', 'p.a.', 'Accoutered with defensive armor; -- said of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barded', 'p.a.', 'Wearing rich caparisons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bardic', 'a.', 'Of or pertaining to bards, or their poetry.');
