  INSERT INTO words (word, word_type, description)VALUES  (    'Bardism', 'n.', 'The system of bards; the learning and maxims of bards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bardling', 'n.', 'An inferior bard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bardship', 'n.', 'The state of being a bard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'a.', 'Without clothes or covering; stripped of the usual covering;\n   naked; as, his body is bare; the trees are bare.');
