  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'a.', 'Plain; simple; unadorned; without polish; bald; meager.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'a.', 'Destitute; indigent; empty; unfurnished or scantily\n   furnished; -- used with of (rarely with in) before the thing wanting or\n   taken away; as, a room bare of furniture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'a.', 'Threadbare; much worn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'a.', 'Mere; alone; unaccompanied by anything else; as, a bare\n   majority.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'n.', 'Surface; body; substance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', 'n.', 'That part of a roofing slate, shingle, tile, or metal plate,\n   which is exposed to the weather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bared', 'imp. & p. p.', 'of Bare');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baring', 'p. pr. & vb. n.', 'of Bare');
