  INSERT INTO words (word, word_type, description)VALUES  (    'Barebone', 'n.', 'A very lean person; one whose bones show through the\n   skin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barefaced', 'a.', 'With the face uncovered; not masked.');
