  INSERT INTO words (word, word_type, description)VALUES  (    'Barefacedly', 'adv.', 'Openly; shamelessly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barefacedness', 'n.', 'The quality of being barefaced; shamelessness;\n   assurance; audaciousness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barefoot', 'a. & adv.', 'With the feet bare; without shoes or stockings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barefooted', 'a.', 'Having the feet bare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barege', 'n.', 'A gauzelike fabric for ladies dresses, veils, etc. of\n   worsted, silk and worsted, or cotton and worsted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barehanded', 'n.', 'Having bare hands.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bareheaded', 'a. & adv.', 'Alt. of Barehead');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barehead', 'a. & adv.', 'Having the head uncovered; as, a bareheaded girl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barelegged', 'a.', 'Having the legs bare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barely', 'adv.', 'Without covering; nakedly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barely', 'adv.', 'Without concealment or disguise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barely', 'adv.', 'Merely; only.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barely', 'adv.', 'But just; without any excess; with nothing to spare ( of\n   quantity, time, etc.); hence, scarcely; hardly; as, there was barely\n   enough for all; he barely escaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barenecked', 'a.', 'Having the neck bare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bareness', 'n.', 'The state of being bare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baresark', 'n.', 'A Berserker, or Norse warrior who fought without armor,\n   or shirt of mail. Hence, adverbially: Without shirt of mail or armor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barfish', 'n.', 'Same as Calico bass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barful', 'a.', 'Full of obstructions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'n.', 'An agreement between parties concerning the sale of\n   property; or a contract by which one party binds himself to transfer\n   the right to some property for a consideration, and the other party\n   binds himself to receive the property and pay the consideration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'n.', 'An agreement or stipulation; mutual pledge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'n.', 'A purchase; also ( when not qualified); a gainful\n   transaction; an advantageous purchase; as, to buy a thing at a bargain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'n.', 'The thing stipulated or purchased; also, anything bought\n   cheap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'n.', 'To make a bargain; to make a contract for the exchange of\n   property or services; -- followed by with and for; as, to bargain with\n   a farmer for a cow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargained', 'imp. & p. p.', 'of Bargain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargaining', 'p. pr. & vb. n.', 'of Bargain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargain', 'v. t.', 'To transfer for a consideration; to barter; to trade;\n   as, to bargain one horse for another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargainee', 'v. i.', 'The party to a contract who receives, or agrees to\n   receive, the property sold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargainer', 'n.', 'One who makes a bargain; -- sometimes in the sense of\n   bargainor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargainor', 'n.', 'One who makes a bargain, or contracts with another;\n   esp., one who sells, or contracts to sell, property to another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barge', 'n.', 'A pleasure boat; a vessel or boat of state, elegantly\n   furnished and decorated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barge', 'n.', 'A large, roomy boat for the conveyance of passengers or\n   goods; as, a ships barge; a charcoal barge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barge', 'n.', 'A large boat used by flag officers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barge', 'n.', 'A double-decked passenger or freight vessel, towed by a\n   steamboat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barge', 'n.', 'A large omnibus used for excursions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargeboard', 'n.', 'A vergeboard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargecourse', 'n.', 'A part of the tiling which projects beyond the\n   principal rafters, in buildings where there is a gable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargee', 'n.', 'A bargeman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargeman', 'n.', 'The man who manages a barge, or one of the crew of a\n   barge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bargemastter', 'n.', 'The proprietor or manager of a barge, or one of the\n   crew of a barge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barger', 'n.', 'The manager of a barge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barghest', 'n.', 'A goblin, in the shape of a large dog, portending\n   misfortune.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baria', 'n.', 'Baryta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baric', 'a.', 'Of or pertaining to barium; as, baric oxide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baric', 'a.', 'Of or pertaining to weight, esp. to the weight or pressure\n   of the atmosphere as measured by the barometer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barilla', 'n.', 'A name given to several species of Salsola from which soda\n   is made, by burning the barilla in heaps and lixiviating the ashes.');
