  INSERT INTO words (word, word_type, description)VALUES  (    'Barillet', 'n.', 'A little cask, or something resembling one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bar iron', '', 'See under Iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barite', 'n.', 'Native sulphate of barium, a mineral occurring in\n   transparent, colorless, white to yellow crystals (generally tabular);\n   also in granular form, and in compact massive forms resembling marble.\n   It has a high specific gravity, and hence is often called heavy spar.\n   It is a common mineral in metallic veins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'a. & n.', 'See Barytone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barium', 'n.', 'One of the elements, belonging to the alkaline earth group;\n   a metal having a silver-white color, and melting at a very high\n   temperature. It is difficult to obtain the pure metal, from the\n   facility with which it becomes oxidized in the air. Atomic weight, 137.\n   Symbol, Ba. Its oxide called baryta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bard', 'n.', 'The exterior covering of the trunk and branches of a tree;\n   the rind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bard', 'n.', 'Specifically, Peruvian bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barked', 'imp. & p. p.', 'of Bark');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barking', 'p. pr. & vb. n.', 'of Bark');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. t.', 'To strip the bark from; to peel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. t.', 'To abrade or rub off any outer covering from; as to bark\n   ones heel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. t.', 'To girdle. See Girdle, v. t., 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. t.', 'To cover or inclose with bark, or as with bark; as, to\n   bark the roof of a hut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. i.', 'To make a short, loud, explosive noise with the vocal\n   organs; -- said of some animals, but especially of dogs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'v. i.', 'To make a clamor; to make importunate outcries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'n.', 'The short, loud, explosive sound uttered by a dog; a similar\n   sound made by some other animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark', 'n.', 'Alt. of Barque');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barque', 'n.', 'Formerly, any small sailing vessel, as a pinnace, fishing\n   smack, etc.; also, a rowing boat; a barge. Now applied poetically to a\n   sailing vessel or boat of any kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barque', 'n.', 'A three-masted vessel, having her foremast and mainmast\n   square-rigged, and her mizzenmast schooner-rigged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkantine', 'n.', 'Same as Barkentine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark beetle', '', 'A small beetle of many species (family Scolytidae);\n   which in the larval state bores under or in the bark of trees, often\n   doing great damage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkbound', 'a.', 'Prevented from growing, by having the bark too firm or\n   close.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkeeper', 'n.', 'One who keeps or tends a bar for the sale of liquors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barken', 'a.', 'Made of bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkentine', 'n.', 'A threemasted vessel, having the foremast\n   square-rigged, and the others schooner-rigged. [Spelled also\n   barquentine, barkantine, etc.] See Illust. in Append.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barker', 'n.', 'An animal that barks; hence, any one who clamors\n   unreasonably.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barker', 'n.', 'One who stands at the doors of shops to urg/ passers by to\n   make purchases.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barker', 'n.', 'A pistol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barker', 'n.', 'The spotted redshank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barker', 'n.', 'One who strips trees of their bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkers mill', '', 'A machine, invented in the 17th century, worked by a\n   form of reaction wheel. The water flows into a vertical tube and gushes\n   from apertures in hollow horizontal arms, causing the machine to\n   revolve on its axis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkery', 'n.', 'A tanhouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barking irons', '', 'Instruments used in taking off the bark of trees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barking irons', '', 'A pair of pistols.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barkless', 'a.', 'Destitute of bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bark louse', '', 'An insect of the family Coccidae, which infests the bark\n   of trees and vines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barky', 'a.', 'Covered with, or containing, bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barley', 'n.', 'A valuable grain, of the family of grasses, genus Hordeum,\n   used for food, and for making malt, from which are prepared beer, ale,\n   and whisky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barleybrake', 'n.', 'Alt. of Barleybreak');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barleybreak', 'n.', 'An ancient rural game, commonly played round stacks of\n   barley, or other grain, in which some of the party attempt to catch\n   others who run from a goal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barley-bree', 'n.', 'Liquor made from barley; strong ale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barleycorn', 'n.', 'A grain or \"corn\" of barley.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barleycorn', 'n.', 'Formerly , a measure of length, equal to the average\n   length of a grain of barley; the third part of an inch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barm', 'n.', 'Foam rising upon beer, or other malt liquors, when\n   fermenting, and used as leaven in making bread and in brewing; yeast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barm', 'n.', 'The lap or bosom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmaid', 'n.', 'A girl or woman who attends the customers of a bar, as in\n   a tavern or beershop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmaster', 'n.', 'Formerly, a local judge among miners; now, an officer of\n   the barmote.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmcloth', 'n.', 'Apron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmecidal', 'a.', 'Unreal; illusory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmecide', 'n.', 'One who proffers some illusory advantage or benefit.\n   Also used as an adj.: Barmecidal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barmote', 'n.', 'A court held in Derbyshire, in England, for deciding\n   controversies between miners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balmy', 'a.', 'Full of barm or froth; in a ferment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barn', 'n.', 'A covered building used chiefly for storing grain, hay, and\n   other productions of a farm. In the United States a part of the barn is\n   often used for stables.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barn', 'v. t.', 'To lay up in a barn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barn', 'n.', 'A child. [Obs.] See Bairn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnabite', 'n.', 'A member of a religious order, named from St. Barnabas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnacle', 'n.', 'Any cirriped crustacean adhering to rocks, floating\n   timber, ships, etc., esp. (a) the sessile species (genus Balanus and\n   allies); and (b) the stalked or goose barnacles (genus Lepas and\n   allies). See Cirripedia, and Goose barnacle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnacle', 'n.', 'A bernicle goose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnacle', 'n.', 'An instrument for pinching a horses nose, and thus\n   restraining him.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnacle', 'sing.', 'Spectacles; -- so called from their resemblance to the\n   barnacles used by farriers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barnyard', 'n.', 'A yard belonging to a barn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barocco', 'a.', 'See Baroque.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barograph', 'n.', 'An instrument for recording automatically the variations\n   of atmospheric pressure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroko', 'n.', 'A form or mode of syllogism of which the first proposition\n   is a universal affirmative, and the other two are particular negative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barology', 'n.', 'The science of weight or gravity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baromacrometer', 'n.', 'An instrument for ascertaining the weight and\n   length of a newborn infant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometer', 'n.', 'An instrument for determining the weight or pressure of\n   the atmosphere, and hence for judging of the probable changes of\n   weather, or for ascertaining the height of any ascent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometric', 'a.', 'Alt. of Barometrical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometrical', 'a.', 'Pertaining to the barometer; made or indicated by a\n   barometer; as, barometric changes; barometrical observations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometrically', 'adv.', 'By means of a barometer, or according to\n   barometric observations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometrograph', 'n.', 'A form of barometer so constructed as to inscribe\n   of itself upon paper a record of the variations of atmospheric\n   pressure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometry', 'n.', 'The art or process of making barometrical measurements.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barometz', 'n.', 'The woolly-skinned rhizoma or rootstock of a fern\n   (Dicksonia barometz); which, when specially prepared and inverted,\n   somewhat resembles a lamb; -- called also Scythian lamb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baron', 'n.', 'A title or degree of nobility; originally, the possessor of\n   a fief, who had feudal tenants under him; in modern times, in France\n   and Germany, a nobleman next in rank below a count; in England, a\n   nobleman of the lowest grade in the House of Lords, being next below a\n   viscount.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baron', 'n.', 'A husband; as, baron and feme, husband and wife.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronage', 'n.', 'The whole body of barons or peers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronage', 'n.', 'The dignity or rank of a baron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronage', 'n.', 'The land which gives title to a baron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroness', 'n.', 'A barons wife; also, a lady who holds the baronial title\n   in her own right; as, the Baroness Burdett-Coutts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronet', 'n.', 'A dignity or degree of honor next below a baron and above\n   a knight, having precedency of all orders of knights except those of\n   the Garter. It is the lowest degree of honor that is hereditary. The\n   baronets are commoners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronetage', 'n.', 'State or rank of a baronet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronetage', 'n.', 'The collective body of baronets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronetcy', 'n.', 'The rank or patent of a baronet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronial', 'a.', 'Pertaining to a baron or a barony.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baronies', 'pl. ', 'of Barony');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barony', 'n.', 'The fee or domain of a baron; the lordship, dignity, or\n   rank of a baron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barony', 'n.', 'In Ireland, a territorial division, corresponding nearly to\n   the English hundred, and supposed to have been originally the district\n   of a native chief. There are 252 of these baronies. In Scotland, an\n   extensive freehold. It may be held by a commoner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroque', 'a.', 'In bad taste; grotesque; odd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroscope', 'n.', 'Any instrument showing the changes in the weight of the\n   atmosphere; also, less appropriately, any instrument that indicates -or\n   foreshadows changes of the weather, as a deep vial of liquid holding in\n   suspension some substance which rises and falls with atmospheric\n   changes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroscopic', 'a.', 'Alt. of Baroscopical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baroscopical', 'a.', 'Pertaining to, or determined by, the baroscope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barouche', 'n.', 'A four-wheeled carriage, with a falling top, a seat on\n   the outside for the driver, and two double seats on the inside arranged\n   so that the sitters on the front seat face those on the back seat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barouchet', 'n.', 'A kind of light barouche.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barpost', 'n.', 'A post sunk in the ground to receive the bars closing a\n   passage into a field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barque', 'n.', 'Same as 3d Bark, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barracan', 'n.', 'A thick, strong stuff, somewhat like camlet; -- still\n   used for outer garments in the Levant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrack', 'n.', 'A building for soldiers, especially when in garrison.\n   Commonly in the pl., originally meaning temporary huts, but now usually\n   applied to a permanent structure or set of buildings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrack', 'n.', 'A movable roof sliding on four posts, to cover hay, straw,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrack', 'v. t.', 'To supply with barracks; to establish in barracks; as,\n   to barrack troops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrack', 'v. i.', 'To live or lodge in barracks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barraclade', 'n.', 'A home-made woolen blanket without nap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barracoon', 'n.', 'A slave warehouse, or an inclosure where slaves are\n   quartered temporarily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barracuda', 'n.', 'Alt. of Barracouata');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barracouata', 'n.', 'A voracious pikelike, marine fish, of the genus\n   Sphyraena, sometimes used as food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barracouata', 'n.', 'A large edible fresh-water fish of Australia and New\n   Zealand (Thyrsites atun).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrage', 'n.', 'An artificial bar or obstruction placed in a river or\n   water course to increase the depth of water; as, the barrages of the\n   Nile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barranca', 'n.', 'A ravine caused by heavy rains or a watercourse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barras', 'n.', 'A resin, called also galipot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrator', 'v. i.', 'One guilty of barratry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barratrous', '/', 'Tainter with, or constituting, barratry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barratry', 'n.', 'The practice of exciting and encouraging lawsuits and\n   quarrels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barratry', 'n.', 'A fraudulent breach of duty or willful act of known\n   illegality on the part of a master of a ship, in his character of\n   master, or of the mariners, to the injury of the owner of the ship or\n   cargo, and without his consent. It includes every breach of trust\n   committed with dishonest purpose, as by running away with the ship,\n   sinking or deserting her, etc., or by embezzling the cargo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barratry', 'n.', 'The crime of a judge who is influenced by bribery in\n   pronouncing judgment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barred owl', '', 'A large American owl (Syrnium nebulosum); -- so called\n   from the transverse bars of a dark brown color on the breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'A round vessel or cask, of greater length than breadth, and\n   bulging in the middle, made of staves bound with hoops, and having flat\n   ends or heads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'The quantity which constitutes a full barrel. This varies\n   for different articles and also in different places for the same\n   article, being regulated by custom or by law. A barrel of wine is 31/\n   gallons; a barrel of flour is 196 pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'A solid drum, or a hollow cylinder or case; as, the barrel\n   of a windlass; the barrel of a watch, within which the spring is\n   coiled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'A metallic tube, as of a gun, from which a projectile is\n   discharged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'A jar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'n.', 'The hollow basal part of a feather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barreled', 'imp. & p. p.', 'of Barrel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrelled', '', 'of Barrel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barreling', 'p. pr. & vb. n.', 'of Barrel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrelling', '', 'of Barrel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrel', 'v. t.', 'To put or to pack in a barrel or barrels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barreled', 'a.', 'Alt. of Barrelled');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrelled', 'a.', 'Having a barrel; -- used in composition; as, a\n   double-barreled gun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'a.', 'Incapable of producing offspring; producing no young;\n   sterile; -- said of women and female animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'a.', 'Not producing vegetation, or useful vegetation; /rile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'a.', 'Unproductive; fruitless; unprofitable; empty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'a.', 'Mentally dull; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'n.', 'A tract of barren land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barren', 'n.', 'Elevated lands or plains on which grow small trees, but not\n   timber; as, pine barrens; oak barrens. They are not necessarily\n   sterile, and are often fertile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrenly', 'adv.', 'Unfruitfully; unproductively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrenness', 'n.', 'The condition of being barren; sterility;\n   unproductiveness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrenwort', 'n.', 'An herbaceous plant of the Barberry family (Epimedium\n   alpinum); having leaves that are bitter and said to be sudorific.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barret', 'n.', 'A kind of cap formerly worn by soldiers; -- called also\n   barret cap. Also, the flat cap worn by Roman Catholic ecclesiastics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricade', 'n.', 'A fortification, made in haste, of trees, earth,\n   palisades, wagons, or anything that will obstruct the progress or\n   attack of an enemy. It is usually an obstruction formed in streets to\n   block an enemys access.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricade', 'n.', 'Any bar, obstruction, or means of defense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricaded', 'imp. & p. p.', 'of Barricade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricading', 'p. pr. & vb. n.', 'of Barricade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricade', 'n.', 'To fortify or close with a barricade or with barricades;\n   to stop up, as a passage; to obstruct; as, the workmen barricaded the\n   streets of Paris.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricader', 'n.', 'One who constructs barricades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barricado', 'n. & v. t.', 'See Barricade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrier', 'n.', 'A carpentry obstruction, stockade, or other obstacle made\n   in a passage in order to stop an enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrier', 'n.', 'A fortress or fortified town, on the frontier of a\n   country, commanding an avenue of approach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrier', 'n.', 'A fence or railing to mark the limits of a place, or to\n   keep back a crowd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrier', 'n.', 'An any obstruction; anything which hinders approach or\n   attack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrier', 'n.', 'Any limit or boundary; a line of separation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrigudo', 'n.', 'A large, dark-colored, South American monkey, of the\n   genus Lagothrix, having a long prehensile tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barringout', 'n.', 'The act of closing the doors of a schoolroom against a\n   schoolmaster; -- a boyish mode of rebellion in schools.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrister', 'n.', 'Counselor at law; a counsel admitted to plead at the\n   bar, and undertake the public trial of causes, as distinguished from an\n   attorney or solicitor. See Attorney.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barroom', 'n.', 'A room containing a bar or counter at which liquors are\n   sold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrow', 'n.', 'A support having handles, and with or without a wheel, on\n   which heavy or bulky things can be transported by hand. See Handbarrow,\n   and Wheelbarrow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrow', 'n.', 'A wicker case, in which salt is put to drain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrow', 'n.', 'A hog, esp. a male hog castrated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrow', 'n.', 'A large mound of earth or stones over the remains of the\n   dead; a tumulus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrow', 'n.', 'A heap of rubbish, attle, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrowist', 'n.', 'A follower of Henry Barrowe, one of the founders of\n   Independency or Congregationalism in England. Barrowe was executed for\n   nonconformity in 1953.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barrulet', 'n.', 'A diminutive of the bar, having one fourth its width.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barruly', 'a.', 'Traversed by barrulets or small bars; -- said of the\n   field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barry', 'a.', 'Divided into bars; -- said of the field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barse', 'n.', 'The common perch. See 1st Bass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartender', 'n.', 'A barkeeper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartered', 'imp. & p. p.', 'of Barter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartering', 'p. pr. & vb. n.', 'of Barter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barter', 'v. i.', 'To traffic or trade, by exchanging one commodity for\n   another, in distinction from a sale and purchase, in which money is\n   paid for the commodities transferred; to truck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barter', 'v. t.', 'To trade or exchange in the way of barter; to exchange\n   (frequently for an unworthy consideration); to traffic; to truck; --\n   sometimes followed by away; as, to barter away goods or honor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barter', 'n.', 'The act or practice of trafficking by exchange of\n   commodities; an exchange of goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barter', 'n.', 'The thing given in exchange.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barterer', 'n.', 'One who barters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartery', 'n.', 'Barter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barth', 'n.', 'A place of shelter for cattle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartholomew tide', '', 'Time of the festival of St. Bartholomew, August\n   24th.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartizan', 'n.', 'A small, overhanging structure for lookout or defense,\n   usually projecting at an angle of a building or near an entrance\n   gateway.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartlett', 'n.', 'A Bartlett pear, a favorite kind of pear, which\n   originated in England about 1770, and was called Williams Bonchretien.\n   It was brought to America, and distributed by Mr. Enoch Bartlett, of\n   Dorchester, Massachusetts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barton', 'n.', 'The demesne lands of a manor; also, the manor itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barton', 'n.', 'A farmyard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bartram', 'n.', 'See Bertram.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barway', 'n.', 'A passage into a field or yard, closed by bars made to take\n   out of the posts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barwise', 'adv.', 'Horizontally.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barwood', 'n.', 'A red wood of a leguminous tree (Baphia nitida); from\n   Angola and the Gaboon in Africa. It is used as a dyewood, and also for\n   ramrods, violin bows and turners work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barycentric', 'a.', 'Of or pertaining to the center of gravity. See\n   Barycentric calculus, under Calculus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baryphony', 'n.', 'Difficulty of speech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baryta', 'n.', 'An oxide of barium (or barytum); a heavy earth with a\n   specific gravity above 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barytes', 'n.', 'Barium sulphate, generally called heavy spar or barite.\n   See Barite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barytic', 'a.', 'Of or pertaining to baryta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baryto-calcite', 'n.', 'A mineral of a white or gray color, occurring\n   massive or crystallized. It is a compound of the carbonates of barium\n   and calcium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barytone', 'a.', 'Alt. of Baritone');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'a.', 'Grave and deep, as a kind of male voice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'a.', 'Not marked with an accent on the last syllable, the grave\n   accent being understood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barytone', 'n.', 'Alt. of Baritone');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'n.', 'A male voice, the compass of which partakes of the common\n   bass and the tenor, but which does not descend as low as the one, nor\n   rise as high as the other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'n.', 'A person having a voice of such range.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'n.', 'The viola di gamba, now entirely disused.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baritone', 'n.', 'A word which has no accent marked on the last syllable,\n   the grave accent being understood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Barytum', 'n.', 'The metal barium. See Barium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basal', 'a.', 'Relating to, or forming, the base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basal-nerved', 'a.', 'Having the nerves radiating from the base; -- said of\n   leaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basalt', 'n.', 'A rock of igneous origin, consisting of augite and\n   triclinic feldspar, with grains of magnetic or titanic iron, and also\n   bottle-green particles of olivine frequently disseminated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basalt', 'n.', 'An imitation, in pottery, of natural basalt; a kind of\n   black porcelain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basaltic', 'a.', 'Pertaining to basalt; formed of, or containing, basalt;\n   as basaltic lava.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basaltiform', 'a.', 'In the form of basalt; columnar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basaltoid', 'a.', 'Formed like basalt; basaltiform.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basan', 'n.', 'Same as Basil, a sheepskin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basanite', 'n.', 'Lydian stone, or black jasper, a variety of siliceous or\n   flinty slate, of a grayish or bluish black color. It is employed to\n   test the purity of gold, the amount of alloy being indicated by the\n   color left on the stone when rubbed by the metal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basbleu', 'n.', 'A bluestocking; a literary woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bascinet', 'n.', 'A light helmet, at first open, but later made with a\n   visor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bascule', 'n.', 'In mechanics an apparatus on the principle of the seesaw,\n   in which one end rises as the other falls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Of little, or less than the usual, height; of low growth; as,\n   base shrubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Low in place or position.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Of humble birth; or low degree; lowly; mean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Illegitimate by birth; bastard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Of little comparative value, as metal inferior to gold and\n   silver, the precious metals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Alloyed with inferior metal; debased; as, base coin; base\n   bullion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Morally low. Hence: Low-minded; unworthy; without dignity of\n   sentiment; ignoble; mean; illiberal; menial; as, a base fellow; base\n   motives; base occupations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Not classical or correct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Deep or grave in sound; as, the base tone of a violin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'Not held by honorable service; as, a base estate, one held by\n   services not honorable; held by villenage. Such a tenure is called\n   base, or low, and the tenant, a base tenant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The bottom of anything, considered as its support, or that on\n   which something rests for support; the foundation; as, the base of a\n   statue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'Fig.: The fundamental or essential part of a thing; the\n   essential principle; a groundwork.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The lower part of a wall, pier, or column, when treated as a\n   separate feature, usually in projection, or especially ornamented.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The lower part of a complete architectural design, as of a\n   monument; also, the lower part of any elaborate piece of furniture or\n   decoration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'That extremity of a leaf, fruit, etc., at which it is\n   attached to its support.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The positive, or non-acid component of a salt; a substance\n   which, combined with an acid, neutralizes the latter and forms a salt;\n   -- applied also to the hydroxides of the positive elements or radicals,\n   and to certain organic bodies resembling them in their property of\n   forming salts with acids.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The chief ingredient in a compound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A substance used as a mordant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The exterior side of the polygon, or that imaginary line\n   which connects the salient angles of two adjacent bastions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The line or surface constituting that part of a figure on\n   which it is supposed to stand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The number from which a mathematical table is constructed;\n   as, the base of a system of logarithms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A low, or deep, sound. (Mus.) (a) The lowest part; the\n   deepest male voice. (b) One who sings, or the instrument which plays,\n   base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A place or tract of country, protected by fortifications, or\n   by natural advantages, from which the operations of an army proceed,\n   forward movements are made, supplies are furnished, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The smallest kind of cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'That part of an organ by which it is attached to another more\n   central organ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The basal plane of a crystal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The ground mass of a rock, especially if not distinctly\n   crystalline.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The lower part of the field. See Escutcheon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The housing of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A kind of skirt ( often of velvet or brocade, but sometimes\n   of mailed armor) which hung from the middle to about the knees, or\n   lower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The lower part of a robe or petticoat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'An apron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'The point or line from which a start is made; a starting\n   place or a goal in various games.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A line in a survey which, being accurately determined in\n   length and position, serves as the origin from which to compute the\n   distances and positions of any points or objects connected with it by a\n   system of triangles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'A rustic play; -- called also prisoners base, prison base,\n   or bars.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'Any one of the four bounds which mark the circuit of the\n   infield.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Based', 'imp. & p. p.', 'of Base');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basing', 'p. pr. & vb. n.', 'of Base');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'n.', 'To put on a base or basis; to lay the foundation of; to\n   found, as an argument or conclusion; -- used with on or upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'To abase; to let, or cast, down; to lower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base', 'a.', 'To reduce the value of; to debase.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseball', 'n.', 'A game of ball, so called from the bases or bounds ( four\n   in number) which designate the circuit which each player must endeavor\n   to make after striking the ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseball', 'n.', 'The ball used in this game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseboard', 'n.', 'A board, or other woodwork, carried round the walls of a\n   room and touching the floor, to form a base and protect the plastering;\n   -- also called washboard (in England); mopboard, and scrubboard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseborn', 'a.', 'Born out of wedlock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseborn', 'a.', 'Born of low parentage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseborn', 'a.', 'Vile; mean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base-burner', 'n.', 'A furnace or stove in which the fuel is contained in a\n   hopper or chamber, and is fed to the fire as the lower stratum is\n   consumed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base-court', 'n.', 'The secondary, inferior, or rear courtyard of a large\n   house; the outer court of a castle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base-court', 'n.', 'An inferior court of law, not of record.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Based', 'a.', 'Having a base, or having as a base; supported; as,\n   broad-based.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Based', 'n.', 'Wearing, or protected by, bases.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basedows disease', '', 'A disease characterized by enlargement of the\n   thyroid gland, prominence of the eyeballs, and inordinate action of the\n   heart; -- called also exophthalmic goiter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baselard', 'n.', 'A short sword or dagger, worn in the fifteenth century.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseless', 'a.', 'Without a base; having no foundation or support.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basely', 'adv.', 'In a base manner; with despicable meanness; dishonorably;\n   shamefully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basely', 'adv.', 'Illegitimately; in bastardy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basement', 'a.', 'The outer wall of the ground story of a building, or of a\n   part of that story, when treated as a distinct substructure. ( See\n   Base, n., 3 (a).) Hence: The rooms of a ground floor, collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baseness', 'n.', 'The quality or condition of being base; degradation;\n   vileness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basenet', 'n.', 'See Bascinet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Base viol', '', 'See Bass viol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bash', 'v. t. & i.', 'To abash; to disconcert or be disconcerted or put out\n   of countenance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashaw', 'n.', 'A Turkish title of honor, now written pasha. See Pasha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashaw', 'n.', 'Fig.: A magnate or grandee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashaw', 'n.', 'A very large siluroid fish (Leptops olivaris) of the\n   Mississippi valley; -- also called goujon, mud cat, and yellow cat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashful', 'a.', 'Abashed; daunted; dismayed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashful', 'a.', 'Very modest, or modest excess; constitutionally disposed\n   to shrink from public notice; indicating extreme or excessive modesty;\n   shy; as, a bashful person, action, expression.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashfully', 'adv.', 'In a bashful manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashfulness', 'n.', 'The quality of being bashful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashi-bazouk', 'n.', 'A soldier belonging to the irregular troops of the\n   Turkish army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashless', 'a.', 'Shameless; unblushing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bashyle', 'n.', 'See Basyle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basi-', '', 'A combining form, especially in anatomical and botanical\n   words, to indicate the base or position at or near a base; forming a\n   base; as, basibranchials, the most ventral of the cartilages or bones\n   of the branchial arches; basicranial, situated at the base of the\n   cranium; basifacial, basitemporal, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basic', 'a.', 'Relating to a base; performing the office of a base in a\n   salt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basic', 'a.', 'Having the base in excess, or the amount of the base\n   atomically greater than that of the acid, or exceeding in proportion\n   that of the related neutral salt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basic', 'a.', 'Apparently alkaline, as certain normal salts which exhibit\n   alkaline reactions with test paper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basic', 'a.', 'Said of crystalline rocks which contain a relatively low\n   percentage of silica, as basalt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basicerite', 'n.', 'The second joint of the antennae of crustaceans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basicity', 'n.', 'The quality or state of being a base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basicity', 'n.', 'The power of an acid to unite with one or more atoms or\n   equivalents of a base, as indicated by the number of replaceable\n   hydrogen atoms contained in the acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basidiospore', 'n.', 'A spore borne by a basidium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basidium', 'n.', 'A special oblong or pyriform cell, with slender branches,\n   which bears the spores in that division of fungi called Basidiomycetes,\n   of which the common mushroom is an example.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basifier', 'n.', 'That which converts into a salifiable base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basifugal', 'n.', 'Tending or proceeding away from the base; as, a\n   basifugal growth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basify', 'v. t.', 'To convert into a salifiable base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basigynium', 'n.', 'The pedicel on which the ovary of certain flowers, as\n   the passion flower, is seated; a carpophore or thecaphore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basihyal', 'a.', 'Noting two small bones, forming the body of the inverted\n   hyoid arch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basihyoid', 'n.', 'The central tongue bone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basil', 'n.', 'The slope or angle to which the cutting edge of a tool, as a\n   plane, is ground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basiled', 'imp. & p. p.', 'of Basil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basiling', 'p. pr. & vb. n.', 'of Basil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basil', 'v. t.', 'To grind or form the edge of to an angle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basil', 'n.', 'The name given to several aromatic herbs of the Mint family,\n   but chiefly to the common or sweet basil (Ocymum basilicum); and the\n   bush basil, or lesser basil (O. minimum); the leaves of which are used\n   in cookery. The name is also given to several kinds of mountain mint\n   (Pycnanthemum).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basil', 'n.', 'The skin of a sheep tanned with bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilar', 'n.', 'Alt. of Basilary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilary', 'n.', 'Relating to, or situated at, the base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilary', 'n.', 'Lower; inferior; applied to impulses or springs of\n   action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilic', 'n.', 'Basilica.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilic', 'a.', 'Alt. of Basilical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilical', 'a.', 'Royal; kingly; also, basilican.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilical', 'a.', 'Pertaining to certain parts, anciently supposed to have\n   a specially important function in the animal economy, as the middle\n   vein of the right arm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilicas', 'pl. ', 'of Basilica');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilic/', 'pl. ', 'of Basilica');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilica', 'n.', 'Originally, the place of a king; but afterward, an\n   apartment provided in the houses of persons of importance, where\n   assemblies were held for dispensing justice; and hence, any large hall\n   used for this purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilica', 'n.', 'A building used by the Romans as a place of public\n   meeting, with court rooms, etc., attached.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilica', 'n.', 'A church building of the earlier centuries of\n   Christianity, the plan of which was taken from the basilica of the\n   Romans. The name is still applied to some churches by way of honorary\n   distinction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilica', 'n.', 'A digest of the laws of Justinian, translated from the\n   original Latin into Greek, by order of Basil I., in the ninth century.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilican', 'a.', 'Of, relating to, or resembling, a basilica; basilical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilicok', 'n.', 'The basilisk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilicon', 'n.', 'An ointment composed of wax, pitch, resin, and olive\n   oil, lard, or other fatty substance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilisk', 'n.', 'A fabulous serpent, or dragon. The ancients alleged that\n   its hissing would drive away all other serpents, and that its breath,\n   and even its look, was fatal. See Cockatrice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilisk', 'n.', 'A lizard of the genus Basiliscus, belonging to the family\n   Iguanidae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basilisk', 'n.', 'A large piece of ordnance, so called from its supposed\n   resemblance to the serpent of that name, or from its size.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Basin', 'n.', 'A hollow vessel or dish, to hold water for washing, and for\n   various other uses.');
