  INSERT INTO words (word, word_type, description)VALUES  (    'Bastard', 'n.', 'Of an unusual make or proportion; as, a bastard musket; a\n   bastard culverin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bastard', 'n.', 'Abbreviated, as the half title in a page preceding the\n   full title page of a book.');
