  INSERT INTO words (word, word_type, description)VALUES  (    'Bateau', 'n.', 'A boat; esp. a flat-bottomed, clumsy boat used on the\n   Canadian lakes and rivers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bated', 'a.', 'Reduced; lowered; restrained; as, to speak with bated\n   breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bateful', 'a.', 'Exciting contention; contentious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bateless', 'a.', 'Not to be abated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batement', 'n.', 'Abatement; diminution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batfish', 'n.', 'A name given to several species of fishes: (a) The Malthe\n   vespertilio of the Atlantic coast. (b) The flying gurnard of the\n   Atlantic (Cephalacanthus spinarella). (c) The California batfish or\n   sting ray (Myliobatis Californicus.)');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batfowler', 'n.', 'One who practices or finds sport in batfowling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batfowling', 'n.', 'A mode of catching birds at night, by holding a torch\n   or other light, and beating the bush or perch where they roost. The\n   birds, flying to the light, are caught with nets or otherwise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batful', 'v. i.', 'Rich; fertile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baths', 'pl. ', 'of Bath');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'The act of exposing the body, or part of the body, for\n   purposes of cleanliness, comfort, health, etc., to water, vapor, hot\n   air, or the like; as, a cold or a hot bath; a medicated bath; a steam\n   bath; a hip bath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'Water or other liquid for bathing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A receptacle or place where persons may immerse or wash their\n   bodies in water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A building containing an apartment or a series of apartments\n   arranged for bathing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A medium, as heated sand, ashes, steam, hot air, through\n   which heat is applied to a body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A solution in which plates or prints are immersed; also, the\n   receptacle holding the solution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A Hebrew measure containing the tenth of a homer, or five\n   gallons and three pints, as a measure for liquids; and two pecks and\n   five quarts, as a dry measure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bath', 'n.', 'A city in the west of England, resorted to for its hot\n   springs, which has given its name to various objects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathed', 'imp. & p. p.', 'of Bathe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathing', 'p. pr. & vb. n.', 'of Bathe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. t.', 'To wash by immersion, as in a bath; to subject to a bath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. t.', 'To lave; to wet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. t.', 'To moisten or suffuse with a liquid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. t.', 'To apply water or some liquid medicament to; as, to bathe\n   the eye with warm water or with sea water; to bathe ones forehead with\n   camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. t.', 'To surround, or envelop, as water surrounds a person\n   immersed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. i.', 'To bathe ones self; to take a bath or baths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. i.', 'To immerse or cover ones self, as in a bath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'v. i.', 'To bask in the sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathe', 'n.', 'The immersion of the body in water; as to take ones usual\n   bathe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bather', 'n.', 'One who bathes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathetic', 'a.', 'Having the character of bathos.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathing', 'n.', 'Act of taking a bath or baths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathmism', 'n.', 'See Vital force.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathometer', 'n.', 'An instrument for measuring depths, esp. one for taking\n   soundings without a sounding line.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathorse', 'n.', 'A horse which carries an officers baggage during a\n   campaign.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathos', 'n.', 'A ludicrous descent from the elevated to the low, in\n   writing or speech; anticlimax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathybius', 'n.', 'A name given by Prof. Huxley to a gelatinous substance\n   found in mud dredged from the Atlantic and preserved in alcohol. He\n   supposed that it was free living protoplasm, covering a large part of\n   the ocean bed. It is now known that the substance is of chemical, not\n   of organic, origin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathymetric', 'a.', 'Alt. of Bathymetrical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathymetrical', 'a.', 'Pertaining to bathymetry; relating to the\n   measurement of depths, especially of depths in the sea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bathymetry', 'n.', 'The art or science of sounding, or measuring depths in\n   the sea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bating', 'prep.', 'With the exception of; excepting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batiste', 'n.', 'Originally, cambric or lawn of fine linen; now applied\n   also to cloth of similar texture made of cotton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batlet', 'n.', 'A short bat for beating clothes in washing them; -- called\n   also batler, batling staff, batting staff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batman', 'n.', 'A weight used in the East, varying according to the\n   locality; in Turkey, the greater batman is about 157 pounds, the lesser\n   only a fourth of this; at Aleppo and Smyrna, the batman is 17 pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batmen', 'pl. ', 'of Batman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batman', 'n.', 'A man who has charge of a bathorse and his load.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batoidei', 'n. pl.', 'The division of fishes which includes the rays and\n   skates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baton', 'n.', 'A staff or truncheon, used for various purposes; as, the\n   baton of a field marshal; the baton of a conductor in musical\n   performances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baton', 'n.', 'An ordinary with its ends cut off, borne sinister as a mark\n   of bastardy, and containing one fourth in breadth of the bend sinister;\n   -- called also bastard bar. See Bend sinister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batoon', 'n.', 'See Baton, and Baston.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bat printing', '', 'A mode of printing on glazed ware.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachia', 'n. pl.', 'The order of amphibians which includes the frogs and\n   toads; the Anura. Sometimes the word is used in a wider sense as\n   equivalent to Amphibia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachian', 'a.', 'Pertaining to the Batrachia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachian', 'n.', 'One of the Batrachia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachoid', 'a.', 'Froglike. Specifically: Of or pertaining to the\n   Batrachidae, a family of marine fishes, including the toadfish. Some\n   have poisonous dorsal spines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachomyomachy', 'n.', 'The battle between the frogs and mice; -- a Greek\n   parody on the Iliad, of uncertain authorship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batrachophagous', 'a.', 'Feeding on frogs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batsmen', 'pl. ', 'of Batsman');
