  INSERT INTO words (word, word_type, description)VALUES  (    'Bats-wing', 'a.', 'Alt. of Batwing');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batwing', 'a.', 'Shaped like a bats wing; as, a bats-wing burner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batta', 'n.', 'Extra pay; esp. an extra allowance to an English officer\n   serving in India.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batta', 'n.', 'Rate of exchange; also, the discount on uncurrent coins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battable', 'a.', 'Capable of cultivation; fertile; productive; fattening.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battailant', 'v. i.', 'Prepared for battle; combatant; warlike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battailant', 'n.', 'A combatant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battailous', 'n.', 'Arrayed for battle; fit or eager for battle; warlike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battalia', 'n.', 'Order of battle; disposition or arrangement of troops\n   (brigades, regiments, battalions, etc.); or of a naval force, for\n   action.');
