  INSERT INTO words (word, word_type, description)VALUES  (    'Battel', 'v. i.', 'To make fertile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battel', 'a.', 'Fertile; fruitful; productive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batteler', 'n.', 'Alt. of Battler');
