  INSERT INTO words (word, word_type, description)VALUES  (    'Batten', 'v. t.', 'The movable bar of a loom, which strikes home or closes\n   the threads of a woof.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battening', 'n.', 'Furring done with small pieces nailed directly upon the\n   wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battered', 'imp. & p. p.', 'of Batter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battering', 'p. pr. & vb. n.', 'of Batter');
