  INSERT INTO words (word, word_type, description)VALUES  (    'Batter', 'v. t.', 'To flatten (metal) by hammering, so as to compress it\n   inwardly and spread it outwardly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batter', 'v. t.', 'A semi-liquid mixture of several ingredients, as, flour,\n   eggs, milk, etc., beaten together and used in cookery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batter', 'v. t.', 'Paste of clay or loam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batter', 'v. t.', 'A bruise on the face of a plate or of type in the form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batter', 'n.', 'A backward slope in the face of a wall or of a bank;\n   receding slope.');
