  INSERT INTO words (word, word_type, description)VALUES  (    'Battle', 'v. t.', 'A division of an army; a battalion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battle', 'v. t.', 'The main body, as distinct from the van and rear;\n   battalia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battled', 'imp. & p. p.', 'of Battle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battling', 'p. pr. & vb. n.', 'of Battle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battle', 'n.', 'To join in battle; to contend in fight; as, to battle over\n   theories.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battle', 'v. t.', 'To assail in battle; to fight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battle-ax', 'n.', 'Alt. of Battle-axe');
