  INSERT INTO words (word, word_type, description)VALUES  (    'Battlement', 'n.', 'pl. The whole parapet, consisting of alternate solids\n   and open spaces. At first purely a military feature, afterwards copied\n   on a smaller scale with decorative features, as for churches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battlemented', 'a.', 'Having battlements.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battologist', 'n.', 'One who battologizes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battologize', 'v. t.', 'To keep repeating needlessly; to iterate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battology', 'n.', 'A needless repetition of words in speaking or writing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batton', 'n.', 'See Batten, and Baton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battue', 'v. t.', 'The act of beating the woods, bushes, etc., for game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battue', 'v. t.', 'The game itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battue', 'v. t.', 'The wanton slaughter of game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batture', 'n.', 'An elevated river bed or sea bed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Battuta', 'n.', 'The measuring of time by beating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Batty', 'a.', 'Belonging to, or resembling, a bat.');
