  INSERT INTO words (word, word_type, description)VALUES  (    'Bawbling', 'a.', 'Insignificant; contemptible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawcock', 'n.', 'A fine fellow; -- a term of endearment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawd', 'n.', 'A person who keeps a house of prostitution, or procures women\n   for a lewd purpose; a procurer or procuress; a lewd person; -- usually\n   applied to a woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawd', 'v. i.', 'To procure women for lewd purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdily', 'adv.', 'Obscenely; lewdly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdiness', 'n.', 'Obscenity; lewdness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdrick', 'n.', 'A belt. See Baldric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdry', 'n.', 'The practice of procuring women for the gratification of\n   lust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdry', 'n.', 'Illicit intercourse; fornication.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdry', 'n.', 'Obscenity; filthy, unchaste language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdy', 'a.', 'Dirty; foul; -- said of clothes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdy', 'a.', 'Obscene; filthy; unchaste.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawdyhouse', 'n.', 'A house of prostitution; a house of ill fame; a\n   brothel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawhorse', 'n.', 'Same as Bathorse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawled', 'imp. & p. p.', 'of Bawl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawling', 'p. pr. & vb. n.', 'of Bawl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawl', 'v. i.', 'To cry out with a loud, full sound; to cry with vehemence,\n   as in calling or exultation; to shout; to vociferate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawl', 'v. i.', 'To cry loudly, as a child from pain or vexation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawl', 'v. t.', 'To proclaim with a loud voice, or by outcry, as a hawker\n   or town-crier does.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawl', 'n.', 'A loud, prolonged cry; an outcry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawler', 'n.', 'One who bawls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawn', 'n.', 'An inclosure with mud or stone walls, for keeping cattle; a\n   fortified inclosure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawn', 'n.', 'A large house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawrel', 'n.', 'A kind of hawk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawsin', 'n.', 'Alt. of Bawson');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawson', 'n.', 'A badger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bawson', 'n.', 'A large, unwieldy person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baxter', 'n.', 'A baker; originally, a female baker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'a.', 'Reddish brown; of the color of a chestnut; -- applied to the\n   color of horses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'An inlet of the sea, usually smaller than a gulf, but of the\n   same general character.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A small body of water set off from the main body; as a\n   compartment containing water for a wheel; the portion of a canal just\n   outside of the gates of a lock, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A recess or indentation shaped like a bay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A principal compartment of the walls, roof, or other part of a\n   building, or of the whole building, as marked off by the buttresses,\n   vaulting, mullions of a window, etc.; one of the main divisions of any\n   structure, as the part of a bridge between two piers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A compartment in a barn, for depositing hay, or grain in the\n   stalks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A kind of mahogany obtained from Campeachy Bay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A berry, particularly of the laurel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'The laurel tree (Laurus nobilis). Hence, in the plural, an\n   honorary garland or crown bestowed as a prize for victory or\n   excellence, anciently made or consisting of branches of the laurel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A tract covered with bay trees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayed', 'imp. & p. p.', 'of Bay');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baying', 'p. pr. & vb. n.', 'of Bay');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. i.', 'To bark, as a dog with a deep voice does, at his game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. t.', 'To bark at; hence, to follow with barking; to bring or\n   drive to bay; as, to bay the bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. i.', 'Deep-toned, prolonged barking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. i.', 'A state of being obliged to face an antagonist or a\n   difficulty, when escape has become impossible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. t.', 'To bathe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'n.', 'A bank or dam to keep back water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay', 'v. t.', 'To dam, as water; -- with up or back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baya', 'n.', 'The East Indian weaver bird (Ploceus Philippinus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayad', 'n.', 'Alt. of Bayatte');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayatte', 'n.', 'A large, edible, siluroid fish of the Nile, of two species\n   (Bagrina bayad and B. docmac).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayadere', 'n.', 'A female dancer in the East Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay-antler', 'n.', 'The second tine of a stags horn. See under Antler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayard', 'a.', 'Properly, a bay horse, but often any horse. Commonly in the\n   phrase blind bayard, an old blind horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayard', 'a.', 'A stupid, clownish fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayardly', 'a.', 'Blind; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayberry', 'n.', 'The fruit of the bay tree or Laurus nobilis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayberry', 'n.', 'A tree of the West Indies related to the myrtle (Pimenta\n   acris).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayberry', 'n.', 'The fruit of Myrica cerifera (wax myrtle); the shrub\n   itself; -- called also candleberry tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baybolt', 'n.', 'A bolt with a barbed shank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayed', 'a.', 'Having a bay or bays.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay ice', '', 'See under Ice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay leaf', '', 'See under 3d Bay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayonet', 'n.', 'A pointed instrument of the dagger kind fitted on the\n   muzzle of a musket or rifle, so as to give the soldier increased means\n   of offense and defense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayonet', 'n.', 'A pin which plays in and out of holes made to receive it,\n   and which thus serves to engage or disengage parts of the machinery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayoneted', 'imp. & p. p.', 'of Bayonet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayoneting', 'p. pr. & vb. n.', 'of Bayonet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayonet', 'v. t.', 'To stab with a bayonet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayonet', 'v. t.', 'To compel or drive by the bayonet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayous', 'pl. ', 'of Bayou');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayou', 'n.', 'An inlet from the Gulf of Mexico, from a lake, or from a\n   large river, sometimes sluggish, sometimes without perceptible movement\n   except from tide and wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay rum', '', 'A fragrant liquid, used for cosmetic and medicinal purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bays', 'n.', 'Alt. of Bayze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bayze', 'n.', 'See Baize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay salt', '', 'Salt which has been obtained from sea water, by evaporation\n   in shallow pits or basins, by the heat of the sun; the large\n   crystalline salt of commerce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay tree', '', 'A species of laurel. (Laurus nobilis).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay window', '', 'A window forming a bay or recess in a room, and\n   projecting outward from the wall, either in a rectangular, polygonal,\n   or semicircular form; -- often corruptly called a bow window.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bay yarn', '', 'Woolen yarn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bazaar', 'n.', 'Alt. of Bazar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bazar', 'n.', 'In the East, an exchange, marketplace, or assemblage of\n   shops where goods are exposed for sale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bazar', 'n.', 'A spacious hall or suite of rooms for the sale of goods, as\n   at a fair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bazar', 'n.', 'A fair for the sale of fancy wares, toys, etc., commonly for\n   a charitable objects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bdellium', 'n.', 'An unidentified substance mentioned in the Bible (Gen.\n   ii. 12, and Num. xi. 7); variously taken to be a gum, a precious stone,\n   or pearls, or perhaps a kind of amber found in Arabia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bdellium', 'n.', 'A gum resin of reddish brown color, brought from India,\n   Persia, and Africa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bdelloidea', 'n. pl.', 'The order of Annulata which includes the leeches.\n   See Hirudinea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bdellometer', 'n.', 'A cupping glass to which are attached a scarificator\n   and an exhausting syringe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bdellomorpha', 'n.', 'An order of Nemertina, including the large leechlike\n   worms (Malacobdella) often parasitic in clams.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Was', 'imp.', 'of Be');
  INSERT INTO words (word, word_type, description)VALUES  (    'Been', 'p. p.', 'of Be');
  INSERT INTO words (word, word_type, description)VALUES  (    'Being', 'p. pr. & vb. n.', 'of Be');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be', 'v. i.', 'To exist actually, or in the world of fact; to have\n   ex/stence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be', 'v. i.', 'To exist in a certain manner or relation, -- whether as a\n   reality or as a product of thought; to exist as the subject of a\n   certain predicate, that is, as having a certain attribute, or as\n   belonging to a certain sort, or as identical with what is specified, --\n   a word or words for the predicate being annexed; as, to be happy; to be\n   here; to be large, or strong; to be an animal; to be a hero; to be a\n   nonentity; three and two are five; annihilation is the cessation of\n   existence; that is the man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be', 'v. i.', 'To take place; to happen; as, the meeting was on Thursday.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be', 'v. i.', 'To signify; to represent or symbolize; to answer to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be-', '', 'A prefix, originally the same word as by;');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be-', '', 'To intensify the meaning; as, bespatter, bestir.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be-', '', 'To render an intransitive verb transitive; as, befall (to fall\n   upon); bespeak (to speak for).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be-', '', 'To make the action of a verb particular or definite; as, beget\n   (to get as offspring); beset (to set around).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaches', 'pl. ', 'of Beach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beach', 'n.', 'Pebbles, collectively; shingle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beach', 'n.', 'The shore of the sea, or of a lake, which is washed by the\n   waves; especially, a sandy or pebbly shore; the strand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beached', 'imp. & p. p.', 'of Beach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaching', 'p. pr. & vb. n.', 'of Beach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beach', 'v. t.', 'To run or drive (as a vessel or a boat) upon a beach; to\n   strand; as, to beach a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beach comber', '', 'A long, curling wave rolling in from the ocean. See\n   Comber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beached', 'p. p. & a.', 'Bordered by a beach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beached', 'p. p. & a.', 'Driven on a beach; stranded; drawn up on a beach;\n   as, the ship is beached.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beachy', 'a.', 'Having a beach or beaches; formed by a beach or beaches;\n   shingly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'n.', 'A signal fire to notify of the approach of an enemy, or to\n   give any notice, commonly of warning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'n.', 'A signal or conspicuous mark erected on an eminence near\n   the shore, or moored in shoal water, as a guide to mariners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'n.', 'A high hill near the shore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'n.', 'That which gives notice of danger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaconed', 'imp. & p. p.', 'of Beacon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaconing', 'p. pr. & vb. n.', 'of Beacon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'v. t.', 'To give light to, as a beacon; to light up; to illumine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beacon', 'v. t.', 'To furnish with a beacon or beacons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaconage', 'n.', 'Money paid for the maintenance of a beacon; also,\n   beacons, collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaconless', 'a.', 'Having no beacon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A prayer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A little perforated ball, to be strung on a thread, and worn\n   for ornament; or used in a rosary for counting prayers, as by Roman\n   Catholics and Mohammedans, whence the phrases to tell beads, to at\n   ones beads, to bid beads, etc., meaning, to be at prayer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'Any small globular body');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A bubble in spirits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A drop of sweat or other liquid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A small knob of metal on a firearm, used for taking aim\n   (whence the expression to draw a bead, for, to take aim).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A small molding of rounded surface, the section being usually\n   an arc of a circle. It may be continuous, or broken into short\n   embossments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'n.', 'A glassy drop of molten flux, as borax or microcosmic salt,\n   used as a solvent and color test for several mineral earths and oxides,\n   as of iron, manganese, etc., before the blowpipe; as, the borax bead;\n   the iron bead, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaded', 'imp. & p. p.', 'of Bead');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beading', 'p. pr. & vb. n.', 'of Bead');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'v. t.', 'To ornament with beads or beading.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead', 'v. i.', 'To form beadlike bubbles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadhouse', 'n.', 'Alt. of Bedehouse');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bedehouse', 'n.', 'An almshouse for poor people who pray daily for their\n   benefactors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beading', 'n.', 'Molding in imitation of beads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beading', 'n.', 'The beads or bead-forming quality of certain liquors; as,\n   the beading of a brand of whisky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadle', 'v.', 'A messenger or crier of a court; a servitor; one who cites\n   or bids persons to appear and answer; -- called also an apparitor or\n   summoner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadle', 'v.', 'An officer in a university, who precedes public processions\n   of officers and students.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadle', 'v.', 'An inferior parish officer in England having a variety of\n   duties, as the preservation of order in church service, the\n   chastisement of petty offenders, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadlery', 'n.', 'Office or jurisdiction of a beadle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadleship', 'n.', 'The state of being, or the personality of, a beadle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead proof', '', 'Among distillers, a certain degree of strength in\n   alcoholic liquor, as formerly ascertained by the floating or sinking of\n   glass globules of different specific gravities thrown into it; now\n   ascertained by more accurate meters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bead proof', '', 'A degree of strength in alcoholic liquor as shown by\n   beads or small bubbles remaining on its surface, or at the side of the\n   glass, when shaken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadroll', 'n.', 'A catalogue of persons, for the rest of whose souls a\n   certain number of prayers are to be said or counted off on the beads of\n   a chaplet; hence, a catalogue in general.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-men', 'pl. ', 'of Bedesman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadsman', 'n.', 'Alt. of Bedesman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bedesman', 'n.', 'A poor man, supported in a beadhouse, and required to\n   pray for the soul of its founder; an almsman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadsnake', 'n.', 'A small poisonous snake of North America (Elaps\n   fulvius); banded with yellow, red, and black.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-women', 'pl. ', 'of Bedeswoman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadswoman', 'n.', 'Alt. of Bedeswoman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bedeswoman', 'n.', 'Fem. of Beadsman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beadwork', 'n.', 'Ornamental work in beads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beady', 'a.', 'Resembling beads; small, round, and glistening.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beady', 'a.', 'Covered or ornamented with, or as with, beads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beady', 'a.', 'Characterized by beads; as, beady liquor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beagle', 'n.', 'A small hound, or hunting dog, twelve to fifteen inches\n   high, used in hunting hares and other small game. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beagle', 'n.', 'Fig.: A spy or detective; a constable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'The bill or nib of a bird, consisting of a horny sheath,\n   covering the jaws. The form varied much according to the food and\n   habits of the bird, and is largely used in the classification of birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'A similar bill in other animals, as the turtles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'The long projecting sucking mouth of some insects, and other\n   invertebrates, as in the Hemiptera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'The upper or projecting part of the shell, near the hinge of\n   a bivalve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'The prolongation of certain univalve shells containing the\n   canal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'Anything projecting or ending in a point, like a beak, as a\n   promontory of land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'A beam, shod or armed at the end with a metal head or point,\n   and projecting from the prow of an ancient galley, in order to pierce\n   the vessel of an enemy; a beakhead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'That part of a ship, before the forecastle, which is fastened\n   to the stem, and supported by the main knee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'A continuous slight projection ending in an arris or narrow\n   fillet; that part of a drip from which the water is thrown off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'Any process somewhat like the beak of a bird, terminating the\n   fruit or other parts of a plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'A toe clip. See Clip, n. (Far.).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beak', 'n.', 'A magistrate or policeman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaked', 'a.', 'Having a beak or a beaklike point; beak-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaked', 'a.', 'Furnished with a process or a mouth like a beak; rostrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaker', 'n.', 'A large drinking cup, with a wide mouth, supported on a\n   foot or standard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaker', 'n.', 'An open-mouthed, thin glass vessel, having a projecting lip\n   for pouring; -- used for holding solutions requiring heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beakhead', 'n.', 'An ornament used in rich Norman doorways, resembling a\n   head with a beak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beakhead', 'n.', 'A small platform at the fore part of the upper deck of a\n   vessel, which contains the water closets of the crew.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beakhead', 'n.', 'Same as Beak, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beakiron', 'n.', 'A bickern; a bench anvil with a long beak, adapted to\n   reach the interior surface of sheet metal ware; the horn of an anvil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bealed', 'imp. & p. p.', 'of Beal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bealing', 'p. pr & vb. n.', 'of Beal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beal', 'v. i.', 'To gather matter; to swell and come to a head, as a\n   pimple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Be-all', 'n.', 'The whole; all that is to be.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'Any large piece of timber or iron long in proportion to its\n   thickness, and prepared for use.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'One of the principal horizontal timbers of a building or\n   ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The width of a vessel; as, one vessel is said to have more\n   beam than another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The bar of a balance, from the ends of which the scales are\n   suspended.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The principal stem or horn of a stag or other deer, which\n   bears the antlers, or branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The pole of a carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'A cylinder of wood, making part of a loom, on which weavers\n   wind the warp before weaving; also, the cylinder on which the cloth is\n   rolled, as it is woven; one being called the fore beam, the other the\n   back beam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The straight part or shank of an anchor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'The main part of a plow, to which the handles and colter are\n   secured, and to the end of which are attached the oxen or horses that\n   draw it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'A heavy iron lever having an oscillating motion on a central\n   axis, one end of which is connected with the piston rod from which it\n   receives motion, and the other with the crank of the wheel shaft; --\n   called also working beam or walking beam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'A ray or collection of parallel rays emitted from the sun or\n   other luminous body; as, a beam of light, or of heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'Fig.: A ray; a gleam; as, a beam of comfort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'n.', 'One of the long feathers in the wing of a hawk; -- called\n   also beam feather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamed', 'imp. & p. p.', 'of Beam');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaming', 'p. pr. & vb. n.', 'of Beam');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'v. t.', 'To send forth; to emit; -- followed ordinarily by forth;\n   as, to beam forth light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam', 'v. i.', 'To emit beams of light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beambird', 'n.', 'A small European flycatcher (Muscicapa gricola); so\n   called because it often nests on a beam in a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamed', 'a.', 'Furnished with beams, as the head of a stag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamful', 'a.', 'Beamy; radiant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamily', 'adv.', 'In a beaming manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaminess', 'n.', 'The state of being beamy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaming', 'a.', 'Emitting beams; radiant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamingly', 'adv.', 'In a beaming manner; radiantly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamless', 'a.', 'Not having a beam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamless', 'a.', 'Not emitting light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamlet', 'n.', 'A small beam of light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beam tree', '', 'A tree (Pyrus aria) related to the apple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamy', 'a.', 'Emitting beams of light; radiant; shining.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamy', 'a.', 'Resembling a beam in size and weight; massy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beamy', 'a.', 'Having horns, or antlers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bean', 'n.', 'A name given to the seed of certain leguminous herbs, chiefly\n   of the genera Faba, Phaseolus, and Dolichos; also, to the herbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bean', 'n.', 'The popular name of other vegetable seeds or fruits, more or\n   less resembling true beans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bean caper', '', 'A deciduous plant of warm climates, generally with fleshy\n   leaves and flowers of a yellow or whitish yellow color, of the genus\n   Zygophyllum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bean trefoil', '', 'A leguminous shrub of southern Europe, with trifoliate\n   leaves (Anagyris foetida).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'imp.', 'of Bear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bare', '', 'of Bear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Born', 'p. p.', 'of Bear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borne', '', 'of Bear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'p. pr. & vb. n.', 'of Bear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To support or sustain; to hold up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To support and remove or carry; to convey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To conduct; to bring; -- said of persons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To possess and use, as power; to exercise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To sustain; to have on (written or inscribed, or as a\n   mark); as, the tablet bears this inscription.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To possess or carry, as a mark of authority or\n   distinction; to wear; as, to bear a sword, badge, or name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To possess mentally; to carry or hold in the mind; to\n   entertain; to harbor');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To endure; to tolerate; to undergo; to suffer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To gain or win.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To sustain, or be answerable for, as blame, expense,\n   responsibility, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To render or give; to bring forward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To carry on, or maintain; to have.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To admit or be capable of; that is, to suffer or sustain\n   without violence, injury, or change.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To manage, wield, or direct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To behave; to conduct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To afford; to be to; to supply with.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To bring forth or produce; to yield; as, to bear apples;\n   to bear children; to bear interest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To produce, as fruit; to be fruitful, in opposition to\n   barrenness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To suffer, as in carrying a burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To endure with patience; to be patient.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To press; -- with on or upon, or against.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To take effect; to have influence or force; as, to bring\n   matters to bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To relate or refer; -- with on or upon; as, how does this\n   bear on the question?');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To have a certain meaning, intent, or effect.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. i.', 'To be situated, as to the point of compass, with respect\n   to something else; as, the land bears N. by E.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'A bier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'Any species of the genus Ursus, and of the closely allied\n   genera. Bears are plantigrade Carnivora, but they live largely on fruit\n   and insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'An animal which has some resemblance to a bear in form or\n   habits, but no real affinity; as, the woolly bear; ant bear; water\n   bear; sea bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'One of two constellations in the northern hemisphere, called\n   respectively the Great Bear and the Lesser Bear, or Ursa Major and Ursa\n   Minor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'Metaphorically: A brutal, coarse, or morose person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'A person who sells stocks or securities for future delivery\n   in expectation of a fall in the market.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'A portable punching machine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'A block covered with coarse matting; -- used to scour the\n   deck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'v. t.', 'To endeavor to depress the price of, or prices in; as, to\n   bear a railroad stock; to bear the market.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bear', 'n.', 'Alt. of Bere');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bere', 'n.', 'Barley; the six-rowed barley or the four-rowed barley,\n   commonly the former (Hord. vulgare).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearable', 'a.', 'Capable of being borne or endured; tolerable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearberry', 'n.', 'A trailing plant of the heath family (Arctostaphylos\n   uva-ursi); having leaves which are tonic and astringent, and glossy red\n   berries of which bears are said to be fond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearbind', 'n.', 'The bindweed (Convolvulus arvensis).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The hair that grows on the chin, lips, and adjacent parts of\n   the human face, chiefly of male adults.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The long hairs about the face in animals, as in the goat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The cluster of small feathers at the base of the beak in\n   some birds');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The appendages to the jaw in some Cetacea, and to the mouth\n   or jaws of some fishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The byssus of certain shellfish, as the muscle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'The gills of some bivalves, as the oyster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'In insects, the hairs of the labial palpi of moths and\n   butterflies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'Long or stiff hairs on a plant; the awn; as, the beard of\n   grain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'A barb or sharp point of an arrow or other instrument,\n   projecting backward to prevent the head from being easily drawn out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'That part of the under side of a horses lower jaw which is\n   above the chin, and bears the curb of a bridle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'That part of a type which is between the shoulder of the\n   shank and the face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'n.', 'An imposition; a trick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearded', 'imp. & p. p.', 'of Beard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearding', 'p. pr. & vb. n.', 'of Beard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'v. t.', 'To take by the beard; to seize, pluck, or pull the beard\n   of (a man); in anger or contempt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'v. t.', 'To oppose to the gills; to set at defiance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beard', 'v. t.', 'To deprive of the gills; -- used only of oysters and\n   similar shellfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearded', 'a.', 'Having a beard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beardie', 'n.', 'The bearded loach (Nemachilus barbatus) of Europe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beardless', 'a.', 'Without a beard. Hence: Not having arrived at puberty or\n   manhood; youthful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beardless', 'a.', 'Destitute of an awn; as, beardless wheat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beardlessness', 'n.', 'The state or quality of being destitute of beard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'One who, or that which, bears, sustains, or carries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'Specifically: One who assists in carrying a body to the\n   grave; a pallbearer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'A palanquin carrier; also, a house servant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'A tree or plant yielding fruit; as, a good bearer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'One who holds a check, note, draft, or other order for the\n   payment of money; as, pay to bearer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearer', 'n.', 'A strip of reglet or other furniture to bear off the\n   impression from a blank page; also, a type or type-high piece of metal\n   interspersed in blank parts to support the plate when it is shaved.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearherd', 'n.', 'A man who tends a bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearhound', 'n.', 'A hound for baiting or hunting bears.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The manner in which one bears or conducts ones self;\n   mien; behavior; carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'Patient endurance; suffering without complaint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The situation of one object, with respect to another, such\n   situation being supposed to have a connection with the object, or\n   influence upon it, or to be influenced by it; hence, relation;\n   connection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'Purport; meaning; intended significance; aspect.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The act, power, or time of producing or giving birth; as,\n   a tree in full bearing; a tree past bearing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'That part of any member of a building which rests upon its\n   supports; as, a lintel or beam may have four inches of bearing upon the\n   wall.');
