  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'Improperly, the unsupported span; as, the beam has twenty\n   feet of bearing between its supports.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The part of an axle or shaft in contact with its support,\n   collar, or boxing; the journal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The part of the support on which a journal rests and\n   rotates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'Any single emblem or charge in an escutcheon or coat of\n   arms -- commonly in the pl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The situation of a distant object, with regard to a ships\n   position, as on the bow, on the lee quarter, etc.; the direction or\n   point of the compass in which an object is seen; as, the bearing of the\n   cape was W. N. W.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The widest part of a vessel below the plank-sheer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing', 'n.', 'The line of flotation of a vessel when properly trimmed\n   with cargo or ballast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing cloth', '', 'A cloth with which a child is covered when carried to\n   be baptized.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearing rein', '', 'A short rein looped over the check hook or the hames to\n   keep the horses head up; -- called in the United States a checkrein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearish', 'a.', 'Partaking of the qualities of a bear; resembling a bear in\n   temper or manners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearishness', 'n.', 'Behavior like that of a bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearn', 'n.', 'See Bairn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bears-breech', 'n.', 'See Acanthus, n., 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bears-breech', 'n.', 'The English cow parsnip (Heracleum sphondylium)');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bears-ear', 'n.', 'A kind of primrose (Primula auricula); so called from\n   the shape of the leaf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bears-foot', 'n.', 'A species of hellebore (Helleborus foetidus); with\n   digitate leaves. It has an offensive smell and acrid taste, and is a\n   powerful emetic, cathartic, and anthelmintic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearskin', 'n.', 'The skin of a bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearskin', 'n.', 'A coarse, shaggy, woolen cloth for overcoats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearskin', 'n.', 'A cap made of bearskin, esp. one worn by soldiers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bears-paw', 'n.', 'A large bivalve shell of the East Indies (Hippopus\n   maculatus); often used as an ornament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bearward', 'n.', 'A keeper of bears. See Bearherd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'Any living creature; an animal; -- including man, insects,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'Any four-footed animal, that may be used for labor, food, or\n   sport; as, a beast of burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'As opposed to man: Any irrational animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'Fig.: A coarse, brutal, filthy, or degraded fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'A game at cards similar to loo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beast', 'n.', 'A penalty at beast, omber, etc. Hence: To be beasted, to be\n   beaten at beast, omber, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beasthood', 'n.', 'State or nature of a beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastings', 'n. pl.', 'See Biestings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastlihead', 'n.', 'Beastliness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastlike', 'a.', 'Like a beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastliness', 'n.', 'The state or quality of being beastly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastly', 'a.', 'Pertaining to, or having the form, nature, or habits of, a\n   beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastly', 'a.', 'Characterizing the nature of a beast; contrary to the\n   nature and dignity of man; brutal; filthy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beastly', 'a.', 'Abominable; as, beastly weather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'imp.', 'of Beat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'p. p.', 'of Beat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beaten', '', 'of Beat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beating', 'p. pr. & vb. n.', 'of Beat');
