  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To punish by blows; to thrash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To scour or range over in hunting, accompanied with the\n   noise made by striking bushes, etc., for the purpose of rousing game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To dash against, or strike, as with water or wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To tread, as a path.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To overcome in a battle, contest, strife, race, game,\n   etc.; to vanquish or conquer; to surpass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To cheat; to chouse; to swindle; to defraud; -- often with\n   out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To exercise severely; to perplex; to trouble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. t.', 'To give the signal for, by beat of drum; to sound by beat\n   of drum; as, to beat an alarm, a charge, a parley, a retreat; to beat\n   the general, the reveille, the tattoo. See Alarm, Charge, Parley, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To strike repeatedly; to inflict repeated blows; to knock\n   vigorously or loudly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To move with pulsation or throbbing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To come or act with violence; to dash or fall with force;\n   to strike anything, as, rain, wind, and waves do.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To be in agitation or doubt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To make progress against the wind, by sailing in a zigzag\n   line or traverse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To make a sound when struck; as, the drums beat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To make a succession of strokes on a drum; as, the\n   drummers beat to call soldiers to their quarters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'To sound with more or less rapid alternations of greater\n   and less intensity, so as to produce a pulsating effect; -- said of\n   instruments, tones, or vibrations, not perfectly in unison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'n.', 'A stroke; a blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'n.', 'A recurring stroke; a throb; a pulsation; as, a beat of the\n   heart; the beat of the pulse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'n.', 'The rise or fall of the hand or foot, marking the divisions\n   of time; a division of the measure so marked. In the rhythm of music\n   the beat is the unit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'n.', 'A transient grace note, struck immediately before the one it\n   is intended to ornament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'n.', 'A sudden swelling or reenforcement of a sound, recurring at\n   regular intervals, and produced by the interference of sound waves of\n   slightly different periods of vibrations; applied also, by analogy, to\n   other kinds of wave motions; the pulsation or throbbing produced by the\n   vibrating together of two tones not quite in unison. See Beat, v. i.,\n   8.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'A round or course which is frequently gone over; as, a\n   watchmans beat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'A place of habitual or frequent resort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beat', 'v. i.', 'A cheat or swindler of the lowest grade; -- often\n   emphasized by dead; as, a dead beat.');
