  INSERT INTO words (word, word_type, description)VALUES  (    'Beatificate', 'v. t.', 'To beatify.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatification', 'n.', 'The act of beatifying, or the state of being\n   beatified; esp., in the R. C. Church, the act or process of\n   ascertaining and declaring that a deceased person is one of \"the\n   blessed,\" or has attained the second degree of sanctity, -- usually a\n   stage in the process of canonization.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatified', 'imp. & p. p.', 'of Beatify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatifying', 'p. pr. & vb. n.', 'of Beatify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatify', 'v. t.', 'To pronounce or regard as happy, or supremely blessed,\n   or as conferring happiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatify', 'v. t.', 'To make happy; to bless with the completion of\n   celestial enjoyment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beatify', 'v. t.', 'To ascertain and declare, by a public process and\n   decree, that a deceased person is one of \"the blessed\" and is to be\n   reverenced as such, though not canonized.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beating', 'n.', 'The act of striking or giving blows; punishment or\n   chastisement by blows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beating', 'n.', 'Pulsation; throbbing; as, the beating of the heart.');
