  INSERT INTO words (word, word_type, description)VALUES  (    'Bee', 'n.', 'An insect of the order Hymenoptera, and family Apidae (the\n   honeybees); or family Andrenidae (the solitary bees.) See Honeybee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bee', 'n.', 'A neighborly gathering of people who engage in united labor\n   for the benefit of an individual or family; as, a quilting bee; a\n   husking bee; a raising bee.');
