  INSERT INTO words (word, word_type, description)VALUES  (    'Benzal', 'n.', 'A compound radical, C6H5.CH, of the aromatic series,\n   related to benzyl and benzoyl; -- used adjectively or in combination.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzamide', 'n.', 'A transparent crystalline substance, C6H5.CO.NH2,\n   obtained by the action of ammonia upon chloride of benzoyl, as also by\n   several other reactions with benzoyl compounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzene', 'n.', 'A volatile, very inflammable liquid, C6H6, contained in\n   the naphtha produced by the destructive distillation of coal, from\n   which it is separated by fractional distillation. The name is sometimes\n   applied also to the impure commercial product or benzole, and also, but\n   rarely, to a similar mixed product of petroleum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzile', 'n.', 'A yellowish crystalline substance, C6H5.CO.CO.C6H5, formed\n   from benzoin by the action of oxidizing agents, and consisting of a\n   doubled benzoyl radical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzine', 'n.', 'A liquid consisting mainly of the lighter and more\n   volatile hydrocarbons of petroleum or kerosene oil, used as a solvent\n   and for cleansing soiled fabrics; -- called also petroleum spirit,\n   petroleum benzine. Varieties or similar products are gasoline, naphtha,\n   rhigolene, ligroin, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzine', 'n.', 'Same as Benzene.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoate', 'n.', 'A salt formed by the union of benzoic acid with any\n   salifiable base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoic', 'a.', 'Pertaining to, or obtained from, benzoin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoin', 'n.', 'A resinous substance, dry and brittle, obtained from the\n   Styrax benzoin, a tree of Sumatra, Java, etc., having a fragrant odor,\n   and slightly aromatic taste. It is used in the preparation of benzoic\n   acid, in medicine, and as a perfume.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoin', 'n.', 'A white crystalline substance, C14H12O2, obtained from\n   benzoic aldehyde and some other sources.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoin', 'n.', 'The spicebush (Lindera benzoin).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoinated', 'a.', 'Containing or impregnated with benzoin; as,\n   benzoinated lard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzole', 'n.', 'Alt. of Benzol');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzol', 'n.', 'An impure benzene, used in the arts as a solvent, and for\n   various other purposes. See Benzene.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoline', 'n.', 'Same as Benzole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoline', 'n.', 'Same as Amarine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzoyl', 'n.', 'A compound radical, C6H5.CO; the base of benzoic acid, of\n   the oil of bitter almonds, and of an extensive series of compounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Benzyl', 'n.', 'A compound radical, C6H5.CH2, related to toluene and\n   benzoic acid; -- commonly used adjectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepaint', 'v. t.', 'To paint; to cover or color with, or as with, paint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepelt', 'v. t.', 'To pelt roundly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepinched', 'imp. & p. p.', 'of Bepinch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepinch', 'v. t.', 'To pinch, or mark with pinches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beplastered', 'imp. & p. p.', 'of Beplaster');
