  INSERT INTO words (word, word_type, description)VALUES  (    'Beplaster', 'v. t.', 'To plaster over; to cover or smear thickly; to\n   bedaub.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beplumed', 'a.', 'Decked with feathers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepommeled', 'imp. & p. p.', 'of Bepommel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepommeling', 'p. pr. & vb. n.', 'of Bepommel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepommel', 'v. t.', 'To pommel; to beat, as with a stick; figuratively, to\n   assail or criticise in conversation, or in writing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepowder', 'v. t.', 'To sprinkle or cover with powder; to powder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepraise', 'v. t.', 'To praise greatly or extravagantly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beprose', 'v. t.', 'To reduce to prose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepuffed', 'a.', 'Puffed; praised.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bepurple', 'v. t.', 'To tinge or dye with a purple color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeathed', 'imp. & p. p.', 'of Bequeath');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeathing', 'p. pr. & vb. n.', 'of Bequeath');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeath', 'v. t.', 'To give or leave by will; to give by testament; --\n   said especially of personal property.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeath', 'v. t.', 'To hand down; to transmit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeath', 'v. t.', 'To give; to offer; to commit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeathable', 'a.', 'Capable of being bequeathed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeathal', 'n.', 'The act of bequeathing; bequeathment; bequest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequeathment', 'n.', 'The act of bequeathing, or the state of being\n   bequeathed; a bequest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequest', 'n.', 'The act of bequeathing or leaving by will; as, a bequest\n   of property by A. to B.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequest', 'n.', 'That which is left by will, esp. personal property; a\n   legacy; also, a gift.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequest', 'v. t.', 'To bequeath, or leave as a legacy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequethen', '', 'old p. p. of Bequeath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bequote', 'v. t.', 'To quote constantly or with great frequency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berained', 'imp. & p. p.', 'of Berain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beraining', 'p. pr. & vb. n.', 'of Berain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berain', 'v. t.', 'To rain upon; to wet with rain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berated', 'imp. & p. p.', 'of Berate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berating', 'p. pr. & vb. n.', 'of Berate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berate', 'v. t.', 'To rate or chide vehemently; to scold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berattle', 'v. t.', 'To make rattle; to scold vociferously; to cry down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beray', 'v. t.', 'To make foul; to soil; to defile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berbe', 'n.', 'An African genet (Genetta pardina). See Genet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berber', 'n.', 'A member of a race somewhat resembling the Arabs, but often\n   classed as Hamitic, who were formerly the inhabitants of the whole of\n   North Africa from the Mediterranean southward into the Sahara, and who\n   still occupy a large part of that region; -- called also Kabyles. Also,\n   the language spoken by this people.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berberine', 'n.', 'An alkaloid obtained, as a bitter, yellow substance,\n   from the root of the barberry, gold thread, and other plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berberry', 'n.', 'See Barberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berdash', 'n.', 'A kind of neckcloth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bere', 'v. t.', 'To pierce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bere', 'n.', 'See Bear, barley.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereaved', 'imp. & p. p.', 'of Bereave');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereft', '', 'of Bereave');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereaving.', 'p. pr. & vb. n.', 'of Bereave');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereave', 'v. t.', 'To make destitute; to deprive; to strip; -- with of\n   before the person or thing taken away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereave', 'v. t.', 'To take away from.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereave', 'v. t.', 'To take away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereavement', 'n.', 'The state of being bereaved; deprivation; esp., the\n   loss of a relative by death.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereaver', 'n.', 'One who bereaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bereft', '', 'imp. & p. p. of Bereave.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beretta', 'n.', 'Same as Berretta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berg', 'n.', 'A large mass or hill, as of ice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'A tree of the Orange family (Citrus bergamia); having a\n   roundish or pear-shaped fruit, from the rind of which an essential oil\n   of delicious odor is extracted, much prized as a perfume. Also, the\n   fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'A variety of mint (Mentha aquatica, var. glabrata).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'The essence or perfume made from the fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'A variety of pear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'A variety of snuff perfumed with bergamot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergamot', 'n.', 'A coarse tapestry, manufactured from flock of cotton or\n   hemp, mixed with oxs or goats hair; -- said to have been invented at\n   Bergamo, Italy. Encyc. Brit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergander', 'n.', 'A European duck (Anas tadorna). See Sheldrake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergeret', 'n.', 'A pastoral song.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergh', 'n.', 'A hill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergmaster', 'n.', 'See Barmaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergmeal', 'n.', 'An earthy substance, resembling fine flour. It is\n   composed of the shells of infusoria, and in Lapland and Sweden is\n   sometimes eaten, mixed with flour or ground birch bark, in times of\n   scarcity. This name is also given to a white powdery variety of\n   calcite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergmote', 'n.', 'See Barmote.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergomask', 'n.', 'A rustic dance, so called in ridicule of the people of\n   Bergamo, in Italy, once noted for their clownishness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bergylt', 'n.', 'The Norway haddock. See Rosefish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berhymed', 'imp. & p. p.', 'of Berhyme');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berhyming', 'p. pr. & vb. n.', 'of Berhyme');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berhyme', 'v. t.', 'To mention in rhyme or verse; to rhyme about.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beriberi', 'n.', 'An acute disease occurring in India, characterized by\n   multiple inflammatory changes in the nerves, producing great muscular\n   debility, a painful rigidity of the limbs, and cachexy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berime', 'v. t.', 'To berhyme.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berkeleian', 'a.', 'Of or relating to Bishop Berkeley or his system of\n   idealism; as, Berkeleian philosophy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berlin', 'n.', 'A four-wheeled carriage, having a sheltered seat behind the\n   body and separate from it, invented in the 17th century, at Berlin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berlin', 'n.', 'Fine worsted for fancy-work; zephyr worsted; -- called also\n   Berlin wool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berm', 'n.', 'Alt. of Berme');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berme', 'n.', 'A narrow shelf or path between the bottom of a parapet and\n   the ditch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berme', 'n.', 'A ledge at the bottom of a bank or cutting, to catch earth\n   that may roll down the slope, or to strengthen the bank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bermuda grass', '', 'A kind of grass (Cynodon Dactylon) esteemed for\n   pasture in the Southern United States. It is a native of Southern\n   Europe, but is now wide-spread in warm countries; -- called also scutch\n   grass, and in Bermuda, devil grass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernacle', 'n.', 'See Barnacle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berna fly', '', 'A Brazilian dipterous insect of the genus Trypeta, which\n   lays its eggs in the nostrils or in wounds of man and beast, where the\n   larvae do great injury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernardine', 'a.', 'Of or pertaining to St. Bernard of Clairvaux, or to the\n   Cistercian monks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernardine', 'n.', 'A Cistercian monk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernese', 'a.', 'Pertaining to the city or canton of Bern, in Switzerland,\n   or to its inhabitants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernese', 'n. sing. & pl.', 'A native or natives of Bern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernicle', 'n.', 'A bernicle goose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bernouse', 'n.', 'Same as Burnoose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berob', 'v. t.', 'To rob; to plunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beroe', 'n.', 'A small, oval, transparent jellyfish, belonging to the\n   Ctenophora.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berretta', 'n.', 'A square cap worn by ecclesiastics of the Roman Catholic\n   Church. A cardinals berretta is scarlet; that worn by other clerics is\n   black, except that a bishops is lined with green.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berried', 'a.', 'Furnished with berries; consisting of a berry; baccate;\n   as, a berried shrub.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berries', 'pl. ', 'of Berry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'n.', 'Any small fleshy fruit, as the strawberry, mulberry,\n   huckleberry, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'n.', 'A small fruit that is pulpy or succulent throughout, having\n   seeds loosely imbedded in the pulp, as the currant, grape, blueberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'n.', 'The coffee bean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'n.', 'One of the ova or eggs of a fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berried', 'imp. & p. p.', 'of Berry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berrying', 'p. pr. & vb. n.', 'of Berry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'v. i.', 'To bear or produce berries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berry', 'n.', 'A mound; a hillock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berrying', 'n.', 'A seeking for or gathering of berries, esp. of such as\n   grow wild.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berserk', 'n.', 'Alt. of Berserker');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berserker', 'n.', 'One of a class of legendary heroes, who fought frenzied\n   by intoxicating liquors, and naked, regardless of wounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berserker', 'n.', 'One who fights as if frenzied, like a Berserker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berstle', 'n.', 'See Bristle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'n.', 'Convenient sea room.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'n.', 'A room in which a number of the officers or ships company\n   mess and reside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'n.', 'The place where a ship lies when she is at anchor, or at a\n   wharf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'n.', 'An allotted place; an appointment; situation or employment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'n.', 'A place in a ship to sleep in; a long box or shelf on the\n   side of a cabin or stateroom, or of a railway car, for sleeping in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berthed', 'imp. & p. p.', 'of Berth');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berthing', 'p. pr. & vb. n.', 'of Berth');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'v. t.', 'To give an anchorage to, or a place to lie at; to place\n   in a berth; as, she was berthed stem to stern with the Adelaide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berth', 'v. t.', 'To allot or furnish berths to, on shipboard; as, to berth\n   a ships company.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bertha', 'n.', 'A kind of collar or cape worn by ladies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berthage', 'n.', 'A place for mooring vessels in a dock or harbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berthierite', 'n.', 'A double sulphide of antimony and iron, of a dark\n   steel-gray color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berthing', 'n.', 'The planking outside of a vessel, above the sheer strake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bertram', 'n.', 'Pellitory of Spain (Anacyclus pyrethrum).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berycoid', 'a.', 'Of or pertaining to the Berycidae, a family of marine\n   fishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beryl', 'n.', 'A mineral of great hardness, and, when transparent, of much\n   beauty. It occurs in hexagonal prisms, commonly of a green or bluish\n   green color, but also yellow, pink, and white. It is a silicate of\n   aluminium and glucinum (beryllium). The aquamarine is a transparent,\n   sea-green variety used as a gem. The emerald is another variety highly\n   prized in jewelry, and distinguished by its deep color, which is\n   probably due to the presence of a little oxide of chromium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berylline', 'a.', 'Like a beryl; of a light or bluish green color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beryllium', 'n.', 'A metallic element found in the beryl. See Glucinum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Berylloid', 'n.', 'A solid consisting of a double twelve-sided pyramid; --\n   so called because the planes of this form occur on crystals of beryl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besaiel', 'n.', 'Alt. of Besayle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besaile', 'n.', 'Alt. of Besayle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besayle', 'n.', 'A great-grandfather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besayle', 'n.', 'A kind of writ which formerly lay where a\n   great-grandfather died seized of lands in fee simple, and on the day of\n   his death a stranger abated or entered and kept the heir out. This is\n   now abolished.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besaint', 'v. t.', 'To make a saint of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besant', 'n.', 'See Bezant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bes-antler', 'n.', 'Same as Bez-antler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescatter', 'v. t.', 'To scatter over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescatter', 'v. t.', 'To cover sparsely by scattering (something); to\n   strew.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescorn', 'v. t.', 'To treat with scorn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescratch', 'v. t.', 'To tear with the nails; to cover with scratches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescrawl', 'v. t.', 'To cover with scrawls; to scribble over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescreen', 'v. t.', 'To cover with a screen, or as with a screen; to\n   shelter; to conceal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescribble', 'v. t.', 'To scribble over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescumber', 'v. t.', 'Alt. of Bescummer');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bescummer', 'v. t.', 'To discharge ordure or dung upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besee', 'v. t. & i.', 'To see; to look; to mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Besought', 'imp. & p. p.', 'of Beseech');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseeching', 'p. pr. & vb. n.', 'of Beseech');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseech', 'v. t.', 'To ask or entreat with urgency; to supplicate; to\n   implore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseech', 'n.', 'Solicitation; supplication.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseecher', 'n.', 'One who beseeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseeching', 'a.', 'Entreating urgently; imploring; as, a beseeching look.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Beseechment', 'n.', 'The act of beseeching or entreating earnestly.');
