  INSERT INTO words (word, word_type, description)VALUES  (    'Bible', 'n.', 'The Book by way of eminence, -- that is, the book which is\n   made up of the writings accepted by Christians as of divine origin and\n   authority, whether such writings be in the original language, or\n   translated; the Scriptures of the Old and New Testaments; -- sometimes\n   in a restricted sense, the Old Testament; as, King Jamess Bible; Douay\n   Bible; Luthers Bible. Also, the book which is made up of writings\n   similarly accepted by the Jews; as, a rabbinical Bible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bible', 'n.', 'A book containing the sacred writings belonging to any\n   religion; as, the Koran is often called the Mohammedan Bible.');
