  INSERT INTO words (word, word_type, description)VALUES  (    'Bigha', 'n.', 'A measure of land in India, varying from a third of an acre\n   to an acre.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bighorn', 'n.', 'The Rocky Mountain sheep (Ovis / Caprovis montana).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bight', 'v.', 'A corner, bend, or angle; a hollow; as, the bight of a\n   horses knee; the bight of an elbow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bight', 'v.', 'A bend in a coast forming an open bay; as, the Bight of\n   Benin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bight', 'v.', 'The double part of a rope when folded, in distinction from\n   the ends; that is, a round, bend, or coil not including the ends; a\n   loop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biglandular', 'a.', 'Having two glands, as a plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigly', 'a.', 'In a tumid, swelling, blustering manner; haughtily;\n   violently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigness', 'n.', 'The state or quality of being big; largeness; size; bulk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bignonia', 'n.', 'A large genus of American, mostly tropical, climbing\n   shrubs, having compound leaves and showy somewhat tubular flowers. B.\n   capreolata is the cross vine of the Southern United States. The trumpet\n   creeper was formerly considered to be of this genus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bignoniaceous', 'a.', 'Of pertaining to, or resembling, the family of\n   plants of which the trumpet flower is an example.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigot', 'n.', 'A hypocrite; esp., a superstitious hypocrite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigot', 'n.', 'A person who regards his own faith and views in matters of\n   religion as unquestionably right, and any belief or opinion opposed to\n   or differing from them as unreasonable or wicked. In an extended sense,\n   a person who is intolerant of opinions which conflict with his own, as\n   in politics or morals; one obstinately and blindly devoted to his own\n   church, party, belief, or opinion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigot', 'a.', 'Bigoted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigoted', 'a.', 'Obstinately and blindly attached to some creed, opinion\n   practice, or ritual; unreasonably devoted to a system or party, and\n   illiberal toward the opinions of others.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigotedly', 'adv.', 'In the manner of a bigot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigotry', 'n.', 'The state of mind of a bigot; obstinate and unreasoning\n   attachment of ones own belief and opinions, with narrow-minded\n   intolerance of beliefs opposed to them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigotry', 'n.', 'The practice or tenets of a bigot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bigwig', 'a.', 'A person of consequence; as, the bigwigs of society.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Big-wigged', 'a.', 'characterized by pomposity of manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bihydroguret', 'n.', 'A compound of two atoms of hydrogen with some other\n   substance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bijoux', 'pl. ', 'of Bijou');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bijou', 'n.', 'A trinket; a jewel; -- a word applied to anything small and\n   of elegant workmanship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bijoutry', 'n.', 'Small articles of virtu, as jewelry, trinkets, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bijugate', 'a.', 'Having two pairs, as of leaflets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bijugous', 'a.', 'Bijugate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bike', 'n.', 'A nest of wild bees, wasps, or ants; a swarm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bikh', 'n.', 'The East Indian name of a virulent poison extracted from\n   Aconitum ferox or other species of aconite: also, the plant itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilabiate', 'a.', 'Having two lips, as the corols of certain flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilaciniate', 'a.', 'Doubly fringed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilalo', 'n.', 'A two-masted passenger boat or small vessel, used in the\n   bay of Manila.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilamellate', 'a.', 'Alt. of Bilamellated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilamellated', 'a.', 'Formed of two plates, as the stigma of the Mimulus;\n   also, having two elevated ridges, as in the lip of certain flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilaminar', 'a.', 'Alt. of Bilaminate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilaminate', 'a.', 'Formed of, or having, two laminae, or thin plates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biland', 'n.', 'A byland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilander', 'n.', 'A small two-masted merchant vessel, fitted only for\n   coasting, or for use in canals, as in Holland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilateral', 'a.', 'Having two sides; arranged upon two sides; affecting two\n   sides or two parties.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilateral', 'a.', 'Of or pertaining to the two sides of a central area or\n   organ, or of a central axis; as, bilateral symmetry in animals, where\n   there is a similarity of parts on the right and left sides of the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilaterality', 'n.', 'State of being bilateral.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilberries', 'pl. ', 'of Bilberry');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilberry', 'n.', 'The European whortleberry (Vaccinium myrtillus); also,\n   its edible bluish black fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilberry', 'n.', 'Any similar plant or its fruit; esp., in America, the\n   species Vaccinium myrtilloides, V. caespitosum and V. uliginosum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilboes', 'pl. ', 'of Bilbo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilbo', 'n.', 'A rapier; a sword; so named from Bilbao, in Spain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilbo', 'n.', 'A long bar or bolt of iron with sliding shackles, and a lock\n   at the end, to confine the feet of prisoners or offenders, esp. on\n   board of ships.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilboquet', 'n.', 'The toy called cup and ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilcock', 'n.', 'The European water rail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bildstein', 'n.', 'Same as Agalmatolite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bile', 'n.', 'A yellow, or greenish, viscid fluid, usually alkaline in\n   reaction, secreted by the liver. It passes into the intestines, where\n   it aids in the digestive process. Its characteristic constituents are\n   the bile salts, and coloring matters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bile', 'n.', 'Bitterness of feeling; choler; anger; ill humor; as, to stir\n   ones bile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bile', 'n.', 'A boil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilection', 'n.', 'That portion of a group of moldings which projects\n   beyond the general surface of a panel; a bolection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilestone', 'n.', 'A gallstone, or biliary calculus. See Biliary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'n.', 'The protuberant part of a cask, which is usually in the\n   middle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'n.', 'That part of a ships hull or bottom which is broadest and\n   most nearly flat, and on which she would rest if aground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'n.', 'Bilge water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilged', 'imp. & p. p.', 'of Bilge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilging', 'p. pr. & vb. n.', 'of Bilge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'v. i.', 'To suffer a fracture in the bilge; to spring a leak by a\n   fracture in the bilge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'v. i.', 'To bulge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'v. t.', 'To fracture the bilge of, or stave in the bottom of (a\n   ship or other vessel).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilge', 'v. t.', 'To cause to bulge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilgy', 'a.', 'Having the smell of bilge water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliary', 'a.', 'Relating or belonging to bile; conveying bile; as, biliary\n   acids; biliary ducts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliation', 'n.', 'The production and excretion of bile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliferous', 'a.', 'Generating bile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilifuscin', 'n.', 'A brownish green pigment found in human gallstones and\n   in old bile. It is a derivative of bilirubin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilimbi', 'n.', 'Alt. of Bilimbing');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilimbing', 'n.', 'The berries of two East Indian species of Averrhoa, of\n   the Oxalideae or Sorrel family. They are very acid, and highly esteemed\n   when preserved or pickled. The juice is used as a remedy for skin\n   diseases.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliment', 'n.', 'A womans ornament; habiliment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilin', 'n.', 'A name applied to the amorphous or crystalline mass obtained\n   from bile by the action of alcohol and ether. It is composed of a\n   mixture of the sodium salts of the bile acids.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilinear', 'a.', 'Of, pertaining to, or included by, two lines; as,\n   bilinear coordinates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilingual', 'a.', 'Containing, or consisting of, two languages; expressed\n   in two languages; as, a bilingual inscription; a bilingual dictionary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilingualism', 'n.', 'Quality of being bilingual.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilinguar', 'a.', 'See Bilingual.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilinguist', 'n.', 'One versed in two languages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilinguous', 'a.', 'Having two tongues, or speaking two languages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilious', 'a.', 'Of or pertaining to the bile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilious', 'a.', 'Disordered in respect to the bile; troubled with an excess\n   of bile; as, a bilious patient; dependent on, or characterized by, an\n   excess of bile; as, bilious symptoms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilious', 'a.', 'Choleric; passionate; ill tempered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliousness', 'n.', 'The state of being bilious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliprasin', 'n.', 'A dark green pigment found in small quantity in human\n   gallstones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilirubin', 'n.', 'A reddish yellow pigment present in human bile, and in\n   that from carnivorous and herbivorous animals; the normal biliary\n   pigment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliteral', 'a.', 'Consisting of two letters; as, a biliteral root of a\n   Sanskrit verb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliteral', 'n.', 'A word, syllable, or root, consisting of two letters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliteralism', 'n.', 'The property or state of being biliteral.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biliverdin', 'n.', 'A green pigment present in the bile, formed from\n   bilirubin by oxidation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilked', 'imp. & p. p.', 'of Bilk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilking', 'p. pr. & vb. n.', 'of Bilk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilk', 'v. t.', 'To frustrate or disappoint; to deceive or defraud, by\n   nonfulfillment of engagement; to leave in the lurch; to give the slip\n   to; as, to bilk a creditor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilk', 'n.', 'A thwarting an adversary in cribbage by spoiling his score; a\n   balk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilk', 'n.', 'A cheat; a trick; a hoax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilk', 'n.', 'Nonsense; vain words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bilk', 'n.', 'A person who tricks a creditor; an untrustworthy, tricky\n   person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A beak, as of a bird, or sometimes of a turtle or other\n   animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billed', 'imp. & p. p.', 'of Bill');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billing', 'p. pr. & vb. n.', 'of Bill');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'v. i.', 'To strike; to peck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'v. i.', 'To join bills, as doves; to caress in fondness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'The bell, or boom, of the bittern');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A cutting instrument, with hook-shaped point, and fitted with\n   a handle; -- used in pruning, etc.; a billhook. When short, called a\n   hand bill, when long, a hedge bill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A weapon of infantry, in the 14th and 15th centuries. A\n   common form of bill consisted of a broad, heavy, double-edged,\n   hook-shaped blade, having a short pike at the back and another at the\n   top, and attached to the end of a long staff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'One who wields a bill; a billman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A pickax, or mattock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'The extremity of the arm of an anchor; the point of or beyond\n   the fluke.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'v. t.', 'To work upon ( as to dig, hoe, hack, or chop anything)\n   with a bill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A declaration made in writing, stating some wrong the\n   complainant has suffered from the defendant, or a fault committed by\n   some person against a law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A writing binding the signer or signers to pay a certain sum\n   at a future day or on demand, with or without interest, as may be\n   stated in the document.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A form or draft of a law, presented to a legislature for\n   enactment; a proposed or projected law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'A paper, written or printed, and posted up or given away, to\n   advertise something, as a lecture, a play, or the sale of goods; a\n   placard; a poster; a handbill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'An account of goods sold, services rendered, or work done,\n   with the price or charge; a statement of a creditors claim, in gross\n   or by items; as, a grocers bill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'n.', 'Any paper, containing a statement of particulars; as, a bill\n   of charges or expenditures; a weekly bill of mortality; a bill of fare,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'v. t.', 'To advertise by a bill or public notice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill', 'v. t.', 'To charge or enter in a bill; as, to bill goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billage', 'n. / v. t. & i.', 'Same as Bilge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billard', 'n.', 'An English fish, allied to the cod; the coalfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billbeetle', 'n.', 'Alt. of Billbug');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billbug', 'n.', 'A weevil or curculio of various species, as the corn\n   weevil. See Curculio.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billboard', 'n.', 'A piece of thick plank, armed with iron plates, and\n   fixed on the bow or fore channels of a vessel, for the bill or fluke of\n   the anchor to rest on.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billboard', 'n.', 'A flat surface, as of a panel or of a fence, on which\n   bills are posted; a bulletin board.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill book', '', 'A book in which a person keeps an account of his notes,\n   bills, bills of exchange, etc., thus showing all that he issues and\n   receives.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill broker', '', 'One who negotiates the discount of bills.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billed', 'a.', 'Furnished with, or having, a bill, as a bird; -- used in\n   composition; as, broad-billed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A small paper; a note; a short letter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A ticket from a public officer directing soldiers at what\n   house to lodge; as, a billet of residence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billeted', 'imp. & p. p.', 'of Billet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billeting', 'p. pr. & vb. n.', 'of Billet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'v. t.', 'To direct, by a ticket or note, where to lodge. Hence:\n   To quarter, or place in lodgings, as soldiers in private houses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A small stick of wood, as for firewood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A short bar of metal, as of gold or iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'An ornament in Norman work, resembling a billet of wood\n   either square or round.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A strap which enters a buckle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A loop which receives the end of a buckled strap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet', 'n.', 'A bearing in the form of an oblong rectangle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billets-doux', 'pl. ', 'of Billet-doux');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billet-doux', 'n.', 'A love letter or note.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billethead', 'n.', 'A round piece of timber at the bow or stern of a\n   whaleboat, around which the harpoon lone is run out when the whale\n   darts off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billfish', 'n.', 'A name applied to several distinct fishes');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billfish', 'n.', 'The garfish (Tylosurus, / Belone, longirostris) and\n   allied species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billfish', 'n.', 'The saury, a slender fish of the Atlantic coast\n   (Scomberesox saurus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billfish', 'n.', 'The Tetrapturus albidus, a large oceanic species related\n   to the swordfish; the spearfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billfish', 'n.', 'The American fresh-water garpike (Lepidosteus osseus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billhead', 'n.', 'A printed form, used by merchants in making out bills or\n   rendering accounts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill holder', '', 'A person who holds a bill or acceptance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bill holder', '', 'A device by means of which bills, etc., are held.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billhook', 'n.', 'A thick, heavy knife with a hooked point, used in pruning\n   hedges, etc. When it has a short handle, it is sometimes called a hand\n   bill; when the handle is long, a hedge bill or scimiter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billiard', 'a.', 'Of or pertaining to the game of billiards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billiards', 'n.', 'A game played with ivory balls o a cloth-covered,\n   rectangular table, bounded by elastic cushions. The player seeks to\n   impel his ball with his cue so that it shall either strike (carom upon)\n   two other balls, or drive another ball into one of the pockets with\n   which the table sometimes is furnished.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billing', 'a. & n.', 'Caressing; kissing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billingsgate', 'n.', 'A market near the Billings gate in London, celebrated\n   for fish and foul language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billingsgate', 'n.', 'Coarsely abusive, foul, or profane language;\n   vituperation; ribaldry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billion', 'n.', 'According to the French and American method of numeration,\n   a thousand millions, or 1,000,000,000; according to the English method,\n   a million millions, or 1,000,000,000,000. See Numeration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billmen', 'pl. ', 'of Billman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billman', 'n.', 'One who uses, or is armed with, a bill or hooked ax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billon', 'n.', 'An alloy of gold and silver with a large proportion of\n   copper or other base metal, used in coinage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billot', 'n.', 'Bullion in the bar or mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billow', 'n.', 'A great wave or surge of the sea or other water, caused\n   usually by violent wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billow', 'n.', 'A great wave or flood of anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billowed', 'imp. & p. p.', 'of Billow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billowing', 'p. pr. & vb. n.', 'of Billow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billow', 'v. i.', 'To surge; to rise and roll in waves or surges; to\n   undulate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billowy', 'a.', 'Of or pertaining to billows; swelling or swollen into\n   large waves; full of billows or surges; resembling billows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Billposter', 'n.', 'Alt. of Billsticker');
