  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To tie, or confine with a cord, band, ligature, chain,\n   etc.; to fetter; to make fast; as, to bind grain in bundles; to bind a\n   prisoner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To confine, restrain, or hold by physical force or\n   influence of any kind; as, attraction binds the planets to the sun;\n   frost binds the earth, or the streams.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To cover, as with a bandage; to bandage or dress; --\n   sometimes with up; as, to bind up a wound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To make fast ( a thing) about or upon something, as by\n   tying; to encircle with something; as, to bind a belt about one; to\n   bind a compress upon a part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To prevent or restrain from customary or natural action;\n   as, certain drugs bind the bowels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To protect or strengthen by a band or binding, as the edge\n   of a carpet or garment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To sew or fasten together, and inclose in a cover; as, to\n   bind a book.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'Fig.: To oblige, restrain, or hold, by authority, law,\n   duty, promise, vow, affection, or other moral tie; as, to bind the\n   conscience; to bind by kindness; bound by affection; commerce binds\n   nations to each other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To bring (any one) under definite legal obligations; esp.\n   under the obligation of a bond or covenant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. t.', 'To place under legal obligation to serve; to indenture;\n   as, to bind an apprentice; -- sometimes with out; as, bound out to\n   service.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. i.', 'To tie; to confine by any ligature.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. i.', 'To contract; to grow hard or stiff; to cohere or stick\n   together in a mass; as, clay binds by heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bind', 'v. i.', 'To be restrained from motion, or from customary or natural\n   action, as by friction.');
