  INSERT INTO words (word, word_type, description)VALUES  (    'Binocular', 'a.', 'Having two eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Binocular', 'a.', 'Pertaining to both eyes; employing both eyes at once;\n   as, binocular vision.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Binocular', 'a.', 'Adapted to the use of both eyes; as, a binocular\n   microscope or telescope.');
