  INSERT INTO words (word, word_type, description)VALUES  (    'Bioplast', 'n.', 'A tiny mass of bioplasm, in itself a living unit and\n   having formative power, as a living white blood corpuscle; bioblast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bioplastic', 'a.', 'Bioplasmic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biorgan', 'n.', 'A physiological organ; a living organ; an organ endowed\n   with function; -- distinguished from idorgan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biostatics', 'n.', 'The physical phenomena of organized bodies, in\n   opposition to their organic or vital phenomena.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biostatistics', 'n.', 'Vital statistics.');
