  INSERT INTO words (word, word_type, description)VALUES  (    'Biquintile', 'n.', 'An aspect of the planets when they are distant from\n   each other by twice the fifth part of a great circle -- that is, twice\n   72 degrees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biradiate', 'a.', 'Alt. of Biradiated');
