  INSERT INTO words (word, word_type, description)VALUES  (    'Birch', 'n.', 'A tree of several species, constituting the genus Betula;\n   as, the white or common birch (B. alba) (also called silver birch and\n   lady birch); the dwarf birch (B. glandulosa); the paper or canoe birch\n   (B. papyracea); the yellow birch (B. lutea); the black or cherry birch\n   (B. lenta).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birch', 'n.', 'The wood or timber of the birch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birch', 'n.', 'A birch twig or birch twigs, used for flogging.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birch', 'n.', 'A birch-bark canoe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birch', 'a.', 'Of or pertaining to the birch; birchen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birched', 'imp. & p. p.', 'of Birch');
