  INSERT INTO words (word, word_type, description)VALUES  (    'Birchen', 'a.', 'Of or relating to birch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'n.', 'Orig., a chicken; the young of a fowl; a young eaglet; a\n   nestling; and hence, a feathered flying animal (see 2).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'n.', 'A warm-blooded, feathered vertebrate provided with wings. See\n   Aves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'n.', 'Specifically, among sportsmen, a game bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'n.', 'Fig.: A girl; a maiden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'v. i.', 'To catch or shoot birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird', 'v. i.', 'Hence: To seek for game or plunder; to thieve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdbolt', 'n.', 'A short blunt arrow for killing birds without piercing\n   them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdbolt', 'n.', 'Anything which smites without penetrating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird cage', 'n.', 'Alt. of Birdcage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdcage', 'n.', 'A cage for confining birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdcall', 'n.', 'A sound made in imitation of the note or cry of a bird\n   for the purpose of decoying the bird or its mate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdcall', 'n.', 'An instrument of any kind, as a whistle, used in making\n   the sound of a birdcall.');
