  INSERT INTO words (word, word_type, description)VALUES  (    'Birdlike', 'a.', 'Resembling a bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdlime', 'n.', 'An extremely adhesive viscid substance, usually made of\n   the middle bark of the holly, by boiling, fermenting, and cleansing it.\n   When a twig is smeared with this substance it will hold small birds\n   which may light upon it. Hence: Anything which insnares.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdlime', 'v. t.', 'To smear with birdlime; to catch with birdlime; to\n   insnare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdling', 'n.', 'A little bird; a nestling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdman', 'n.', 'A fowler or birdcatcher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird of paradise', '', 'The name of several very beautiful birds of the\n   genus Paradisea and allied genera, inhabiting New Guinea and the\n   adjacent islands. The males have brilliant colors, elegant plumes, and\n   often remarkable tail feathers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bird pepper', '', 'A species of capsicum (Capsicum baccatum); whose small,\n   conical, coral-red fruit is among the most piquant of all red peppers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-beak', 'n.', 'A molding whose section is thought to resemble a beak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birdseed', 'n.', 'Canary seed, hemp, millet or other small seeds used for\n   feeding caged birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-eye', 'a.', 'Seen from above, as if by a flying bird; embraced at a\n   glance; hence, general; not minute, or entering into details; as, a\n   birds-eye view.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-eye', 'a.', 'Marked with spots resembling birds eyes; as,\n   birds-eye diaper; birds-eye maple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-eye', 'n.', 'A plant with a small bright flower, as the Adonis or\n   pheasants eye, the mealy primrose (Primula farinosa); and species of\n   Veronica, Geranium, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-eye maple', '', 'See under Maple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Birds-foot', 'n.', 'A papilionaceous plant, the Ornithopus, having a\n   curved, cylindrical pod tipped with a short, clawlike point.');
