  INSERT INTO words (word, word_type, description)VALUES  (    'Bismare', 'n.', 'Alt. of Bismer');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismer', 'n.', 'Shame; abuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismer', 'n.', 'A rule steelyard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismer', 'n.', 'The fifteen-spined (Gasterosteus spinachia).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismillah', 'interj.', 'An adjuration or exclamation common among the\n   Mohammedans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismite', 'n.', 'Bismuth trioxide, or bismuth ocher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuth', 'n.', 'One of the elements; a metal of a reddish white color,\n   crystallizing in rhombohedrons. It is somewhat harder than lead, and\n   rather brittle; masses show broad cleavage surfaces when broken across.\n   It melts at 507¡ Fahr., being easily fused in the flame of a candle. It\n   is found in a native state, and as a constituent of some minerals.\n   Specific gravity 9.8. Atomic weight 207.5. Symbol Bi.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthal', 'a.', 'Containing bismuth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthic', 'a.', 'Of or pertaining to bismuth; containing bismuth, when\n   this element has its higher valence; as, bismuthic oxide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthiferous', 'a.', 'Containing bismuth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthine', 'n.', 'Alt. of Bismuthinite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthinite', 'n.', 'Native bismuth sulphide; -- sometimes called\n   bismuthite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthous', 'a.', 'Of, or containing, bismuth, when this element has its\n   lower valence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bismuthyl', 'n.', 'Hydrous carbonate of bismuth, an earthy mineral of a\n   dull white or yellowish color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bison', 'n.', 'The aurochs or European bison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bison', 'n.', 'The American bison buffalo (Bison Americanus); a large,\n   gregarious bovine quadruped with shaggy mane and short black horns,\n   which formerly roamed in herds over most of the temperate portion of\n   North America, but is now restricted to very limited districts in the\n   region of the Rocky Mountains, and is rapidly decreasing in numbers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bispinose', 'a.', 'Having two spines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisque', 'n.', 'Unglazed white porcelain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisque', 'n.', 'A point taken by the receiver of odds in the game of\n   tennis; also, an extra innings allowed to a weaker player in croquet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisque', 'n.', 'A white soup made of crayfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bissextile', 'n.', 'Leap year; every fourth year, in which a day is added\n   to the month of February on account of the excess of the tropical year\n   (365 d. 5 h. 48 m. 46 s.) above 365 days. But one day added every four\n   years is equivalent to six hours each year, which is 11 m. 14 s. more\n   than the excess of the real year. Hence, it is necessary to suppress\n   the bissextile day at the end of every century which is not divisible\n   by 400, while it is retained at the end of those which are divisible by\n   400.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bissextile', 'a.', 'Pertaining to leap year.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisson', 'a.', 'Purblind; blinding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bister', 'n.', 'Alt. of Bistre');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistre', 'n.', 'A dark brown pigment extracted from the soot of wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistipuled', 'a.', 'Having two stipules.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistort', 'n.', 'An herbaceous plant of the genus Polygonum, section\n   Bistorta; snakeweed; adderwort. Its root is used in medicine as an\n   astringent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistouries', 'pl. ', 'of Bistoury');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistoury', 'n.', 'A surgical instrument consisting of a slender knife,\n   either straight or curved, generally used by introducing it beneath the\n   part to be divided, and cutting towards the surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bistre', 'n.', 'See Bister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulcate', 'a.', 'Having two grooves or furrows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulcate', 'a.', 'Cloven; said of a foot or hoof.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulcous', 'a.', 'Bisulcate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulphate', 'n.', 'A sulphate in which but half the hydrogen of the acid\n   is replaced by a positive element or radical, thus making the\n   proportion of the acid to the positive or basic portion twice what it\n   is in the normal sulphates; an acid sulphate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulphide', 'n.', 'A sulphide having two atoms of sulphur in the molecule;\n   a disulphide, as in iron pyrites, FeS2; -- less frequently called\n   bisulphuret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulphite', 'n.', 'A salt of sulphurous acid in which the base replaces\n   but half the hydrogen of the acid; an acid sulphite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bisulphuret', 'n.', 'See Bisulphide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'The part of a bridle, usually of iron, which is inserted in\n   the mouth of a horse, and having appendages to which the reins are\n   fastened.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'Fig.: Anything which curbs or restrains.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitted', 'imp. & p. p.', 'of Bit');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitting', 'p. pr. & vb. n.', 'of Bit');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v. t.', 'To put a bridle upon; to put the bit in the mouth of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', '', 'imp. & p. p. of Bite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'A part of anything, such as may be bitten off or taken into\n   the mouth; a morsel; a bite. Hence: A small piece of anything; a\n   little; a mite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'Somewhat; something, but not very great.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'A tool for boring, of various forms and sizes, usually turned\n   by means of a brace or bitstock. See Bitstock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'The part of a key which enters the lock and acts upon the bolt\n   and tumblers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'The cutting iron of a plane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'v.', 'In the Southern and Southwestern States, a small silver coin\n   (as the real) formerly current; commonly, one worth about 12 1/2 cents;\n   also, the sum of 12 1/2 cents.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', '', '3d sing. pr. of Bid, for biddeth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitake', 'v. t.', 'To commend; to commit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitangent', 'a.', 'Possessing the property of touching at two points.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitangent', 'n.', 'A line that touches a curve in two points.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitartrate', 'n.', 'A salt of tartaric acid in which the base replaces but\n   half the acid hydrogen; an acid tartrate, as cream of tartar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitch', 'n.', 'The female of the canine kind, as of the dog, wolf, and fox.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitch', 'n.', 'An opprobrious name for a woman, especially a lewd woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', 'imp.', 'of Bite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitten', 'p. p.', 'of Bite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bit', '', 'of Bite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biting', 'p. pr. & vb. n.', 'of Bite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. t.', 'To seize with the teeth, so that they enter or nip the\n   thing seized; to lacerate, crush, or wound with the teeth; as, to bite\n   an apple; to bite a crust; the dog bit a man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. t.', 'To puncture, abrade, or sting with an organ (of some\n   insects) used in taking food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. t.', 'To cause sharp pain, or smarting, to; to hurt or injure,\n   in a literal or a figurative sense; as, pepper bites the mouth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. t.', 'To cheat; to trick; to take in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. t.', 'To take hold of; to hold fast; to adhere to; as, the\n   anchor bites the ground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. i.', 'To seize something forcibly with the teeth; to wound with\n   the teeth; to have the habit of so doing; as, does the dog bite?');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. i.', 'To cause a smarting sensation; to have a property which\n   causes such a sensation; to be pungent; as, it bites like pepper or\n   mustard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. i.', 'To cause sharp pain; to produce anguish; to hurt or\n   injure; to have the property of so doing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. i.', 'To take a bait into the mouth, as a fish does; hence, to\n   take a tempting offer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v. i.', 'To take or keep a firm hold; as, the anchor bites.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'The act of seizing with the teeth or mouth; the act of\n   wounding or separating with the teeth or mouth; a seizure with the\n   teeth or mouth, as of a bait; as, to give anything a hard bite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'The act of puncturing or abrading with an organ for taking\n   food, as is done by some insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'The wound made by biting; as, the pain of a dogs or snakes\n   bite; the bite of a mosquito.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'A morsel; as much as is taken at once by biting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'The hold which the short end of a lever has upon the thing to\n   be lifted, or the hold which one part of a machine has upon another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'A cheat; a trick; a fraud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'A sharper; one who cheats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bite', 'v.', 'A blank on the edge or corner of a page, owing to a portion\n   of the frisket, or something else, intervening between the type and\n   paper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biter', 'n.', 'One who, or that which, bites; that which bites often, or is\n   inclined to bite, as a dog or fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biter', 'n.', 'One who cheats; a sharper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biternate', 'a.', 'Doubly ternate, as when a petiole has three ternate\n   leaflets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitheism', 'n.', 'Belief in the existence of two gods; dualism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biting', 'a.', 'That bites; sharp; cutting; sarcastic; caustic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biting in', '', 'The process of corroding or eating into metallic plates,\n   by means of an acid. See Etch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitingly', 'adv.', 'In a biting manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitless', 'a.', 'Not having a bit or bridle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitstock', 'n.', 'A stock or handle for holding and rotating a bit; a\n   brace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitt', 'n.', 'See Bitts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitt', 'v. t.', 'To put round the bitts; as, to bitt the cable, in order to\n   fasten it or to slacken it gradually, which is called veering away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittacle', 'n.', 'A binnacle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitten', '', 'p. p. of Bite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitten', 'a.', 'Terminating abruptly, as if bitten off; premorse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'n.', 'AA turn of the cable which is round the bitts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'Having a peculiar, acrid, biting taste, like that of\n   wormwood or an infusion of hops; as, a bitter medicine; bitter as\n   aloes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'Causing pain or smart; piercing; painful; sharp; severe;\n   as, a bitter cold day.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'Causing, or fitted to cause, pain or distress to the\n   mind; calamitous; poignant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'Characterized by sharpness, severity, or cruelty; harsh;\n   stern; virulent; as, bitter reproach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'Mournful; sad; distressing; painful; pitiable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'n.', 'Any substance that is bitter. See Bitters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter', 'v. t.', 'To make bitter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterbump', 'n.', 'the butterbump or bittern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterful', 'a.', 'Full of bitterness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittering', 'n.', 'A bitter compound used in adulterating beer; bittern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterish', 'a.', 'Somewhat bitter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterling', 'n.', 'A roachlike European fish (Rhodima amarus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterly', 'adv.', 'In a bitter manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittern', 'n.', 'A wading bird of the genus Botaurus, allied to the herons,\n   of various species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittern', 'a.', 'The brine which remains in salt works after the salt is\n   concreted, having a bitter taste from the chloride of magnesium which\n   it contains.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittern', 'a.', 'A very bitter compound of quassia, cocculus Indicus, etc.,\n   used by fraudulent brewers in adulterating beer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterness', 'n.', 'The quality or state of being bitter, sharp, or acrid,\n   in either a literal or figurative sense; implacableness; resentfulness;\n   severity; keenness of reproach or sarcasm; deep distress, grief, or\n   vexation of mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterness', 'n.', 'A state of extreme impiety or enmity to God.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterness', 'n.', 'Dangerous error, or schism, tending to draw persons to\n   apostasy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitternut', 'n.', 'The swamp hickory (Carya amara). Its thin-shelled nuts\n   are bitter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterroot', 'n.', 'A plant (Lewisia rediviva) allied to the purslane, but\n   with fleshy, farinaceous roots, growing in the mountains of Idaho,\n   Montana, etc. It gives the name to the Bitter Root mountains and river.\n   The Indians call both the plant and the river Spaetlum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitters', 'n. pl.', 'A liquor, generally spirituous in which a bitter herb,\n   leaf, or root is steeped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitter spar', '', 'A common name of dolomite; -- so called because it\n   contains magnesia, the soluble salts of which are bitter. See Dolomite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittersweet', 'a.', 'Sweet and then bitter or bitter and then sweet; esp.\n   sweet with a bitter after taste; hence (Fig.); pleasant but painful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittersweet', 'n.', 'Anything which is bittersweet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittersweet', 'n.', 'A kind of apple so called.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittersweet', 'n.', 'A climbing shrub, with oval coral-red berries (Solanum\n   dulcamara); woody nightshade. The whole plant is poisonous, and has a\n   taste at first sweetish and then bitter. The branches are the officinal\n   dulcamara.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittersweet', 'n.', 'An American woody climber (Celastrus scandens); whose\n   yellow capsules open late in autumn, and disclose the red aril which\n   covers the seeds; -- also called Roxbury waxwork.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterweed', 'n.', 'A species of Ambrosia (A. artemisiaefolia); Roman worm\n   wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterwood', 'n.', 'A West Indian tree (Picraena excelsa) from the wood of\n   which the bitter drug Jamaica quassia is obtained.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitterwort', 'n.', 'The yellow gentian (Gentiana lutea); which has a very\n   bitter taste.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittock', 'n.', 'A small bit of anything, of indefinite size or quantity; a\n   short distance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bittor Bittour', 'n.', 'The bittern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitts', 'n. pl.', 'A frame of two strong timbers fixed perpendicularly in\n   the fore part of a ship, on which to fasten the cables as the ship\n   rides at anchor, or in warping. Other bitts are used for belaying\n   (belaying bitts); for sustaining the windlass (carrick bitts, winch\n   bitts, or windlass bitts); to hold the pawls of the windlass (pawl\n   bitts) etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitume', 'n.', 'Bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitumed', 'a.', 'Smeared with bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitumen', 'n.', 'Mineral pitch; a black, tarry substance, burning with a\n   bright flame; Jews pitch. It occurs as an abundant natural product in\n   many places, as on the shores of the Dead and Caspian Seas. It is used\n   in cements, in the construction of pavements, etc. See Asphalt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bitumen', 'n.', 'By extension, any one of the natural hydrocarbons,\n   including the hard, solid, brittle varieties called asphalt, the\n   semisolid maltha and mineral tars, the oily petroleums, and even the\n   light, volatile naphthas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminated', 'imp. & p. p.', 'of Bituminate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminating', 'p. pr. & vb. n.', 'of Bituminate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminate', 'v. t.', 'To treat or impregnate with bitumen; to cement with\n   bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminiferous', 'a.', 'Producing bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminization', 'n.', 'The process of bituminizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminized', 'imp. & p. p.', 'of Bituminize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminizing', 'p. pr. & vb. n.', 'of Bituminize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminize', 'v. t.', 'To prepare, treat, impregnate, or coat with bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bituminous', 'a.', 'Having the qualities of bitumen; compounded with\n   bitumen; containing bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biuret', 'n.', 'A white, crystalline, nitrogenous substance, C2O2N3H5,\n   formed by heating urea. It is intermediate between urea and cyanuric\n   acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalency', 'n.', 'The quality of being bivalent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalent', 'p. pr.', 'Equivalent in combining or displacing power to two\n   atoms of hydrogen; dyad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalve', 'n.', 'A mollusk having a shell consisting of two lateral plates\n   or valves joined together by an elastic ligament at the hinge, which is\n   usually strengthened by prominences called teeth. The shell is closed\n   by the contraction of two transverse muscles attached to the inner\n   surface, as in the clam, -- or by one, as in the oyster. See Mollusca.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalve', 'n.', 'A pericarp in which the seed case opens or splits into two\n   parts or valves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalve', 'a.', 'Having two shells or valves which open and shut, as the\n   oyster and certain seed vessels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalved', 'a.', 'Having two valves, as the oyster and some seed pods;\n   bivalve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalvous', 'a.', 'Bivalvular.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivalvular', 'a.', 'Having two valves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivaulted', 'a.', 'Having two vaults or arches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivector', 'n.', 'A term made up of the two parts / + /1 /-1, where / and\n   /1 are vectors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biventral', 'a.', 'Having two bellies or protuberances; as, a biventral, or\n   digastric, muscle, or the biventral lobe of the cerebellum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivial', 'a.', 'Of or relating to the bivium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivious', 'a.', 'Having, or leading, two ways.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivium', 'n.', 'One side of an echinoderm, including a pair of ambulacra,\n   in distinction from the opposite side (trivium); which includes three\n   ambulacra.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouac', 'n.', 'The watch of a whole army by night, when in danger of\n   surprise or attack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouac', 'n.', 'An encampment for the night without tents or covering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouacked', 'imp. & p. p.', 'of Bivouac');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouacking', 'p. pr. & vb. n.', 'of Bivouac');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouac', 'v. i.', 'To watch at night or be on guard, as a whole army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bivouac', 'v. i.', 'To encamp for the night without tents or covering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biweekly', 'a.', 'Occurring or appearing once every two weeks; fortnightly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biweekly', 'n.', 'A publication issued every two weeks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Biwreye', 'v. t.', 'To bewray; to reveal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bizantine', '', 'See Byzantine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bizarre', 'a.', 'Odd in manner or appearance; fantastic; whimsical;\n   extravagant; grotesque.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bizet', 'n.', 'The upper faceted portion of a brilliant-cut diamond, which\n   projects from the setting and occupies the zone between the girdle and\n   the table. See Brilliant, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blabbed', 'imp. & p. p.', 'of Blab');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blabbing', 'p. pr. & vb. n.', 'of Blab');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blab', 'v.', 'To utter or tell unnecessarily, or in a thoughtless manner;\n   to publish (secrets or trifles) without reserve or discretion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blab', 'v. i.', 'To talk thoughtlessly or without discretion; to tattle; to\n   tell tales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blab', 'n.', 'One who blabs; a babbler; a telltale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blabber', 'n.', 'A tattler; a telltale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'Destitute of light, or incapable of reflecting it; of the\n   color of soot or coal; of the darkest or a very dark color, the\n   opposite of white; characterized by such a color; as, black cloth;\n   black hair or eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'In a less literal sense: Enveloped or shrouded in darkness;\n   very dark or gloomy; as, a black night; the heavens black with clouds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'Fig.: Dismal, gloomy, or forbidding, like darkness;\n   destitute of moral light or goodness; atrociously wicked; cruel;\n   mournful; calamitous; horrible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'Expressing menace, or discontent; threatening; sullen;\n   foreboding; as, to regard one with black looks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'adv.', 'Sullenly; threateningly; maliciously; so as to produce\n   blackness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'That which is destitute of light or whiteness; the darkest\n   color, or rather a destitution of all color; as, a cloth has a good\n   black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'A black pigment or dye.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'A negro; a person whose skin is of a black color, or shaded\n   with black; esp. a member or descendant of certain African races.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'A black garment or dress; as, she wears black');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'Mourning garments of a black color; funereal drapery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'The part of a thing which is distinguished from the rest by\n   being black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'n.', 'A stain; a spot; a smooch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacked', 'imp. & p. p.', 'of Black');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacking', 'p. pr. & vb. n.', 'of Black');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'To make black; to blacken; to soil; to sully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black', 'a.', 'To make black and shining, as boots or a stove, by applying\n   blacking and then polishing with a brush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackamoor', 'n.', 'A negro or negress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black art', '', 'The art practiced by conjurers and witches; necromancy;\n   conjuration; magic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-a-vised', 'a.', 'Dark-visaged; swart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackball', 'n.', 'A composition for blacking shoes, boots, etc.; also, one\n   for taking impressions of engraved work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackball', 'n.', 'A ball of black color, esp. one used as a negative in\n   voting; -- in this sense usually two words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackballed', 'imp. & p. p.', 'of Blackball');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackballing', 'p. pr. & vb. n.', 'of Blackball');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackball', 'v. t.', 'To vote against, by putting a black ball into a\n   ballot box; to reject or exclude, as by voting against with black\n   balls; to ostracize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackball', 'v. t.', 'To blacken (leather, shoes, etc.) with blacking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackband', 'n.', 'An earthy carbonate of iron containing considerable\n   carbonaceous matter; -- valuable as an iron ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black bass', '', 'An edible, fresh-water fish of the United States, of the\n   genus Micropterus. the small-mouthed kind is M. dolomiei; the\n   large-mouthed is M. salmoides.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black bass', '', 'The sea bass. See Blackfish, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackberry', 'n.', 'The fruit of several species of bramble (Rubus); also,\n   the plant itself. Rubus fruticosus is the blackberry of England; R.\n   villosus and R. Canadensis are the high blackberry and low blackberry\n   of the United States. There are also other kinds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackbird', 'n.', 'In England, a species of thrush (Turdus merula); a\n   singing bird with a fin note; the merle. In America the name is given\n   to several birds, as the Quiscalus versicolor, or crow blackbird; the\n   Agelaeus phoeniceus, or red-winged blackbird; the cowbird; the rusty\n   grackle, etc. See Redwing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackboard', 'n.', 'A broad board painted black, or any black surface on\n   which writing, drawing, or the working of mathematical problems can be\n   done with chalk or crayons. It is much used in schools.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'One of several books of a political character, published\n   at different times and for different purposes; -- so called either from\n   the color of the binding, or from the character of the contents.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'A book compiled in the twelfth century, containing a\n   description of the court of exchequer of England, an official statement\n   of the revenues of the crown, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'A book containing details of the enormities practiced in\n   the English monasteries and religious houses, compiled by order of\n   their visitors under Henry VIII., to hasten their dissolution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'A book of admiralty law, of the highest authority,\n   compiled in the reign of Edw. III.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'A book kept for the purpose of registering the names of\n   persons liable to censure or punishment, as in the English\n   universities, or the English armies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black book', '', 'Any book which treats of necromancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-browed', 'a.', 'Having black eyebrows. Hence: Gloomy; dismal;\n   threatening; forbidding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackburnian warbler', '', 'A beautiful warbler of the United States\n   (Dendroica Blackburniae). The male is strongly marked with orange,\n   yellow, and black on the head and neck, and has an orange-yellow\n   breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcap', 'n.', 'A small European song bird (Sylvia atricapilla); with a\n   black crown; the mock nightingale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcap', 'n.', 'An American titmouse (Parus atricapillus); the chickadee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcap', 'n.', 'An apple roasted till black, to be served in a dish of\n   boiled custard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcap', 'n.', 'The black raspberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcoat', 'n.', 'A clergyman; -- familiarly so called, as a soldier is\n   sometimes called a redcoat or a bluecoat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackcock', 'n.', 'The male of the European black grouse (Tetrao tetrix,\n   Linn.); -- so called by sportsmen. The female is called gray hen. See\n   Heath grouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black death', '', 'A pestilence which ravaged Europe and Asia in the\n   fourteenth century.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackened', 'imp. & p. p.', 'of Blacken');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackening', 'p. pr. & vb. n.', 'of Blacken');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacken', 'v. t.', 'To make or render black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacken', 'v. t.', 'To make dark; to darken; to cloud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacken', 'v. t.', 'To defame; to sully, as reputation; to make infamous;\n   as, vice blackens the character.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacken', 'v. i.', 'To grow black or dark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackener', 'n.', 'One who blackens.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-eyed', 'a.', 'Having black eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-faced', 'a.', 'Having a black, dark, or gloomy face or aspect.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfeet', 'n. pl.', 'A tribe of North American Indians formerly\n   inhabiting the country from the upper Missouri River to the\n   Saskatchewan, but now much reduced in numbers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfin', 'n.', 'See Bluefin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfish', 'n.', 'A small kind of whale, of the genus Globicephalus, of\n   several species. The most common is G. melas. Also sometimes applied to\n   other whales of larger size.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfish', 'n.', 'The tautog of New England (Tautoga).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfish', 'n.', 'The black sea bass (Centropristis atrarius) of the\n   Atlantic coast. It is excellent food fish; -- locally called also black\n   Harry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfish', 'n.', 'A fish of southern Europe (Centrolophus pompilus) of the\n   Mackerel family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfish', 'n.', 'The female salmon in the spawning season.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfoot', 'a.', 'Of or pertaining to the Blackfeet; as, a Blackfoot\n   Indian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackfoot', 'n.', 'A Blackfoot Indian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black friar', '', 'A friar of the Dominican order; -- called also predicant\n   and preaching friar; in France, Jacobin. Also, sometimes, a\n   Benedictine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'n.', 'The scullions and lower menials of a court, or of a\n   noblemans household, who, in a removal from one residence to another,\n   had charge of the kitchen utensils, and being smutted by them, were\n   jocularly called the \"black guard\"; also, the servants and hangers-on\n   of an army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'n.', 'The criminals and vagrants or vagabonds of a town or\n   community, collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'n.', 'A person of stained or low character, esp. one who uses\n   scurrilous language, or treats others with foul abuse; a scoundrel; a\n   rough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'n.', 'A vagrant; a bootblack; a gamin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguarded', 'imp. & p. p.', 'of Blackguard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguarding', 'p. pr. & vb. n.', 'of Blackguard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'v. t.', 'To revile or abuse in scurrilous language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguard', 'a.', 'Scurrilous; abusive; low; worthless; vicious; as,\n   blackguard language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguardism', 'n.', 'The conduct or language of a blackguard; ruffianism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackguardly', 'adv. & a.', 'In the manner of or resembling a blackguard;\n   abusive; scurrilous; ruffianly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackhead', 'n.', 'The scaup duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackheart', 'n.', 'A heart-shaped cherry with a very dark-colored skin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-hearted', 'a.', 'Having a wicked, malignant disposition; morally bad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black hole', '', 'A dungeon or dark cell in a prison; a military lock-up or\n   guardroom; -- now commonly with allusion to the cell (the Black Hole)\n   in a fort at Calcutta, into which 146 English prisoners were thrust by\n   the nabob Suraja Dowla on the night of June 20, 17656, and in which 123\n   of the prisoners died before morning from lack of air.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacking', 'n.', 'Any preparation for making things black; esp. one for\n   giving a black luster to boots and shoes, or to stoves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacking', 'n.', 'The act or process of making black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackish', 'a.', 'Somewhat black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-jack', 'n.', 'A name given by English miners to sphalerite, or zinc\n   blende; -- called also false galena. See Blende.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-jack', 'n.', 'Caramel or burnt sugar, used to color wines, spirits,\n   ground coffee, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-jack', 'n.', 'A large leather vessel for beer, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-jack', 'n.', 'The Quercus nigra, or barren oak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-jack', 'n.', 'The ensign of a pirate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black lead', '', 'Plumbago; graphite. It leaves a blackish mark somewhat\n   like lead. See Graphite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacklead', 'v. t.', 'To coat or to polish with black lead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackleg', 'n.', 'A notorious gambler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackleg', 'n.', 'A disease among calves and sheep, characterized by a\n   settling of gelatinous matter in the legs, and sometimes in the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black letter', '', 'The old English or Gothic letter, in which the Early\n   English manuscripts were written, and the first English books were\n   printed. It was conspicuous for its blackness. See Type.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-letter', 'a.', 'Written or printed in black letter; as, a\n   black-letter manuscript or book.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-letter', 'a.', 'Given to the study of books in black letter; that is,\n   of old books; out of date.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-letter', 'a.', 'Of or pertaining to the days in the calendar not\n   marked with red letters as saints days. Hence: Unlucky; inauspicious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacklist', 'v. t.', 'To put in a black list as deserving of suspicion,\n   censure, or punishment; esp. to put in a list of persons stigmatized as\n   insolvent or untrustworthy, -- as tradesmen and employers do for mutual\n   protection; as, to blacklist a workman who has been discharged. See\n   Black list, under Black, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackly', 'adv.', 'In a black manner; darkly, in color; gloomily;\n   threateningly; atrociously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmail', 'n.', 'A certain rate of money, corn, cattle, or other thing,\n   anciently paid, in the north of England and south of Scotland, to\n   certain men who were allied to robbers, or moss troopers, to be by them\n   protected from pillage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmail', 'n.', 'Payment of money exacted by means of intimidation; also,\n   extortion of money from a person by threats of public accusation,\n   exposure, or censure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmail', 'n.', 'Black rent, or rent paid in corn, flesh, or the lowest\n   coin, a opposed to \"white rent\", which paid in silver.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmailed', 'imp. & p. p.', 'of Blackmail');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmailing', 'p. pr. & vb. n.', 'of Blackmail');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmail', 'v. t.', 'To extort money from by exciting fears of injury\n   other than bodily harm, as injury to reputation, distress of mind,\n   etc.; as, to blackmail a merchant by threatening to expose an alleged\n   fraud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmailer', 'n.', 'One who extorts, or endeavors to extort, money, by\n   black mailing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmailing', 'n.', 'The act or practice of extorting money by exciting\n   fears of injury other than bodily harm, as injury to reputation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black Monday', '', 'Easter Monday, so called from the severity of that day\n   in 1360, which was so unusual that many of Edward III.s soldiers, then\n   before Paris, died from the cold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black Monday', '', 'The first Monday after the holidays; -- so called by\n   English schoolboys.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black monk', '', 'A Benedictine monk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackmoor', 'n.', 'See Blackamoor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black-mouthed', 'a.', 'Using foul or scurrilous language; slanderous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackness', 'n.', 'The quality or state of being black; black color;\n   atrociousness or enormity in wickedness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackpoll', 'n.', 'A warbler of the United States (Dendroica striata).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black pudding', '', 'A kind of sausage made of blood, suet, etc., thickened\n   with meal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black Rod', '', 'the usher to the Chapter of the Garter, so called from the\n   black rod which he carries. He is of the kings chamber, and also usher\n   to the House of Lords.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black Rod', '', 'An usher in the legislature of British colonies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackroot', 'n.', 'See Colicroot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacks', 'n. pl.', 'The name of a kind of in used in copperplate printing,\n   prepared from the charred husks of the grape, and residue of the wine\n   press.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacks', 'n. pl.', 'Soot flying in the air.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacks', 'n. pl.', 'Black garments, etc. See Black, n., 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacksalter', 'n.', 'One who makes crude potash, or black salts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black salts', '', 'Crude potash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacksmith', 'n.', 'A smith who works in iron with a forge, and makes iron\n   utensils, horseshoes, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacksmith', 'n.', 'A fish of the Pacific coast (Chromis, / Heliastes,\n   punctipinnis); of a blackish color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black snake', 'n.', 'Alt. of Blacksnake');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacksnake', 'n.', 'A snake of a black color, of which two species are\n   common in the United States, the Bascanium constrictor, or racer,\n   sometimes six feet long, and the Scotophis Alleghaniensis, seven or\n   eight feet long.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackstrap', 'n.', 'A mixture of spirituous liquor (usually rum) and\n   molasses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackstrap', 'n.', 'Bad port wine; any common wine of the Mediterranean; --\n   so called by sailors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacktail', 'n.', 'A fish; the ruff or pope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blacktail', 'n.', 'The black-tailed deer (Cervus / Cariacus Columbianus) of\n   California and Oregon; also, the mule deer of the Rocky Mountains. See\n   Mule deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackthorn', 'n.', 'A spreading thorny shrub or small tree (Prunus\n   spinosa); with blackish bark, and bearing little black plums, which are\n   called sloes; the sloe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackthorn', 'n.', 'A species of Crataegus or hawthorn (C. tomentosa). Both\n   are used for hedges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black vomit', '', 'A copious vomiting of dark-colored matter; or the\n   substance so discharged; -- one of the most fatal symptoms in yellow\n   fever.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Black wash', 'n.', 'Alt. of Blackwash');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackwash', 'n.', 'A lotion made by mixing calomel and lime water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackwash', 'n.', 'A wash that blackens, as opposed to whitewash; hence,\n   figuratively, calumny.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackwood', 'n.', 'A name given to several dark-colored timbers. The East\n   Indian black wood is from the tree Dalbergia latifolia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blackwork', 'n.', 'Work wrought by blacksmiths; -- so called in distinction\n   from that wrought by whitesmiths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'n.', 'A bag or sac in animals, which serves as the receptacle of\n   some fluid; as, the urinary bladder; the gall bladder; -- applied\n   especially to the urinary bladder, either within the animal, or when\n   taken out and inflated with air.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'n.', 'Any vesicle or blister, especially if filled with air, or\n   a thin, watery fluid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'n.', 'A distended, membranaceous pericarp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'n.', 'Anything inflated, empty, or unsound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladdered', 'imp. & p. p.', 'of Bladder');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladdering', 'p. pr. & vb. n.', 'of Bladder');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'v. t.', 'To swell out like a bladder with air; to inflate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladder', 'v. t.', 'To put up in bladders; as, bladdered lard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladderwort', 'n.', 'A genus (Utricularia) of aquatic or marshy plants,\n   which usually bear numerous vesicles in the divisions of the leaves.\n   These serve as traps for minute animals. See Ascidium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladdery', 'a.', 'Having bladders; also, resembling a bladder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'Properly, the leaf, or flat part of the leaf, of any plant,\n   especially of gramineous plants. The term is sometimes applied to the\n   spire of grasses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'The cutting part of an instrument; as, the blade of a knife\n   or a sword.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'The broad part of an oar; also, one of the projecting arms\n   of a screw propeller.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'The scapula or shoulder blade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'The principal rafters of a roof.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'The four large shell plates on the sides, and the five large\n   ones of the middle, of the carapace of the sea turtle, which yield the\n   best tortoise shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'n.', 'A sharp-witted, dashing, wild, or reckless, fellow; -- a\n   word of somewhat indefinite meaning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'v. t.', 'To furnish with a blade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blade', 'v. i.', 'To put forth or have a blade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladebone', 'n.', 'The scapula. See Blade, 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladed', 'a.', 'Having a blade or blades; as, a two-bladed knife.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladed', 'a.', 'Divested of blades; as, bladed corn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladed', 'a.', 'Composed of long and narrow plates, shaped like the blade\n   of a knife.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladefish', 'n.', 'A long, thin, marine fish of Europe (Trichiurus\n   lepturus); the ribbon fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bladesmith', 'n.', 'A sword cutler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blady', 'a.', 'Consisting of blades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blae', 'a.', 'Dark blue or bluish gray; lead-colored.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaeberry', 'n.', 'The bilberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blague', 'n.', 'Mendacious boasting; falsehood; humbug.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blain', 'n.', 'An inflammatory swelling or sore; a bulla, pustule, or\n   blister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blain', 'n.', 'A bladder growing on the root of the tongue of a horse,\n   against the windpipe, and stopping the breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blamable', 'a.', 'Deserving of censure; faulty; culpable; reprehensible;\n   censurable; blameworthy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blamed', 'imp. & p. p.', 'of Blame');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaming', 'p. pr. & vb. n.', 'of Blame');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blame', 'v. t.', 'To censure; to express disapprobation of; to find fault\n   with; to reproach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blame', 'v. t.', 'To bring reproach upon; to blemish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blame', 'v.', 'An expression of disapprobation fir something deemed to be\n   wrong; imputation of fault; censure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blame', 'v.', 'That which is deserving of censure or disapprobation;\n   culpability; fault; crime; sin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blame', 'v.', 'Hurt; injury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blameful', 'a.', 'Faulty; meriting blame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blameful', 'a.', 'Attributing blame or fault; implying or conveying\n   censure; faultfinding; censorious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blameless', 'a.', 'Free from blame; without fault; innocent; guiltless; --\n   sometimes followed by of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blamelessly', 'adv.', 'In a blameless manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blamelessness', 'n.', 'The quality or state of being blameless; innocence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blamer', 'n.', 'One who blames.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blameworthy', 'a.', 'Deserving blame; culpable; reprehensible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blancard', 'n.', 'A kind of linen cloth made in Normandy, the thread of\n   which is partly blanches before it is woven.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanched', 'imp. & p. p.', 'of Blanch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanching', 'p. pr. & vb. n.', 'of Blanch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To take the color out of, and make white; to bleach; as, to\n   blanch linen; age has blanched his hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To bleach by excluding the light, as the stalks or leaves\n   of plants, by earthing them up or tying them together.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To make white by removing the skin of, as by scalding; as,\n   to blanch almonds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To whiten, as the surface of meat, by plunging into boiling\n   water and afterwards into cold, so as to harden the surface and retain\n   the juices.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To give a white luster to (silver, before stamping, in the\n   process of coining.).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'To cover (sheet iron) with a coating of tin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'a.', 'Fig.: To whiten; to give a favorable appearance to; to\n   whitewash; to palliate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'v. i.', 'To grow or become white; as, his cheek blanched with\n   fear; the rose blanches in the sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'v. t.', 'To avoid, as from fear; to evade; to leave unnoticed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'v. t.', 'To cause to turn aside or back; as, to blanch a deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'v. i.', 'To use evasion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch', 'n.', 'Ore, not in masses, but mixed with other minerals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blancher', 'n.', 'One who, or that which, blanches or whitens; esp., one\n   who anneals and cleanses money; also, a chemical preparation for this\n   purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blancher', 'n.', 'One who, or that which, frightens away or turns aside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanch holding', '', 'A mode of tenure by the payment of a small duty in\n   white rent (silver) or otherwise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanchimeter', 'n.', 'An instrument for measuring the bleaching power of\n   chloride of lime and potash; a chlorometer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blancmange', 'n.', 'A preparation for desserts, etc., made from isinglass,\n   sea moss, cornstarch, or other gelatinous or starchy substance, with\n   mild, usually sweetened and flavored, and shaped in a mold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blancmanger', 'n.', 'A sort of fricassee with white sauce, variously made\n   of capon, fish, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bland', 'a.', 'Mild; soft; gentle; smooth and soothing in manner; suave;\n   as, a bland temper; bland persuasion; a bland sycophant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bland', 'a.', 'Having soft and soothing qualities; not drastic or\n   irritating; not stimulating; as, a bland oil; a bland diet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandation', 'n.', 'Flattery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandiloquence', 'n.', 'Mild, flattering speech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandiloquous', 'a.', 'Alt. of Blandiloquious');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandiloquious', 'a.', 'Fair-spoken; flattering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandise', 'v. i.', 'To blandish any one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandished', 'imp. & p. p.', 'of Blandish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandishing', 'p. pr. & vb. n.', 'of Blandish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandish', 'v. t.', 'To flatter with kind words or affectionate actions; to\n   caress; to cajole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandish', 'v. t.', 'To make agreeable and enticing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandisher', 'n.', 'One who uses blandishments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandishment', 'n.', 'The act of blandishing; a word or act expressive of\n   affection or kindness, and tending to win the heart; soft words and\n   artful caresses; cajolery; allurement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandly', 'adv.', 'In a bland manner; mildly; suavely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blandness', 'n.', 'The state or quality of being bland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Of a white or pale color; without color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Free from writing, printing, or marks; having an empty space\n   to be filled in with some special writing; -- said of checks, official\n   documents, etc.; as, blank paper; a blank check; a blank ballot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Utterly confounded or discomfited.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Empty; void; without result; fruitless; as, a blank space; a\n   blank day.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Lacking characteristics which give variety; as, a blank\n   desert; a blank wall; destitute of interests, affections, hopes, etc.;\n   as, to live a blank existence; destitute of sensations; as, blank\n   unconsciousness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Lacking animation and intelligence, or their associated\n   characteristics, as expression of face, look, etc.; expressionless;\n   vacant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'a.', 'Absolute; downright; unmixed; as, blank terror.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'Any void space; a void space on paper, or in any written\n   instrument; an interval void of consciousness, action, result, etc; a\n   void.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A lot by which nothing is gained; a ticket in a lottery on\n   which no prize is indicated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A paper unwritten; a paper without marks or characters a\n   blank ballot; -- especially, a paper on which are to be inserted\n   designated items of information, for which spaces are left vacant; a\n   bland form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A paper containing the substance of a legal instrument, as a\n   deed, release, writ, or execution, with spaces left to be filled with\n   names, date, descriptions, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'The point aimed at in a target, marked with a white spot;\n   hence, the object to which anything is directed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'Aim; shot; range.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A kind of base silver money, first coined in England by\n   Henry V., and worth about 8 pence; also, a French coin of the\n   seventeenth century, worth about 4 pence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A piece of metal prepared to be made into something by a\n   further operation, as a coin, screw, nuts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'n.', 'A piece or division of a piece, without spots; as, the\n   \"double blank\"; the \"six blank.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanked', 'imp. & p. p.', 'of Blank');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanking', 'p. pr. & vb. n.', 'of Blank');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'v. t.', 'To make void; to annul.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blank', 'v. t.', 'To blanch; to make blank; to damp the spirits of; to\n   dispirit or confuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'a.', 'A heavy, loosely woven fabric, usually of wool, and having\n   a nap, used in bed clothing; also, a similar fabric used as a robe; or\n   any fabric used as a cover for a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'a.', 'A piece of rubber, felt, or woolen cloth, used in the\n   tympan to make it soft and elastic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'a.', 'A streak or layer of blubber in whales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanketed', 'imp. & p. p.', 'of Blanket');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanketing', 'p. pr. & vb. n.', 'of Blanket');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'v. t.', 'To cover with a blanket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'v. t.', 'To toss in a blanket by way of punishment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanket', 'v. t.', 'To take the wind out of the sails of (another vessel)\n   by sailing to windward of her.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanketing', 'n.', 'Cloth for blankets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanketing', 'n.', 'The act or punishment of tossing in a blanket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blankly', 'adv.', 'In a blank manner; without expression; vacuously; as, to\n   stare blankly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blankly', 'adv.', 'Directly; flatly; point blank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blankness', 'n.', 'The state of being blank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanquette', 'n.', 'A white fricassee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blanquillo', 'n.', 'A large fish of Florida and the W. Indies (Caulolatilus\n   chrysops). It is red, marked with yellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blared', 'imp. & p. p.', 'of Blare');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaring', 'p. pr. & vb. n.', 'of Blare');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blare', 'v. i.', 'To sound loudly and somewhat harshly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blare', 'v. t.', 'To cause to sound like the blare of a trumpet; to\n   proclaim loudly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blare', 'n.', 'The harsh noise of a trumpet; a loud and somewhat harsh\n   noise, like the blast of a trumpet; a roar or bellowing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blarney', 'n.', 'Smooth, wheedling talk; flattery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blarneyed', 'imp. & p. p.', 'of Blarney');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blarneying', 'p. pr. & vb. n.', 'of Blarney');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blarney', 'v. t.', 'To influence by blarney; to wheedle with smooth talk;\n   to make or accomplish by blarney.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blase', 'a.', 'Having the sensibilities deadened by excess or frequency of\n   enjoyment; sated or surfeited with pleasure; used up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemed', 'imp. & p. p.', 'of Blaspheme');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaspheming', 'p. pr. & vb. n.', 'of Blaspheme');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaspheme', 'v.', 'To speak of, or address, with impious irreverence; to\n   revile impiously (anything sacred); as, to blaspheme the Holy Spirit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaspheme', 'v.', 'Figuratively, of persons and things not religiously\n   sacred, but held in high honor: To calumniate; to revile; to abuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaspheme', 'v. i.', 'To utter blasphemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemer', 'n.', 'One who blasphemes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemous', 'a.', 'Speaking or writing blasphemy; uttering or exhibiting\n   anything impiously irreverent; profane; as, a blasphemous person;\n   containing blasphemy; as, a blasphemous book; a blasphemous caricature.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemously', 'adv.', 'In a blasphemous manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemy', 'n.', 'An indignity offered to God in words, writing, or signs;\n   impiously irreverent words or signs addressed to, or used in reference\n   to, God; speaking evil of God; also, the act of claiming the attributes\n   or prerogatives of deity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasphemy', 'n.', 'Figuratively, of things held in high honor: Calumny;\n   abuse; vilification.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-blast', '', 'A suffix or terminal formative, used principally in\n   biological terms, and signifying growth, formation; as, bioblast,\n   epiblast, mesoblast, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'A violent gust of wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'A forcible stream of air from an orifice, as from a bellows,\n   the mouth, etc. Hence: The continuous blowing to which one charge of\n   ore or metal is subjected in a furnace; as, to melt so many tons of\n   iron at a blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'The exhaust steam from and engine, driving a column of air\n   out of a boiler chimney, and thus creating an intense draught through\n   the fire; also, any draught produced by the blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'The sound made by blowing a wind instrument; strictly, the\n   sound produces at one breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'A sudden, pernicious effect, as if by a noxious wind,\n   especially on animals and plants; a blight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'The act of rending, or attempting to rend, heavy masses of\n   rock, earth, etc., by the explosion of gunpowder, dynamite, etc.; also,\n   the charge used for this purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'n.', 'A flatulent disease of sheep.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasted', 'imp. & p. p.', 'of Blast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasting', 'p. pr. & vb. n.', 'of Blast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. t.', 'To injure, as by a noxious wind; to cause to wither; to\n   stop or check the growth of, and prevent from fruit-bearing, by some\n   pernicious influence; to blight; to shrivel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. t.', 'Hence, to affect with some sudden violence, plague,\n   calamity, or blighting influence, which destroys or causes to fail; to\n   visit with a curse; to curse; to ruin; as, to blast pride, hopes, or\n   character.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. t.', 'To confound by a loud blast or din.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. t.', 'To rend open by any explosive agent, as gunpowder,\n   dynamite, etc.; to shatter; as, to blast rocks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. i.', 'To be blighted or withered; as, the bud blasted in the\n   blossom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast', 'v. i.', 'To blow; to blow on a trumpet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasted', 'a.', 'Blighted; withered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasted', 'a.', 'Confounded; accursed; detestable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasted', 'a.', 'Rent open by an explosive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastemata', 'pl. ', 'of Blastema');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastema', 'n.', 'The structureless, protoplasmic tissue of the embryo; the\n   primitive basis of an organ yet unformed, from which it grows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastemal', 'a.', 'Relating to the blastema; rudimentary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastematic', 'a.', 'Connected with, or proceeding from, the blastema;\n   blastemal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaster', 'n.', 'One who, or that which, blasts or destroys.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastide', 'n.', 'A small, clear space in the segments of the ovum, the\n   precursor of the nucleus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasting', 'n.', 'A blast; destruction by a blast, or by some pernicious\n   cause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasting', 'n.', 'The act or process of one who, or that which, blasts; the\n   business of one who blasts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastment', 'n.', 'A sudden stroke or injury produced by some destructive\n   cause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastocarpous', 'a.', 'Germinating inside the pericarp, as the mangrove.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastocoele', 'n.', 'The cavity of the blastosphere, or segmentation\n   cavity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastocyst', 'n.', 'The germinal vesicle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastoderm', 'n.', 'The germinal membrane in an ovum, from which the embryo\n   is developed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastodermatic', 'a.', 'Alt. of Blastodermic');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastodermic', 'a.', 'Of or pertaining to the blastoderm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastogenesis', 'n.', 'Multiplication or increase by gemmation or budding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastoid', 'n.', 'One of the Blastoidea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastoidea', 'n. pl.', 'One of the divisions of Crinoidea found fossil in\n   paleozoic rocks; pentremites. They are so named on account of their\n   budlike form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastomere', 'n.', 'One of the segments first formed by the division of the\n   ovum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastophoral', 'a.', 'Alt. of Blastophoric');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastophoric', 'a.', 'Relating to the blastophore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastophore', 'n.', 'That portion of the spermatospore which is not\n   converted into spermatoblasts, but carries them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastopore', 'n.', 'The pore or opening leading into the cavity of\n   invagination, or archenteron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastosphere', 'n.', 'The hollow globe or sphere formed by the arrangement\n   of the blastomeres on the periphery of an impregnated ovum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastostyle', 'n.', 'In certain hydroids, an imperfect zooid, whose special\n   function is to produce medusoid buds. See Hydroidea, and Athecata.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blast pipe', '', 'The exhaust pipe of a steam engine, or any pipe\n   delivering steam or air, when so constructed as to cause a blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastula', 'n.', 'That stage in the development of the ovum in which the\n   outer cells of the morula become more defined and form the blastoderm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blastule', 'n.', 'Same as Blastula.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasty', 'a.', 'Affected by blasts; gusty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blasty', 'a.', 'Causing blast or injury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blat', 'v. i.', 'To cry, as a calf or sheep; to bleat; to make a senseless\n   noise; to talk inconsiderately.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blat', 'v. t.', 'To utter inconsiderately.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatancy', 'n.', 'Blatant quality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatant', 'a.', 'Bellowing, as a calf; bawling; brawling; clamoring;\n   disagreeably clamorous; sounding loudly and harshly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatantly', 'adv.', 'In a blatant manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatherskite', 'n.', 'A blustering, talkative fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blattered', 'imp. & p. p.', 'of Blatter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatter', 'v. i.', 'To prate; to babble; to rail; to make a senseless\n   noise; to patter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatteration', 'n.', 'Blattering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatterer', 'n.', 'One who blatters; a babbler; a noisy, blustering\n   boaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blattering', 'n.', 'Senseless babble or boasting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blatteroon', 'n.', 'A senseless babbler or boaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaubok', 'n.', 'The blue buck. See Blue buck, under Blue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blay', 'a.', 'A fish. See Bleak, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'n.', 'A stream of gas or vapor emitting light and heat in the\n   process of combustion; a bright flame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'n.', 'Intense, direct light accompanied with heat; as, to seek\n   shelter from the blaze of the sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'n.', 'A bursting out, or active display of any quality; an\n   outburst; a brilliant display.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'n.', 'A white spot on the forehead of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'n.', 'A spot made on trees by chipping off a piece of the bark,\n   usually as a surveyors mark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazed', 'imp. & p. p.', 'of Blaze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazing', 'p. pr. & vb. n.', 'of Blaze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. i.', 'To shine with flame; to glow with flame; as, the fire\n   blazes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. i.', 'To send forth or reflect glowing or brilliant light; to\n   show a blaze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. i.', 'To be resplendent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. t.', 'To mark (a tree) by chipping off a piece of the bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. t.', 'To designate by blazing; to mark out, as by blazed trees;\n   as, to blaze a line or path.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. i.', 'To make public far and wide; to make known; to render\n   conspicuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blaze', 'v. i.', 'To blazon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazer', 'n.', 'One who spreads reports or blazes matters abroad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazing', 'a.', 'Burning with a blaze; as, a blazing fire; blazing torches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'n.', 'A shield.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'n.', 'An heraldic shield; a coat of arms, or a bearing on a coat\n   of arms; armorial bearings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'n.', 'The art or act of describing or depicting heraldic bearings\n   in the proper language or manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'n.', 'Ostentatious display, either by words or other means;\n   publication; show; description; record.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazoned', 'imp. & p. p.', 'of Blazon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazoning', 'p. pr. & vb. n.', 'of Blazon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'v. t.', 'To depict in colors; to display; to exhibit\n   conspicuously; to publish or make public far and wide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'v. t.', 'To deck; to embellish; to adorn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'v. t.', 'To describe in proper terms (the figures of heraldic\n   devices); also, to delineate (armorial bearings); to emblazon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazon', 'v. i.', 'To shine; to be conspicuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazoner', 'n.', 'One who gives publicity, proclaims, or blazons; esp., one\n   who blazons coats of arms; a herald.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazonment', 'n.', 'The act of blazoning; blazoning; emblazonment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazonry', 'n.', 'Same as Blazon, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazonry', 'n.', 'A coat of arms; an armorial bearing or bearings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blazonry', 'n.', 'Artistic representation or display.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blea', 'n.', 'The part of a tree which lies immediately under the bark; the\n   alburnum or sapwood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleaberry', 'n.', 'See Blaeberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleached', 'imp. & p. p.', 'of Bleach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleaching', 'p. pr. & vb. n.', 'of Bleach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleach', 'a.', 'To make white, or whiter; to remove the color, or stains,\n   from; to blanch; to whiten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleach', 'v. i.', 'To grow white or lose color; to whiten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleached', 'a.', 'Whitened; make white.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleacher', 'n.', 'One who whitens, or whose occupation is to whiten, by\n   bleaching.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleacheries', 'pl. ', 'of Bleachery');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleachery', 'n.', 'A place or an establishment where bleaching is done.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleaching', 'n.', 'The act or process of whitening, by removing color or\n   stains; esp. the process of whitening fabrics by chemical agents.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleak', 'a.', 'Without color; pale; pallid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleak', 'a.', 'Desolate and exposed; swept by cold winds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleak', 'a.', 'Cold and cutting; cheerless; as, a bleak blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleak', 'a.', 'A small European river fish (Leuciscus alburnus); of the\n   family Cyprinidae; the blay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleaky', 'a.', 'Bleak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blear', 'v.', 'Dim or sore with water or rheum; -- said of the eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blear', 'v.', 'Causing or caused by dimness of sight; dim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleared', 'imp. & p. p.', 'of Blear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blearing', 'p. pr. & vb. n.', 'of Blear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blear', 'v. t.', 'To make somewhat sore or watery, as the eyes; to dim, or\n   blur, as the sight. Figuratively: To obscure (mental or moral\n   perception); to blind; to hoodwink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleared', 'a.', 'Dimmed, as by a watery humor; affected with rheum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleareye', 'n.', 'A disease of the eyelids, consisting in chronic\n   inflammation of the margins, with a gummy secretion of sebaceous\n   matter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blear-eyed', 'a.', 'Having sore eyes; having the eyes dim with rheum;\n   dim-sighted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blear-eyed', 'a.', 'Lacking in perception or penetration; short-sighted;\n   as, a blear-eyed bigot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleareyedness', 'n.', 'The state of being blear-eyed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleary', 'a.', 'Somewhat blear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleated', 'imp. & p. p.', 'of Bleat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleating', 'p. pr. & vb. n.', 'of Bleat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleat', 'v. i.', 'To make the noise of, or one like that of, a sheep; to\n   cry like a sheep or calf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleat', 'n.', 'A plaintive cry of, or like that of, a sheep.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleater', 'n.', 'One who bleats; a sheep.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleating', 'a.', 'Crying as a sheep does.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleating', 'n.', 'The cry of, or as of, a sheep.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleb', 'n.', 'A large vesicle or bulla, usually containing a serous fluid;\n   a blister; a bubble, as in water, glass, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blebby', 'a.', 'Containing blebs, or characterized by blebs; as, blebby\n   glass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleck', 'v. t.', 'Alt. of Blek');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blek', 'v. t.', 'To blacken; also, to defile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bled', '', 'imp. & p. p. of Bleed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blee', 'n.', 'Complexion; color; hue; likeness; form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bled', 'imp. & p. p.', 'of Bleed');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleeding', 'p. pr. & vb. n.', 'of Bleed');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To emit blood; to lose blood; to run with blood, by\n   whatever means; as, the arm bleeds; the wound bled freely; to bleed at\n   the nose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To withdraw blood from the body; to let blood; as, Dr. A.\n   bleeds in fevers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To lose or shed ones blood, as in case of a violent\n   death or severe wounds; to die by violence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To issue forth, or drop, as blood from an incision.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To lose sap, gum, or juice; as, a tree or a vine bleeds\n   when tapped or wounded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. i.', 'To pay or lose money; to have money drawn or extorted;\n   as, to bleed freely for a cause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. t.', 'To let blood from; to take or draw blood from, as by\n   opening a vein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. t.', 'To lose, as blood; to emit or let drop, as sap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleed', 'v. t.', 'To draw money from (one); to induce to pay; as, they bled\n   him freely for this fund.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleeder', 'n.', 'One who, or that which, draws blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleeder', 'n.', 'One in whom slight wounds give rise to profuse or\n   uncontrollable bleeding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleeding', 'a.', 'Emitting, or appearing to emit, blood or sap, etc.; also,\n   expressing anguish or compassion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleeding', 'n.', 'A running or issuing of blood, as from the nose or a\n   wound; a hemorrhage; the operation of letting blood, as in surgery; a\n   drawing or running of sap from a tree or plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemished', 'imp. & p. p.', 'of Blemish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemishing', 'p. pr. & vb. n.', 'of Blemish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemish', 'v. t.', 'To mark with deformity; to injure or impair, as\n   anything which is well formed, or excellent; to mar, or make defective,\n   either the body or mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemish', 'v. t.', 'To tarnish, as reputation or character; to defame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemishes', 'pl. ', 'of Blemish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemish', 'n.', 'Any mark of deformity or injury, whether physical or\n   moral; anything that diminishes beauty, or renders imperfect that which\n   is otherwise well formed; that which impairs reputation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemishless', 'a.', 'Without blemish; spotless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blemishment', 'n.', 'The state of being blemished; blemish; disgrace;\n   damage; impairment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenched', 'imp. & p. p.', 'of Blench');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenching', 'p. pr. & vb. n.', 'of Blench');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'v. i.', 'To shrink; to start back; to draw back, from lack of\n   courage or resolution; to flinch; to quail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'v. i.', 'To fly off; to turn aside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'v. t.', 'To baffle; to disconcert; to turn away; -- also, to\n   obstruct; to hinder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'v. t.', 'To draw back from; to deny from fear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'n.', 'A looking aside or askance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench', 'v. i. & t.', 'To grow or make pale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blencher', 'n.', 'One who, or that which, scares another; specifically, a\n   person stationed to prevent the escape of the deer, at a hunt. See\n   Blancher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blencher', 'n.', 'One who blenches, flinches, or shrinks back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blench holding', '', 'See Blanch holding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blended', 'imp. & p. p.', 'of Blend');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blent', '', 'of Blend');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blending', 'p. pr. & vb. n.', 'of Blend');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blend', 'v. t.', 'To mix or mingle together; esp. to mingle, combine, or\n   associate so that the separate things mixed, or the line of\n   demarcation, can not be distinguished. Hence: To confuse; to confound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blend', 'v. t.', 'To pollute by mixture or association; to spoil or\n   corrupt; to blot; to stain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blend', 'v. i.', 'To mingle; to mix; to unite intimately; to pass or shade\n   insensibly into each other, as colors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blend', 'n.', 'A thorough mixture of one thing with another, as color,\n   tint, etc., into another, so that it cannot be known where one ends or\n   the other begins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blend', 'a.', 'To make blind, literally or figuratively; to dazzle; to\n   deceive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blende', 'n.', 'A mineral, called also sphalerite, and by miners mock lead,\n   false galena, and black-jack. It is a zinc sulphide, but often contains\n   some iron. Its color is usually yellow, brown, or black, and its luster\n   resinous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blende', 'n.', 'A general term for some minerals, chiefly metallic\n   sulphides which have a somewhat brilliant but nonmetallic luster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blender', 'n.', 'One who, or that which, blends; an instrument, as a brush,\n   used in blending.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blending', 'n.', 'The act of mingling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blending', 'n.', 'The method of laying on different tints so that they may\n   mingle together while wet, and shade into each other insensibly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blendous', 'a.', 'Pertaining to, consisting of, or containing, blende.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blendwater', 'n.', 'A distemper incident to cattle, in which their livers\n   are affected.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenheim spaniel', '', 'A small variety of spaniel, kept as a pet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenk', 'v. i.', 'To blink; to shine; to look.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blennioid', 'a.', 'Alt. of Blenniid');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenniid', 'a.', 'Of, pertaining to, or resembling, the blennies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blennogenous', 'a.', 'Generating mucus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blennorrhea', 'n.', 'An inordinate secretion and discharge of mucus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blennorrhea', 'n.', 'Gonorrhea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blennies', 'pl. ', 'of Blenny');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blenny', 'n.', 'A marine fish of the genus Blennius or family Blenniidae;\n   -- so called from its coating of mucus. The species are numerous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blent', 'imp. & p. p.', 'Mingled; mixed; blended; also, polluted; stained.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blent', 'imp. & p. p.', 'Blinded. Also (Chaucer); 3d sing. pres. Blindeth.');
