  INSERT INTO words (word, word_type, description)VALUES  (    'Bless', 'v. t.', 'To wave; to brandish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Hallowed; consecrated; worthy of blessing or adoration;\n   heavenly; holy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Enjoying happiness or bliss; favored with blessings;\n   happy; highly favored.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Imparting happiness or bliss; fraught with happiness;\n   blissful; joyful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Enjoying, or pertaining to, spiritual happiness, or\n   heavenly felicity; as, the blessed in heaven.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Beatified.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed', 'a.', 'Used euphemistically, ironically, or intensively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessedly', 'adv.', 'Happily; fortunately; joyfully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessedness', 'n.', 'The state of being blessed; happiness; felicity;\n   bliss; heavenly joys; the favor of God.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessed thistle', '', 'See under Thistle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blesser', 'n.', 'One who blesses; one who bestows or invokes a blessing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessing', 'v. t.', 'The act of one who blesses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessing', 'v. t.', 'A declaration of divine favor, or an invocation\n   imploring divine favor on some or something; a benediction; a wish of\n   happiness pronounces.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessing', 'v. t.', 'A means of happiness; that which promotes prosperity\n   and welfare; a beneficent gift.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessing', 'v. t.', 'A gift.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blessing', 'v. t.', 'Grateful praise or worship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blest', 'a.', 'Blessed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blet', 'n.', 'A form of decay in fruit which is overripe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bletonism', 'n.', 'The supposed faculty of perceiving subterraneous springs\n   and currents by sensation; -- so called from one Bleton, of France.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bletting', 'n.', 'A form of decay seen in fleshy, overripe fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blew', '', 'imp. of Blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleyme', 'n.', 'An inflammation in the foot of a horse, between the sole\n   and the bone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bleynte', '', 'imp. of Blench.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blickey', 'n.', 'A tin dinner pail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blighted', 'imp. & p. p.', 'of Blight');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blighting', 'p. pr. & vb. n.', 'of Blight');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'v. t.', 'To affect with blight; to blast; to prevent the growth\n   and fertility of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'v. t.', 'Hence: To destroy the happiness of; to ruin; to mar\n   essentially; to frustrate; as, to blight ones prospects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'v. i.', 'To be affected by blight; to blast; as, this vine never\n   blights.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'n.', 'Mildew; decay; anything nipping or blasting; -- applied as\n   a general name to various injuries or diseases of plants, causing the\n   whole or a part to wither, whether occasioned by insects, fungi, or\n   atmospheric influences.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'n.', 'The act of blighting, or the state of being blighted; a\n   withering or mildewing, or a stoppage of growth in the whole or a part\n   of a plant, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'n.', 'That which frustrates ones plans or withers ones hopes;\n   that which impairs or destroys.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'n.', 'A downy species of aphis, or plant louse, destructive to\n   fruit trees, infesting both the roots and branches; -- also applied to\n   several other injurious insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blight', 'n.', 'A rashlike eruption on the human skin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blighting', 'a.', 'Causing blight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blightingly', 'adv.', 'So as to cause blight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blimbi', 'n.', 'Alt. of Blimbing');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blimbing', 'n.', 'See Bilimbi, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blin', 'v. t. & i.', 'To stop; to cease; to desist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blin', 'n.', 'Cessation; end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Destitute of the sense of seeing, either by natural defect\n   or by deprivation; without sight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Not having the faculty of discernment; destitute of\n   intellectual light; unable or unwilling to understand or judge; as,\n   authors are blind to their own defects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Undiscerning; undiscriminating; inconsiderate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Having such a state or condition as a thing would have to a\n   person who is blind; not well marked or easily discernible; hidden;\n   unseen; concealed; as, a blind path; a blind ditch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Involved; intricate; not easily followed or traced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Having no openings for light or passage; as, a blind wall;\n   open only at one end; as, a blind alley; a blind gut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Unintelligible, or not easily intelligible; as, a blind\n   passage in a book; illegible; as, blind writing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'a.', 'Abortive; failing to produce flowers or fruit; as, blind\n   buds; blind flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinded', 'imp. & p. p.', 'of Blind');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinding', 'p. pr. & vb. n.', 'of Blind');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'v. t.', 'To make blind; to deprive of sight or discernment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'v. t.', 'To deprive partially of vision; to make vision difficult\n   for and painful to; to dazzle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'v. t.', 'To darken; to obscure to the eye or understanding; to\n   conceal; to deceive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'v. t.', 'To cover with a thin coating of sand and fine gravel; as\n   a road newly paved, in order that the joints between the stones may be\n   filled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'n.', 'Something to hinder sight or keep out light; a screen; a\n   cover; esp. a hinged screen or shutter for a window; a blinder for a\n   horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'n.', 'Something to mislead the eye or the understanding, or to\n   conceal some covert deed or design; a subterfuge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'n.', 'A blindage. See Blindage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'n.', 'A halting place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blind', 'n.', 'Alt. of Blinde');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinde', 'n.', 'See Blende.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindage', 'n.', 'A cover or protection for an advanced trench or approach,\n   formed of fascines and earth supported by a framework.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinder', 'n.', 'One who, or that which, blinds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinder', 'n.', 'One of the leather screens on a bridle, to hinder a horse\n   from seeing objects at the side; a blinker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindfish', 'n.', 'A small fish (Amblyopsis spelaeus) destitute of eyes,\n   found in the waters of the Mammoth Cave, in Kentucky. Related fishes\n   from other caves take the same name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindfolded', 'imp. & p. p.', 'of Blindfold');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindfolding', 'p. pr. & vb. n.', 'of Blindfold');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindfold', 'v. t.', 'To cover the eyes of, as with a bandage; to hinder\n   from seeing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindfold', 'a.', 'Having the eyes covered; blinded; having the mental eye\n   darkened. Hence: Heedless; reckless; as, blindfold zeal; blindfold\n   fury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinding', 'a.', 'Making blind or as if blind; depriving of sight or of\n   understanding; obscuring; as, blinding tears; blinding snow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinding', 'n.', 'A thin coating of sand and fine gravel over a newly paved\n   road. See Blind, v. t., 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindly', 'adv.', 'Without sight, discernment, or understanding; without\n   thought, investigation, knowledge, or purpose of ones own.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindmans buff', '', 'A play in which one person is blindfolded, and tries\n   to catch some one of the company and tell who it is.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindmans holiday', '', 'The time between daylight and candle light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindness', 'n.', 'State or condition of being blind, literally or\n   figuratively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindstory', 'n.', 'The triforium as opposed to the clearstory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blindworm', 'n.', 'A small, burrowing, snakelike, limbless lizard (Anguis\n   fragilis); with minute eyes, popularly believed to be blind; the\n   slowworm; -- formerly a name for the adder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinked', 'imp. & p. p.', 'of Blink');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinking', 'p. pr. & vb. n.', 'of Blink');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'To wink; to twinkle with, or as with, the eye.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'To see with the eyes half shut, or indistinctly and with\n   frequent winking, as a person with weak eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'To shine, esp. with intermittent light; to twinkle; to\n   flicker; to glimmer, as a lamp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'To turn slightly sour, as beer, mild, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. t.', 'To shut out of sight; to avoid, or purposely evade; to\n   shirk; as, to blink the question.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. t.', 'To trick; to deceive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'A glimpse or glance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'Gleam; glimmer; sparkle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'v. i.', 'The dazzling whiteness about the horizon caused by the\n   reflection of light from fields of ice at sea; ice blink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink', 'pl.', 'Boughs cast where deer are to pass, to turn or check them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinkard', 'n.', 'One who blinks with, or as with, weak eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinkard', 'n.', 'That which twinkles or glances, as a dim star, which\n   appears and disappears.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink beer', '', 'Beer kept unbroached until it is sharp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinker', 'n.', 'One who, or that which, blinks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinker', 'n.', 'A blinder for horses; a flap of leather on a horses\n   bridle to prevent him from seeing objects as his side hence, whatever\n   obstructs sight or discernment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blinker', 'pl.', 'A kind of goggles, used to protect the eyes form glare,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blink-eyed', 'a.', 'Habitually winking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blirt', 'n.', 'A gust of wind and rain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blisses', 'pl. ', 'of Bliss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bliss', 'n.', 'Orig., blithesomeness; gladness; now, the highest degree of\n   happiness; blessedness; exalted felicity; heavenly joy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blissful', 'a.', 'Full of, characterized by, or causing, joy and felicity;\n   happy in the highest degree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blissless', 'a.', 'Destitute of bliss.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blissom', 'v. i.', 'To be lustful; to be lascivious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blissom', 'a.', 'Lascivious; also, in heat; -- said of ewes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'n.', 'A vesicle of the skin, containing watery matter or serum,\n   whether occasioned by a burn or other injury, or by a vesicatory; a\n   collection of serous fluid causing a bladderlike elevation of the\n   cuticle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'n.', 'Any elevation made by the separation of the film or skin,\n   as on plants; or by the swelling of the substance at the surface, as on\n   steel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'n.', 'A vesicatory; a plaster of Spanish flies, or other matter,\n   applied to raise a blister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blistered', 'imp. & p. p.', 'of Blister');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blistering', 'p. pr. & vb. n.', 'of Blister');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'v. i.', 'To be affected with a blister or blisters; to have a\n   blister form on.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'v. t.', 'To raise a blister or blisters upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blister', 'v. t.', 'To give pain to, or to injure, as if by a blister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blistery', 'a.', 'Full of blisters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blite', 'n.', 'A genus of herbs (Blitum) with a fleshy calyx. Blitum\n   capitatum is the strawberry blite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blithe', 'a.', 'Gay; merry; sprightly; joyous; glad; cheerful; as, a blithe\n   spirit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blitheful', 'a.', 'Gay; full of gayety; joyous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blithely', 'adv.', 'In a blithe manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blitheness', 'n.', 'The state of being blithe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blithesome', 'a.', 'Cheery; gay; merry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blive', 'adv.', 'Quickly; forthwith.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blizzard', 'n.', 'A gale of piercingly cold wind, usually accompanied with\n   fine and blinding snow; a furious blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloated', 'imp. & p. p.', 'of Bloat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloating', 'p. pr. & vb. n.', 'of Bloat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'v. t.', 'To make turgid, as with water or air; to cause a swelling\n   of the surface of, from effusion of serum in the cellular tissue,\n   producing a morbid enlargement, often accompanied with softness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'v. t.', 'To inflate; to puff up; to make vain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'v. i.', 'To grow turgid as by effusion of liquid in the cellular\n   tissue; to puff out; to swell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'a.', 'Bloated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'n.', 'A term of contempt for a worthless, dissipated fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloat', 'v. t.', 'To dry (herrings) in smoke. See Blote.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloated', 'p. a.', 'Distended beyond the natural or usual size, as by the\n   presence of water, serum, etc.; turgid; swollen; as, a bloated face.\n   Also, puffed up with pride; pompous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloatedness', 'n.', 'The state of being bloated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloater', 'n.', 'The common herring, esp. when of large size, smoked, and\n   half dried; -- called also bloat herring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blob', 'n.', 'Something blunt and round; a small drop or lump of something\n   viscid or thick; a drop; a bubble; a blister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blob', 'n.', 'A small fresh-water fish (Uranidea Richardsoni); the millers\n   thumb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blobber', 'n.', 'A bubble; blubber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blobber-lipped', 'a.', 'Having thick lips.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocage', 'n.', 'The roughest and cheapest sort of rubblework, in masonry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A piece of wood more or less bulky; a solid mass of wood,\n   stone, etc., usually with one or more plane, or approximately plane,\n   faces; as, a block on which a butcher chops his meat; a block by which\n   to mount a horse; childrens playing blocks, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'The solid piece of wood on which condemned persons lay\n   their necks when they are beheaded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'The wooden mold on which hats, bonnets, etc., are shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'The pattern or shape of a hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A large or long building divided into separate houses or\n   shops, or a number of houses or shops built in contact with each other\n   so as to form one building; a row of houses or shops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A square, or portion of a city inclosed by streets,\n   whether occupied by buildings or not.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A grooved pulley or sheave incased in a frame or shell\n   which is provided with a hook, eye, or strap, by which it may be\n   attached to an object. It is used to change the direction of motion, as\n   in raising a heavy object that can not be conveniently reached, and\n   also, when two or more such sheaves are compounded, to change the rate\n   of motion, or to exert increased force; -- used especially in the\n   rigging of ships, and in tackles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'The perch on which a bird of prey is kept.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'Any obstruction, or cause of obstruction; a stop; a\n   hindrance; an obstacle; as, a block in the way.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A piece of box or other wood for engravers work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A piece of hard wood (as mahogany or cherry) on which a\n   stereotype or electrotype plate is mounted to make it type high.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A blockhead; a stupid fellow; a dolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'v. t.', 'A section of a railroad where the block system is used.\n   See Block system, below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocked', 'imp. & p. p.', 'of Block');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocking', 'p. pr. & vb. n.', 'of Block');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'n.', 'To obstruct so as to prevent passage or progress; to prevent\n   passage from, through, or into, by obstructing the way; -- used both of\n   persons and things; -- often followed by up; as, to block up a road or\n   harbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'n.', 'To secure or support by means of blocks; to secure, as two\n   boards at their angles of intersection, by pieces of wood glued to\n   each.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block', 'n.', 'To shape on, or stamp with, a block; as, to block a hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockade', 'v. t.', 'The shutting up of a place by troops or ships, with\n   the purpose of preventing ingress or egress, or the reception of\n   supplies; as, the blockade of the ports of an enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockade', 'v. t.', 'An obstruction to passage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockaded', 'imp. & p. p.', 'of Blockade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockading', 'p. pr. & vb. n.', 'of Blockade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockade', 'v. t. ', 'To shut up, as a town or fortress, by investing it\n   with troops or vessels or war for the purpose of preventing ingress or\n   egress, or the introduction of supplies. See note under Blockade, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockade', 'n.', 'Hence, to shut in so as to prevent egress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockade', 'n.', 'To obstruct entrance to or egress from.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockader', 'n.', 'One who blockades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockader', 'n.', 'A vessel employed in blockading.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockage', 'n.', 'The act of blocking up; the state of being blocked up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block book', '', 'A book printed from engraved wooden blocks instead of\n   movable types.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockhead', 'n.', 'A stupid fellow; a dolt; a person deficient in\n   understanding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockheaded', 'a.', 'Stupid; dull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockheadism', 'n.', 'That which characterizes a blockhead; stupidity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockhouse', 'n.', 'An edifice or structure of heavy timbers or logs for\n   military defense, having its sides loopholed for musketry, and often an\n   upper story projecting over the lower, or so placed upon it as to have\n   its sides make an angle wit the sides of the lower story, thus enabling\n   the defenders to fire downward, and in all directions; -- formerly much\n   used in America and Germany.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockhouse', 'n.', 'A house of squared logs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocking', 'n.', 'The act of obstructing, supporting, shaping, or stamping\n   with a block or blocks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocking', 'n.', 'Blocks used to support (a building, etc.) temporarily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocking course', '', 'The finishing course of a wall showing above a\n   cornice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blockish', 'a.', 'Like a block; deficient in understanding; stupid; dull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blocklike', 'a.', 'Like a block; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Block tin', '', 'See under Tin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloedite', 'n.', 'A hydrous sulphate of magnesium and sodium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blomary', 'n.', 'See Bloomery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloncket', 'a.', 'Alt. of Blonket');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blonket', 'a.', 'Gray; bluish gray.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blond', 'v. t.', 'Alt. of Blonde');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blonde', 'v. t.', 'Of a fair color; light-colored; as, blond hair; a blond\n   complexion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blonde', 'n.', 'A person of very fair complexion, with light hair and light\n   blue eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blonde', 'n.', 'A kind of silk lace originally of the color of raw silk,\n   now sometimes dyed; -- called also blond lace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blond metal', '', 'A variety of clay ironstone, in Staffordshire, England,\n   used for making tools.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blondness', 'n.', 'The state of being blond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'The fluid which circulates in the principal vascular system\n   of animals, carrying nourishment to all parts of the body, and bringing\n   away waste products to be excreted. See under Arterial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'Relationship by descent from a common ancestor;\n   consanguinity; kinship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'Descent; lineage; especially, honorable birth; the highest\n   royal lineage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'Descent from parents of recognized breed; excellence or\n   purity of breed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'The fleshy nature of man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'The shedding of blood; the taking of life, murder;\n   manslaughter; destruction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'A bloodthirsty or murderous disposition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'Temper of mind; disposition; state of the passions; -- as if\n   the blood were the seat of emotions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'A man of fire or spirit; a fiery spark; a gay, showy man; a\n   rake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'n.', 'The juice of anything, especially if red.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooded', 'imp. & p. p.', 'of Blood');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooding', 'p. pr. & vb. n.', 'of Blood');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'v. t.', 'To bleed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'v. t.', 'To stain, smear or wet, with blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'v. t.', 'To give (hounds or soldiers) a first taste or sight of\n   blood, as in hunting or war.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood', 'v. t.', 'To heat the blood of; to exasperate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodbird', 'n.', 'An Australian honeysucker (Myzomela sanguineolata); --\n   so called from the bright red color of the male bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood-boltered', 'a.', 'Having the hair matted with clotted blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooded', 'a.', 'Having pure blood, or a large admixture or pure blood; of\n   approved breed; of the best stock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodflower', 'n.', 'A genus of bulbous plants, natives of Southern Africa,\n   named Haemanthus, of the Amaryllis family. The juice of H. toxicarius\n   is used by the Hottentots to poison their arrows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodguilty', 'a.', 'Guilty of murder or bloodshed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodhound', 'n.', 'A breed of large and powerful dogs, with long, smooth,\n   and pendulous ears, and remarkable for acuteness of smell. It is\n   employed to recover game or prey which has escaped wounded from a\n   hunter, and for tracking criminals. Formerly it was used for pursuing\n   runaway slaves. Other varieties of dog are often used for the same\n   purpose and go by the same name. The Cuban bloodhound is said to be a\n   variety of the mastiff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodily', 'adv.', 'In a bloody manner; cruelly; with a disposition to shed\n   blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodiness', 'n.', 'The state of being bloody.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodiness', 'n.', 'Disposition to shed blood; bloodthirstiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodless', 'a.', 'Destitute of blood, or apparently so; as, bloodless\n   cheeks; lifeless; dead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodless', 'a.', 'Not attended with shedding of blood, or slaughter; as, a\n   bloodless victory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodless', 'a.', 'Without spirit or activity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodlet', 'v. t. ', 'bleed; to let blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodletter', 'n.', 'One who, or that which, lets blood; a phlebotomist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodletting', 'n.', 'The act or process of letting blood or bleeding, as\n   by opening a vein or artery, or by cupping or leeches; -- esp. applied\n   to venesection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood money', '', 'Money paid to the next of kin of a person who has been\n   killed by another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood money', '', 'Money obtained as the price, or at the cost, of\n   anothers life; -- said of a reward for supporting a capital charge, of\n   money obtained for betraying a fugitive or for committing murder, or of\n   money obtained from the sale of that which will destroy the purchaser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodroot', 'n.', 'A plant (Sanguinaria Canadensis); with a red root and\n   red sap, and bearing a pretty, white flower in early spring; -- called\n   also puccoon, redroot, bloodwort, tetterwort, turmeric, and Indian\n   paint. It has acrid emetic properties, and the rootstock is used as a\n   stimulant expectorant. See Sanguinaria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodshed', 'n.', 'The shedding or spilling of blood; slaughter; the act of\n   shedding human blood, or taking life, as in war, riot, or murder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodshedder', 'n.', 'One who sheds blood; a manslayer; a murderer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodshedding', 'n.', 'Bloodshed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodshot', 'a.', 'Red and inflamed; suffused with blood, or having the\n   vessels turgid with blood, as when the conjunctiva is inflamed or\n   irritated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood-shotten', 'a.', 'Bloodshot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodstick', 'n.', 'A piece of hard wood loaded at one end with lead, and\n   used to strike the fleam into the vein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodstone', 'n.', 'A green siliceous stone sprinkled with red jasper, as\n   if with blood; hence the name; -- called also heliotrope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodstone', 'n.', 'Hematite, an ore of iron yielding a blood red powder or\n   \"streak.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodstroke', 'n.', 'Loss of sensation and motion from hemorrhage or\n   congestion in the brain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodsucker', 'n.', 'Any animal that sucks blood; esp., the leech (Hirudo\n   medicinalis); and related species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodsucker', 'n.', 'One who sheds blood; a cruel, bloodthirsty man; one\n   guilty of bloodshed; a murderer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodsucker', 'n.', 'A hard and exacting master, landlord, or money lender;\n   an extortioner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodthirsty', 'a.', 'Eager to shed blood; cruel; sanguinary; murderous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodulf', 'n.', 'The European bullfinch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blood vessel', '', 'Any vessel or canal in which blood circulates in an\n   animal, as an artery or vein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodwite', 'n.', 'Alt. of Bloodwit');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodwit', 'n.', 'A fine or amercement paid as a composition for the\n   shedding of blood; also, a riot wherein blood was spilled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodwood', 'n.', 'A tree having the wood or the sap of the color of blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodwort', 'n.', 'A plant, Rumex sanguineus, or bloody-veined dock. The\n   name is applied also to bloodroot (Sanguinaria Canadensis); and to an\n   extensive order of plants (Haemodoraceae); the roots of many species of\n   which contain a red coloring matter useful in dyeing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'a.', 'Containing or resembling blood; of the nature of blood; as,\n   bloody excretions; bloody sweat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'a.', 'Smeared or stained with blood; as, bloody hands; a bloody\n   handkerchief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'a.', 'Given, or tending, to the shedding of blood; having a\n   cruel, savage disposition; murderous; cruel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'a.', 'Attended with, or involving, bloodshed; sanguinary; esp.,\n   marked by great slaughter or cruelty; as, a bloody battle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'a.', 'Infamous; contemptible; -- variously used for mere emphasis\n   or as a low epithet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodied', 'imp. & p. p.', 'of Bloody');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodying', 'p. pr. & vb. n.', 'of Bloody');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody', 'v. t.', 'To stain with blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloodybones', 'n.', 'A terrible bugbear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody flux', '', 'The dysentery, a disease in which the flux or discharge\n   from the bowels has a mixture of blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody hand', '', 'A hand stained with the blood of a deer, which, in the\n   old forest laws of England, was sufficient evidence of a mans trespass\n   in the forest against venison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody hand', '', 'A red hand, as in the arms of Ulster, which is now the\n   distinguishing mark of a baronet of the United Kingdom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody-minded', 'a.', 'Having a cruel, ferocious disposition; bloodthirsty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloody sweat', '', 'A sweat accompanied by a discharge of blood; a disease,\n   called sweating sickness, formerly prevalent in England and other\n   countries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A blossom; the flower of a plant; an expanded bud; flowers,\n   collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'The opening of flowers in general; the state of blossoming\n   or of having the flowers open; as, the cherry trees are in bloom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A state or time of beauty, freshness, and vigor; an opening\n   to higher perfection, analogous to that of buds into blossoms; as, the\n   bloom of youth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'The delicate, powdery coating upon certain growing or\n   newly-gathered fruits or leaves, as on grapes, plums, etc. Hence:\n   Anything giving an appearance of attractive freshness; a flush; a glow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'The clouded appearance which varnish sometimes takes upon\n   the surface of a picture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A yellowish deposit or powdery coating which appears on\n   well-tanned leather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A popular term for a bright-hued variety of some minerals;\n   as, the rose-red cobalt bloom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomed', 'imp. & p. p.', 'of Bloom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooming', 'p. pr. & vb. n.', 'of Bloom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'v. i.', 'To produce or yield blossoms; to blossom; to flower or be\n   in flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'v. i.', 'To be in a state of healthful, growing youth and vigor;\n   to show beauty and freshness, as of flowers; to give promise, as by or\n   with flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'v. t.', 'To cause to blossom; to make flourish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'v. t.', 'To bestow a bloom upon; to make blooming or radiant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A mass of wrought iron from the Catalan forge or from the\n   puddling furnace, deprived of its dross, and shaped usually in the form\n   of an oblong block by shingling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloom', 'n.', 'A large bar of steel formed directly from an ingot by\n   hammering or rolling, being a preliminary shape for further working.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomary', 'n.', 'See Bloomery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomer', 'n.', 'A costume for women, consisting of a short dress, with\n   loose trousers gathered round ankles, and (commonly) a broad-brimmed\n   hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomer', 'n.', 'A woman who wears a Bloomer costume.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomery', 'n.', 'A furnace and forge in which wrought iron in the form of\n   blooms is made directly from the ore, or (more rarely) from cast iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooming', 'n.', 'The process of making blooms from the ore or from cast\n   iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooming', 'a.', 'Opening in blossoms; flowering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooming', 'a.', 'Thriving in health, beauty, and vigor; indicating the\n   freshness and beauties of youth or health.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomingly', 'adv.', 'In a blooming manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomingness', 'n.', 'A blooming condition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomless', 'a.', 'Without bloom or flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomy', 'a.', 'Full of bloom; flowery; flourishing with the vigor of\n   youth; as, a bloomy spray.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloomy', 'a.', 'Covered with bloom, as fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blooth', 'n.', 'Bloom; a blossoming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blore', 'n.', 'The act of blowing; a roaring wind; a blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blosmy', 'a.', 'Blossomy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossom', 'n.', 'The flower of a plant, or the essential organs of\n   reproduction, with their appendages; florescence; bloom; the flowers of\n   a plant, collectively; as, the blossoms and fruit of a tree; an apple\n   tree in blossom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossom', 'n.', 'A blooming period or stage of development; something\n   lovely that gives rich promise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossom', 'n.', 'The color of a horse that has white hairs intermixed with\n   sorrel and bay hairs; -- otherwise called peach color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossomed', 'imp. & p. p.', 'of Blossom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossoming', 'p. pr. & vb. n.', 'of Blossom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossom', 'n.', 'To put forth blossoms or flowers; to bloom; to blow; to\n   flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossom', 'n.', 'To flourish and prosper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossomless', 'a.', 'Without blossoms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blossomy', 'a.', 'Full of blossoms; flowery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotted', 'imp. & p. p.', 'of Blot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotting', 'p. pr. & vb. n.', 'of Blot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To spot, stain, or bespatter, as with ink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To impair; to damage; to mar; to soil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To stain with infamy; to disgrace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To obliterate, as writing with ink; to cancel; to efface;\n   -- generally with out; as, to blot out a word or a sentence. Often\n   figuratively; as, to blot out offenses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To obscure; to eclipse; to shadow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. t.', 'To dry, as writing, with blotting paper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'v. i.', 'To take a blot; as, this paper blots easily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'A spot or stain, as of ink on paper; a blur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'An obliteration of something written or printed; an erasure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'A spot on reputation; a stain; a disgrace; a reproach; a\n   blemish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'An exposure of a single man to be taken up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'A single man left on a point, exposed to be taken up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blot', 'n.', 'A weak point; a failing; an exposed point or mark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotch', 'a.', 'A blot or spot, as of color or of ink; especially a large\n   or irregular spot. Also Fig.; as, a moral blotch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotch', 'a.', 'A large pustule, or a coarse eruption.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotched', 'a.', 'Marked or covered with blotches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotchy', 'a.', 'Having blotches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloted', 'imp. & p. p.', 'of Blote');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bloting', 'p. pr. & vb. n.', 'of Blote');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blote', 'v. t.', 'To cure, as herrings, by salting and smoking them; to\n   bloat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotless', 'a.', 'Without blot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotter', 'n.', 'One who, or that which, blots; esp. a device for absorbing\n   superfluous ink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotter', 'n.', 'A wastebook, in which entries of transactions are made as\n   they take place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blottesque', 'a.', 'Characterized by blots or heavy touches; coarsely\n   depicted; wanting in delineation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blotting paper', '', 'A kind of thick, bibulous, unsized paper, used to\n   absorb superfluous ink from freshly written manuscript, and thus\n   prevent blots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blouse', 'n.', 'A light, loose over-garment, like a smock frock, worn\n   especially by workingmen in France; also, a loose coat of any material,\n   as the undress uniform coat of the United States army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blew', 'imp.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowing', 'p. pr. & vb. n.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To flower; to blossom; to bloom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To cause to blossom; to put forth (blossoms or flowers).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'A blossom; a flower; also, a state of blossoming; a mass of\n   blossoms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'A forcible stroke with the hand, fist, or some instrument, as\n   a rod, a club, an ax, or a sword.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'A sudden or forcible act or effort; an assault.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'The infliction of evil; a sudden calamity; something which\n   produces mental, physical, or financial suffering or loss (esp. when\n   sudden); a buffet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blew', 'imp.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowing', 'p. pr. & vb. n.', 'of Blow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To produce a current of air; to move, as air, esp. to move\n   rapidly or with power; as, the wind blows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To send forth a forcible current of air, as from the mouth\n   or from a pair of bellows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To breathe hard or quick; to pant; to puff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To sound on being blown into, as a trumpet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To spout water, etc., from the blowholes, as a whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To be carried or moved by the wind; as, the dust blows in\n   from the street.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. i.', 'To talk loudly; to boast; to storm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To force a current of air upon with the mouth, or by other\n   means; as, to blow the fire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To drive by a current air; to impel; as, the tempest blew\n   the ship ashore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To cause air to pass through by the action of the mouth,\n   or otherwise; to cause to sound, as a wind instrument; as, to blow a\n   trumpet; to blow an organ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To clear of contents by forcing air through; as, to blow\n   an egg; to blow ones nose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To burst, shatter, or destroy by an explosion; -- usually\n   with up, down, open, or similar adverb; as, to blow up a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To spread by report; to publish; to disclose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To form by inflation; to swell by injecting air; as, to\n   blow bubbles; to blow glass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To inflate, as with pride; to puff up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To put out of breath; to cause to blow from fatigue; as,\n   to blow a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'v. t.', 'To deposit eggs or larvae upon, or in (meat, etc.).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'A blowing, esp., a violent blowing of the wind; a gale; as, a\n   heavy blow came on, and the ship put back to port.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'The act of forcing air from the mouth, or through or from\n   some instrument; as, to give a hard blow on a whistle or horn; to give\n   the fire a blow with the bellows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'The spouting of a whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'A single heat or operation of the Bessemer converter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow', 'n.', 'An egg, or a larva, deposited by a fly on or in flesh, or the\n   act of depositing it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowball', 'n.', 'The downy seed head of a dandelion, which children\n   delight to blow away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowen', 'n.', 'Alt. of Blowess');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowess', 'n.', 'A prostitute; a courtesan; a strumpet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'One who, or that which, blows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'A device for producing a current of air; as: (a) A metal\n   plate temporarily placed before the upper part of a grate or open fire.\n   (b) A machine for producing an artificial blast or current of air by\n   pressure, as for increasing the draft of a furnace, ventilating a\n   building or shaft, cleansing gram, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'A blowing out or excessive discharge of gas from a hole or\n   fissure in a mine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'The whale; -- so called by seamen, from the circumstance of\n   its spouting up a column of water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'A small fish of the Atlantic coast (Tetrodon turgidus); the\n   puffer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blower', 'n.', 'A braggart, or loud talker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowfly', 'n.', 'Any species of fly of the genus Musca that deposits its\n   eggs or young larvae (called flyblows and maggots) upon meat or other\n   animal products.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowgun', 'n.', 'A tube, as of cane or reed, sometimes twelve feet long,\n   through which an arrow or other projectile may be impelled by the force\n   of the breath. It is a weapon much used by certain Indians of America\n   and the West Indies; -- called also blowpipe, and blowtube. See\n   Sumpitan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowhole', 'n.', 'A cavern in a cliff, at the water level, opening to the\n   air at its farther extremity, so that the waters rush in with each\n   surge and rise in a lofty jet from the extremity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowhole', 'n.', 'A nostril or spiracle in the top of the head of a whale\n   or other cetacean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowhole', 'n.', 'A hole in the ice to which whales, seals, etc., come to\n   breathe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowhole', 'n.', 'An air hole in a casting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p. & a.', 'Swollen; inflated; distended; puffed up, as cattle\n   when gorged with green food which develops gas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p. & a.', 'Stale; worthless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p. & a.', 'Out of breath; tired; exhausted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p. & a.', 'Covered with the eggs and larvae of flies; fly\n   blown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blown', 'p. p. & a.', 'Opened; in blossom or having blossomed, as a flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow-off', 'n.', 'A blowing off steam, water, etc.;');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow-off', 'adj.', 'as, a blow-off cock or pipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow-off', 'adj.', 'An outburst of temper or excitement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow-out', 'n.', 'The cleaning of the flues of a boiler from scale, etc.,\n   by a blast of steam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowpipe', 'n.', 'A tube for directing a jet of air into a fire or into the\n   flame of a lamp or candle, so as to concentrate the heat on some\n   object.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowpipe', 'n.', 'A blowgun; a blowtube.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowpoint', 'n.', 'A childs game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowse', 'n.', 'See Blowze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowth', 'n.', 'A blossoming; a bloom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowtube', 'n.', 'A blowgun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowtube', 'n.', 'A similar instrument, commonly of tin, used by boys for\n   discharging paper wads and other light missiles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowtube', 'n.', 'A long wrought iron tube, on the end of which the workman\n   gathers a quantity of \"metal\" (melted glass); and through which he\n   blows to expand or shape it; -- called also blowing tube, and blowpipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blow valve', '', 'See Snifting valve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowy', 'a.', 'Windy; as, blowy weather; a blowy upland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowze', 'n.', 'A ruddy, fat-faced woman; a wench.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowzed', 'a.', 'Having high color from exposure to the weather;\n   ruddy-faced; blowzy; disordered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blowzy', 'a.', 'Coarse and ruddy-faced; fat and ruddy; high colored;\n   frowzy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blub', 'v. t. & i.', 'To swell; to puff out, as with weeping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'n.', 'A bubble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'n.', 'The fat of whales and other large sea animals from which\n   oil is obtained. It lies immediately under the skin and over the\n   muscular flesh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'n.', 'A large sea nettle or medusa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbered', 'imp. & p. p.', 'of Blubber');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbering', 'p. pr. & vb. n.', 'of Blubber');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'v. i.', 'To weep noisily, or so as to disfigure the face; to cry\n   in a childish manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'v. t.', 'To swell or disfigure (the face) with weeping; to wet\n   with tears.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubber', 'v. t.', 'To give vent to (tears) or utter (broken words or\n   cries); -- with forth or out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbered', 'p. p. & a.', 'Swollen; turgid; as, a blubbered lip.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbering', 'n.', 'The act of weeping noisily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbery', 'a.', 'Swollen; protuberant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blubbery', 'a.', 'Like blubber; gelatinous and quivering; as, a blubbery\n   mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blucher', 'n.', 'A kind of half boot, named from the Prussian general\n   Blucher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bludgeon', 'n.', 'A short stick, with one end loaded, or thicker and\n   heavier that the other, used as an offensive weapon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Having the color of the clear sky, or a hue resembling\n   it, whether lighter or darker; as, the deep, blue sea; as blue as a\n   sapphire; blue violets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Pale, without redness or glare, -- said of a flame;\n   hence, of the color of burning brimstone, betokening the presence of\n   ghosts or devils; as, the candle burns blue; the air was blue with\n   oaths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Low in spirits; melancholy; as, to feel blue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Suited to produce low spirits; gloomy in prospect; as,\n   thongs looked blue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Severe or over strict in morals; gloom; as, blue and\n   sour religionists; suiting one who is over strict in morals;\n   inculcating an impracticable, severe, or gloomy mortality; as, blue\n   laws.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'superl.', 'Literary; -- applied to women; -- an abbreviation of\n   bluestocking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'n.', 'One of the seven colors into which the rays of light divide\n   themselves, when refracted through a glass prism; the color of the\n   clear sky, or a color resembling that, whether lighter or darker; a\n   pigment having such color. Sometimes, poetically, the sky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'n.', 'A pedantic woman; a bluestocking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'pl.', 'Low spirits; a fit of despondency; melancholy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blued', 'imp. & p. p.', 'of Blue');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluing', 'p. pr. & vb. n.', 'of Blue');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue', 'v. t.', 'To make blue; to dye of a blue color; to make blue by\n   heating, as metals, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueback', 'n.', 'A trout (Salmo oquassa) inhabiting some of the lakes of\n   Maine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueback', 'n.', 'A salmon (Oncorhynchus nerka) of the Columbia River and\n   northward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueback', 'n.', 'An American river herring (Clupea aestivalis); closely\n   allied to the alewife.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebeard', 'n.', 'The hero of a mediaeval French nursery legend, who,\n   leaving home, enjoined his young wife not to open a certain room in his\n   castle. She entered it, and found the murdered bodies of his former\n   wives. -- Also used adjectively of a subject which it is forbidden to\n   investigate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebell', 'n.', 'A plant of the genus Campanula, especially the Campanula\n   rotundifolia, which bears blue bell-shaped flowers; the harebell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebell', 'n.', 'A plant of the genus Scilla (Scilla nutans).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueberry', 'n.', 'The berry of several species of Vaccinium, an ericaceous\n   genus, differing from the American huckleberries in containing numerous\n   minute seeds instead of ten nutlets. The commonest species are V.\n   Pennsylvanicum and V. vacillans. V. corymbosum is the tall blueberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebill', 'n.', 'A duck of the genus Fuligula. Two American species (F.\n   marila and F. affinis) are common. See Scaup duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebird', 'n.', 'A small song bird (Sialia sialis); very common in the\n   United States, and, in the north, one of the earliest to arrive in\n   spring. The male is blue, with the breast reddish. It is related to the\n   European robin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue bonnet', 'n.', 'Alt. of Blue-bonnet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-bonnet', 'n.', 'A broad, flat Scottish cap of blue woolen, or one\n   wearing such cap; a Scotchman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-bonnet', 'n.', 'A plant. Same as Bluebottle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-bonnet', 'n.', 'The European blue titmouse (Parus coeruleus); the\n   bluecap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue book', '', 'A parliamentary publication, so called from its blue paper\n   covers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue book', '', 'The United States official \"Biennial Register.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebottle', 'n.', 'A plant (Centaurea cyanus) which grows in grain fields.\n   It receives its name from its blue bottle-shaped flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebottle', 'n.', 'A large and troublesome species of blowfly (Musca\n   vomitoria). Its body is steel blue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluebreast', 'n.', 'A small European bird; the blue-throated warbler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluecap', 'n.', 'The bluepoll.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluecap', 'n.', 'The blue bonnet or blue titmouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluecap', 'n.', 'A Scot; a Scotchman; -- so named from wearing a blue\n   bonnet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluecoat', 'n.', 'One dressed in blue, as a soldier, a sailor, a beadle,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-eye', 'n.', 'The blue-cheeked honeysucker of Australia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-eyed', 'a.', 'Having blue eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-eyed grass', '', 'a grasslike plant (Sisyrinchium anceps); with small\n   flowers of a delicate blue color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluefin', 'n.', 'A species of whitefish (Coregonus nigripinnis) found in\n   Lake Michigan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluefish', 'n.', 'A large voracious fish (Pomatomus saitatrix); of the\n   family Carangidae, valued as a food fish, and widely distributed on the\n   American coast. On the New Jersey and Rhode Island coast it is called\n   the horse mackerel, in Virginia saltwater tailor, or skipjack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluefish', 'n.', 'A West Indian fish (Platyglossus radiatus); of the family\n   Labridae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluegown', 'n.', 'One of a class of paupers or pensioners, or licensed\n   beggars, in Scotland, to whim annually on the kings birthday were\n   distributed certain alms, including a blue gown; a beadsman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue grass', '', 'A species of grass (Poa compressa) with bluish green\n   stems, valuable in thin gravelly soils; wire grass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue jay', '', 'The common jay of the United States (Cyanocitta, or\n   Cyanura, cristata). The predominant color is bright blue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-john', 'n.', 'A name given to fluor spar in Derbyshire, where it is\n   used for ornamental purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluely', 'adv.', 'With a blue color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueness', 'n.', 'The quality of being blue; a blue color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluenose', 'n.', 'A nickname for a Nova Scotian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluepoll', 'n.', 'A kind of salmon (Salmo Cambricus) found in Wales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blueprint', '', 'See under Print.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluestocking', 'n.', 'A literary lady; a female pedant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluestocking', 'n.', 'The American avocet (Recurvirostra Americana).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluestockingism', 'n.', 'The character or manner of a bluestocking; female\n   pedantry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluestone', 'n.', 'Blue vitriol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluestone', 'n.', 'A grayish blue building stone, as that commonly used in\n   the eastern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluethroat', 'n.', 'A singing bird of northern Europe and Asia (Cyanecula\n   Suecica); related to the nightingales; -- called also blue-throated\n   robin and blue-throated warbler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluets', 'a.', 'A name given to several different species of plants having\n   blue flowers, as the Houstonia coerulea, the Centaurea cyanus or\n   bluebottle, and the Vaccinium angustifolium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blue-veined', 'a.', 'Having blue veins or blue streaks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluewing', 'n.', 'The blue-winged teal. See Teal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluey', 'a.', 'Bluish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'a.', 'Having a broad, flattened front; as, the bluff bows of a\n   ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'a.', 'Rising steeply with a flat or rounded front.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'a.', 'Surly; churlish; gruff; rough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'a.', 'Abrupt; roughly frank; unceremonious; blunt; brusque; as, a\n   bluff answer; a bluff manner of talking; a bluff sea captain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'n.', 'A high, steep bank, as by a river or the sea, or beside a\n   ravine or plain; a cliff with a broad face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'n.', 'An act of bluffing; an expression of self-confidence for the\n   purpose of intimidation; braggadocio; as, that is only bluff, or a\n   bluff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'n.', 'A game at cards; poker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffed', 'imp. & p. p.', 'of Bluff');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffing', 'p. pr. & vb. n.', 'of Bluff');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'v. t.', 'To deter (an opponent) from taking the risk of betting on\n   his hand of cards, as the bluffer does by betting heavily on his own\n   hand although it may be of less value.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'v. t.', 'To frighten or deter from accomplishing a purpose by\n   making a show of confidence in ones strength or resources; as, he\n   bluffed me off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff', 'v. i.', 'To act as in the game of bluff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff-bowed', 'a.', 'Built with the stem nearly straight up and down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffer', 'n.', 'One who bluffs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluff-headed', 'a.', 'Built with the stem nearly straight up and down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffness', 'n.', 'The quality or state of being bluff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffy', 'a.', 'Having bluffs, or bold, steep banks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluffy', 'a.', 'Inclined to bo bluff; brusque.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluing', 'n.', 'The act of rendering blue; as, the bluing of steel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluing', 'n.', 'Something to give a bluish tint, as indigo, or preparations\n   used by washerwomen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluish', 'a.', 'Somewhat blue; as, bluish veins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blundered', 'imp. & p. p.', 'of Blunder');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blundering', 'p. pr. & vb. n.', 'of Blunder');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'v. i.', 'To make a gross error or mistake; as, to blunder in\n   writing or preparing a medical prescription.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'v. i.', 'To move in an awkward, clumsy manner; to flounder and\n   stumble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'v. t.', 'To cause to blunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'v. t.', 'To do or treat in a blundering manner; to confuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'n.', 'Confusion; disturbance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunder', 'n.', 'A gross error or mistake, resulting from carelessness,\n   stupidity, or culpable ignorance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunderbuss', 'n.', 'A short gun or firearm, with a large bore, capable of\n   holding a number of balls, and intended to do execution without exact\n   aim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunderbuss', 'n.', 'A stupid, blundering fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunderer', 'n.', 'One who is apt to blunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunderhead', 'n.', 'A stupid, blundering fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blundering', 'a.', 'Characterized by blunders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunderingly', 'adv.', 'In a blundering manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunge', 'v. t.', 'To amalgamate and blend; to beat up or mix in water, as\n   clay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunger', 'n.', 'A wooden blade with a cross handle, used for mi/ing the\n   clay in potteries; a plunger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunging', 'n.', 'The process of mixing clay in potteries with a blunger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'a.', 'Having a thick edge or point, as an instrument; dull; not\n   sharp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'a.', 'Dull in understanding; slow of discernment; stupid; --\n   opposed to acute.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'a.', 'Abrupt in address; plain; unceremonious; wanting the forms\n   of civility; rough in manners or speech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'a.', 'Hard to impress or penetrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunted', 'imp. & p. p.', 'of Blunt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunting', 'p. pr. & vb. n.', 'of Blunt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'v. t.', 'To dull the edge or point of, by making it thicker; to\n   make blunt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'v. t.', 'To repress or weaken, as any appetite, desire, or power\n   of the mind; to impair the force, keenness, or susceptibility, of; as,\n   to blunt the feelings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'n.', 'A fencers foil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'n.', 'A short needle with a strong point. See Needle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt', 'n.', 'Money.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluntish', 'a.', 'Somewhat blunt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluntly', 'adv.', 'In a blunt manner; coarsely; plainly; abruptly; without\n   delicacy, or the usual forms of civility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluntness', 'n.', 'Want of edge or point; dullness; obtuseness; want of\n   sharpness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluntness', 'n.', 'Abruptness of address; rude plainness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blunt-witted', 'n.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurred', 'imp. & p. p.', 'of Blur');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurring', 'p. pr. & vb. n.', 'of Blur');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'v. t.', 'To render obscure by making the form or outline of\n   confused and uncertain, as by soiling; to smear; to make indistinct and\n   confused; as, to blur manuscript by handling it while damp; to blur the\n   impression of a woodcut by an excess of ink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'v. t.', 'To cause imperfection of vision in; to dim; to darken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'v. t.', 'To sully; to stain; to blemish, as reputation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'n.', 'That which obscures without effacing; a stain; a blot, as\n   upon paper or other substance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'n.', 'A dim, confused appearance; indistinctness of vision; as, to\n   see things with a blur; it was all blur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blur', 'n.', 'A moral stain or blot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurry', 'a.', 'Full of blurs; blurred.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurted', 'imp. & p. p.', 'of Blurt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurting', 'p. pr. & vb. n.', 'of Blurt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blurt', 'v. t.', 'To utter suddenly and unadvisedly; to divulge\n   inconsiderately; to ejaculate; -- commonly with out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushed', 'imp. & p. p.', 'of Blush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushing', 'p. pr. & vb. n.', 'of Blush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'v. i.', 'To become suffused with red in the cheeks, as from a\n   sense of shame, modesty, or confusion; to become red from such cause,\n   as the cheeks or face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'v. i.', 'To grow red; to have a red or rosy color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'v. i.', 'To have a warm and delicate color, as some roses and\n   other flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'v. t.', 'To suffuse with a blush; to redden; to make roseate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'v. t.', 'To express or make known by blushing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'n.', 'A suffusion of the cheeks or face with red, as from a sense\n   of shame, confusion, or modesty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blush', 'n.', 'A red or reddish color; a rosy tint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blusher', 'n.', 'One that blushes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushet', 'n.', 'A modest girl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushful', 'a.', 'Full of blushes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushing', 'a.', 'Showing blushes; rosy red; having a warm and delicate\n   color like some roses and other flowers; blooming; ruddy; roseate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushing', 'n.', 'The act of turning red; the appearance of a reddish color\n   or flush upon the cheeks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushingly', 'adv.', 'In a blushing manner; with a blush or blushes; as, to\n   answer or confess blushingly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushless', 'a.', 'Free from blushes; incapable of blushing; shameless;\n   impudent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blushy', 'a.', 'Like a blush; having the color of a blush; rosy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blustered', 'imp. & p. p.', 'of Bluster');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blustering', 'p. pr. & vb. n.', 'of Bluster');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluster', 'v. i.', 'To blow fitfully with violence and noise, as wind; to\n   be windy and boisterous, as the weather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluster', 'v. i.', 'To talk with noisy violence; to swagger, as a turbulent\n   or boasting person; to act in a noisy, tumultuous way; to play the\n   bully; to storm; to rage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluster', 'v. t.', 'To utter, or do, with noisy violence; to force by\n   blustering; to bully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluster', 'n.', 'Fitful noise and violence, as of a storm; violent winds;\n   boisterousness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bluster', 'n.', 'Noisy and violent or threatening talk; noisy and boastful\n   language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blusterer', 'n.', 'One who, or that which, blusters; a noisy swaggerer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blustering', 'a.', 'Exhibiting noisy violence, as the wind; stormy;\n   tumultuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blustering', 'a.', 'Uttering noisy threats; noisy and swaggering;\n   boisterous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blusteringly', 'adv.', 'In a blustering manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blusterous', 'a.', 'Inclined to bluster; given to blustering; blustering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Blustrous', 'a.', 'Blusterous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bo', 'interj.', 'An exclamation used to startle or frighten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boas', 'pl. ', 'of Boa');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boa', 'n.', 'A genus of large American serpents, including the boa\n   constrictor, the emperor boa of Mexico (B. imperator); and the\n   chevalier boa of Peru (B. eques).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boa', 'n.', 'A long, round fur tippet; -- so called from its resemblance in\n   shape to the boa constrictor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boa constrictor', '', 'A large and powerful serpent of tropical America,\n   sometimes twenty or thirty feet long. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boanerges', '', 'Any declamatory and vociferous preacher or orator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boar', 'n.', 'The uncastrated male of swine; specifically, the wild hog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'A piece of timber sawed thin, and of considerable length and\n   breadth as compared with the thickness, -- used for building, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'A table to put food upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'Hence: What is served on a table as food; stated meals;\n   provision; entertainment; -- usually as furnished for pay; as, to work\n   for ones board; the price of board.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'A table at which a council or court is held. Hence: A\n   council, convened for business, or any authorized assembly or meeting,\n   public or private; a number of persons appointed or elected to sit in\n   council for the management or direction of some public or private\n   business or trust; as, the Board of Admiralty; a board of trade; a\n   board of directors, trustees, commissioners, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'A square or oblong piece of thin wood or other material used\n   for some special purpose, as, a molding board; a board or surface\n   painted or arranged for a game; as, a chessboard; a backgammon board.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'Paper made thick and stiff like a board, for book covers,\n   etc.; pasteboard; as, to bind a book in boards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'The stage in a theater; as, to go upon the boards, to enter\n   upon the theatrical profession.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'The border or side of anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'The side of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'The stretch which a ship makes in one tack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarded', 'imp. & p. p.', 'of Board');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarding', 'p. pr. & vb. n.', 'of Board');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'v. t.', 'To cover with boards or boarding; as, to board a house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'To go on board of, or enter, as a ship, whether in a hostile\n   or a friendly way.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'To enter, as a railway car.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'To furnish with regular meals, or with meals and lodgings,\n   for compensation; to supply with daily meals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'n.', 'To place at board, for compensation; as, to board ones\n   horse at a livery stable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'v. i.', 'To obtain meals, or meals and lodgings, statedly for\n   compensation; as, he boards at the hotel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Board', 'v. t.', 'To approach; to accost; to address; hence, to woo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boardable', 'a.', 'That can be boarded, as a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarder', 'n.', 'One who has food statedly at anothers table, or meals and\n   lodgings in his house, for pay, or compensation of any kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarder', 'n.', 'One who boards a ship; one selected to board an enemys\n   ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarding', 'n.', 'The act of entering a ship, whether with a hostile or a\n   friendly purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarding', 'n.', 'The act of covering with boards; also, boards,\n   collectively; or a covering made of boards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarding', 'n.', 'The act of supplying, or the state of being supplied,\n   with regular or specified meals, or with meals and lodgings, for pay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarfish', 'n.', 'A Mediterranean fish (Capros aper); of the family\n   Caproidae; -- so called from the resemblance of the extended lips to a\n   hogs snout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarfish', 'n.', 'An Australian percoid fish (Histiopterus recurvirostris);\n   valued as a food fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boarish', 'a.', 'Swinish; brutal; cruel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boasted', 'imp. & p. p.', 'of Boast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boasting', 'p. pr. & vb. n.', 'of Boast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. i.', 'To vaunt ones self; to brag; to say or tell things which\n   are intended to give others a high opinion of ones self or of things\n   belonging to ones self; as, to boast of ones exploits courage,\n   descent, wealth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. i.', 'To speak in exulting language of another; to glory; to\n   exult.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. t.', 'To display in ostentatious language; to speak of with\n   pride, vanity, or exultation, with a view to self-commendation; to\n   extol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. t.', 'To display vaingloriously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. t.', 'To possess or have; as, to boast a name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. t.', 'To dress, as a stone, with a broad chisel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'v. t.', 'To shape roughly as a preparation for the finer work to\n   follow; to cut to the general form required.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'n.', 'Act of boasting; vaunting or bragging.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boast', 'n.', 'The cause of boasting; occasion of pride or exultation, --\n   sometimes of laudable pride or exultation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boastance', 'n.', 'Boasting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boaster', 'n.', 'One who boasts; a braggart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boaster', 'n.', 'A stone masons broad-faced chisel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boastful', 'a.', 'Given to, or full of, boasting; inclined to boast;\n   vaunting; vainglorious; self-praising.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boasting', 'n.', 'The act of glorying or vaunting; vainglorious speaking;\n   ostentatious display.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boastingly', 'adv.', 'Boastfully; with boasting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boastive', 'a.', 'Presumptuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boastless', 'a.', 'Without boasting or ostentation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'n.', 'A small open vessel, or water craft, usually moved by cars or\n   paddles, but often by a sail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'n.', 'Hence, any vessel; usually with some epithet descriptive of\n   its use or mode of propulsion; as, pilot boat, packet boat, passage\n   boat, advice boat, etc. The term is sometimes applied to steam vessels,\n   even of the largest class; as, the Cunard boats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'n.', 'A vehicle, utensil, or dish, somewhat resembling a boat in\n   shape; as, a stone boat; a gravy boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boated', 'imp. & p. p.', 'of Boat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boating', 'p. pr. & vb. n.', 'of Boat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'v. t.', 'To transport in a boat; as, to boat goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'v. t.', 'To place in a boat; as, to boat oars.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat', 'v. i.', 'To go or row in a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatable', 'a.', 'Such as can be transported in a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatable', 'a.', 'Navigable for boats, or small river craft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatage', 'n.', 'Conveyance by boat; also, a charge for such conveyance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatbill', 'n.', 'A wading bird (Cancroma cochlearia) of the tropical parts\n   of South America. Its bill is somewhat like a boat with the keel\n   uppermost.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatbill', 'n.', 'A perching bird of India, of the genus Eurylaimus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat bug', '', 'An aquatic hemipterous insect of the genus Notonecta; -- so\n   called from swimming on its back, which gives it the appearance of a\n   little boat. Called also boat fly, boat insect, boatman, and water\n   boatman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatfuls', 'pl. ', 'of Boatful');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatful', 'n.', 'The quantity or amount that fills a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boathouse', 'n.', 'A house for sheltering boats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boating', 'n.', 'The act or practice of rowing or sailing, esp. as an\n   amusement; carriage in boats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boating', 'n.', 'In Persia, a punishment of capital offenders, by laying\n   them on the back in a covered boat, where they are left to perish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boation', 'n.', 'A crying out; a roaring; a bellowing; reverberation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatmen', 'pl. ', 'of Boatman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatman', 'n.', 'A man who manages a boat; a rower of a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatman', 'n.', 'A boat bug. See Boat bug.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatmanship', 'n.', 'The art of managing a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat-shaped', 'a.', 'See Cymbiform.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat shell', '', 'A marine gastropod of the genus Crepidula. The species\n   are numerous. It is so named from its form and interior deck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat shell', '', 'A marine univalve shell of the genus Cymba.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatsman', 'n.', 'A boatman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatswain', 'n.', 'An officer who has charge of the boats, sails, rigging,\n   colors, anchors, cables, cordage, etc., of a ship, and who also summons\n   the crew, and performs other duties.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatswain', 'n.', 'The jager gull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatswain', 'n.', 'The tropic bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boat-tail', 'n.', 'A large grackle or blackbird (Quiscalus major); found in\n   the Southern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatwomen', 'pl. ', 'of Boatwoman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boatwoman', 'n.', 'A woman who manages a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'Anything that hangs so as to play loosely, or with a short\n   abrupt motion, as at the end of a string; a pendant; as, the bob at the\n   end of a kites tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A knot of worms, or of rags, on a string, used in angling, as\n   for eels; formerly, a worm suitable for bait.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A small piece of cork or light wood attached to a fishing line\n   to show when a fish is biting; a float.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'The ball or heavy part of a pendulum; also, the ball or weight\n   at the end of a plumb line.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A small wheel, made of leather, with rounded edges, used in\n   polishing spoons, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A short, jerking motion; act of bobbing; as, a bob of the\n   head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A working beam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A knot or short curl of hair; also, a bob wig.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A peculiar mode of ringing changes on bells.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'The refrain of a song.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A blow; a shake or jog; a rap, as with the fist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A jeer or flout; a sharp jest or taunt; a trick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'A shilling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbed', 'imp. & p. p.', 'of Bob');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbing', 'p. pr. & vb. n.', 'of Bob');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'To cause to move in a short, jerking manner; to move (a thing)\n   with a bob.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'To strike with a quick, light blow; to tap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'To cheat; to gain by fraud or cheating; to filch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'To mock or delude; to cheat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'n.', 'To cut short; as, to bob the hair, or a horses tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'v. i.', 'To have a short, jerking motion; to play to and fro, or up\n   and down; to play loosely against anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob', 'v. i.', 'To angle with a bob. See Bob, n., 2 & 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobac', 'n.', 'The Poland marmot (Arctomys bobac).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobance', 'n.', 'A boasting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobber', 'n.', 'One who, or that which, bobs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbery', 'n.', 'A squabble; a tumult; a noisy disturbance; as, to raise a\n   bobbery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbin', 'n.', 'A small pin, or cylinder, formerly of bone, now most\n   commonly of wood, used in the making of pillow lace. Each thread is\n   wound on a separate bobbin which hangs down holding the thread at a\n   slight tension.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbin', 'n.', 'A spool or reel of various material and construction, with\n   a head at one or both ends, and sometimes with a hole bored through its\n   length by which it may be placed on a spindle or pivot. It is used to\n   hold yarn or thread, as in spinning or warping machines, looms, sewing\n   machines, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbin', 'n.', 'The little rounded piece of wood, at the end of a latch\n   string, which is pulled to raise the latch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbin', 'n.', 'A fine cord or narrow braid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbin', 'n.', 'A cylindrical or spool-shaped coil or insulated wire,\n   usually containing a core of soft iron which becomes magnetic when the\n   wire is traversed by an electrical current.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbinet', 'n.', 'A kind of cotton lace which is wrought by machines, and\n   not by hand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbinwork', 'n.', 'Work woven with bobbins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobbish', 'a.', 'Hearty; in good spirits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobby', 'n.', 'A nickname for a policeman; -- from Sir Robert Peel, who\n   remodeled the police force. See Peeler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob-cherry', 'n.', 'A play among children, in which a cherry, hung so as to\n   bob against the mouth, is to be caught with the teeth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobfly', 'n.', 'The fly at the end of the leader; an end fly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobolink', 'n.', 'An American singing bird (Dolichonyx oryzivorus). The\n   male is black and white; the female is brown; -- called also, ricebird,\n   reedbird, and Boblincoln.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobsled', 'n.', 'Alt. of Bobsleigh');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobsleigh', 'n.', 'A short sled, mostly used as one of a pair connected by\n   a reach or coupling; also, the compound sled so formed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobstay', 'n.', 'A rope or chain to confine the bowsprit of a ship downward\n   to the stem or cutwater; -- usually in the pl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobtail', 'n.', 'An animal (as a horse or dog) with a short tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobtail', 'a.', 'Bobtailed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobtailed', 'a.', 'Having the tail cut short, or naturally short;\n   curtailed; as, a bobtailed horse or dog; a bobtailed coat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bobwhite', 'n.', 'The common quail of North America (Colinus, or Ortyx,\n   Virginianus); -- so called from its note.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bob wig', '', 'A short wig with bobs or short curls; -- called also bobtail\n   wig.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocal', 'n.', 'A cylindrical glass vessel, with a large and short neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocardo', 'n.', 'A form of syllogism of which the first and third\n   propositions are particular negatives, and the middle term a universal\n   affirmative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocardo', 'n.', 'A prison; -- originally the name of the old north gate in\n   Oxford, which was used as a prison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocasine', 'n.', 'A sort of fine buckram.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocca', 'n.', 'The round hole in the furnace of a glass manufactory through\n   which the fused glass is taken out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boce', 'n.', 'A European fish (Box vulgaris); having a compressed body and\n   bright colors; -- called also box, and bogue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bock beer', '', 'A strong beer, originally made in Bavaria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bockelet', 'n.', 'A kind of long-winged hawk; -- called also bockerel, and\n   bockeret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bockey', 'n.', 'A bowl or vessel made from a gourd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bocking', 'n.', 'A coarse woolen fabric, used for floor cloths, to cover\n   carpets, etc.; -- so called from the town of Bocking, in England, where\n   it was first made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bockland', 'n.', 'See Bookland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boddice', 'n.', 'See Bodick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boded', 'imp. & p. p.', 'of Bode');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boding', 'p. pr. & vb. n.', 'of Bode');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'v. t.', 'To indicate by signs, as future events; to be the omen of;\n   to portend to presage; to foreshow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'v. i.', 'To foreshow something; to augur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'n.', 'An omen; a foreshadowing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'n.', 'A bid; an offer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'v. t.', 'A messenger; a herald.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'n.', 'A stop; a halting; delay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'imp. & p. p.', 'Abode.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bode', 'p. p.', 'Bid or bidden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodeful', 'a.', 'Portentous; ominous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodement', 'n.', 'An omen; a prognostic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodge', 'n.', 'A botch; a patch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodged', 'imp. & p. p.', 'of Bodge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodge', 'v. t.', 'To botch; to mend clumsily; to patch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodge', 'v. i.', 'See Budge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodian', 'n.', 'A large food fish (Diagramma lineatum); native of the East\n   Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodice', 'n.', 'A kind of under waist stiffened with whalebone, etc., worn\n   esp. by women; a corset; stays.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodice', 'n.', 'A close-fitting outer waist or vest forming the upper part\n   of a womans dress, or a portion of it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodiced', 'a.', 'Wearing a bodice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodied', 'a.', 'Having a body; -- usually in composition; as, able-bodied.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodiless', 'a.', 'Having no body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodiless', 'a.', 'Without material form; incorporeal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodiliness', 'n.', 'Corporeality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodily', 'a.', 'Having a body or material form; physical; corporeal;\n   consisting of matter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodily', 'a.', 'Of or pertaining to the body, in distinction from the mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodily', 'a.', 'Real; actual; put in execution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodily', 'adv.', 'Corporeally; in bodily form; united with a body or\n   matter; in the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodily', 'adv.', 'In respect to, or so as to affect, the entire body or\n   mass; entirely; all at once; completely; as, to carry away bodily.\n   \"Leapt bodily below.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boding', 'a.', 'Foreshowing; presaging; ominous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boding', 'n.', 'A prognostic; an omen; a foreboding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'A dagger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'An implement of steel, bone, ivory, etc., with a sharp\n   point, for making holes by piercing; a /tiletto; an eyeleteer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'A sharp tool, like an awl, used for picking /ut letters\n   from a column or page in making corrections.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'A kind of needle with a large eye and a blunt point, for\n   drawing tape, ribbon, etc., through a loop or a hem; a tape needle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'A kind of pin used by women to fasten the hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodkin', 'n.', 'See Baudekin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodle', 'n.', 'A small Scotch coin worth about one sixth of an English\n   penny.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodleian', 'a.', 'Of or pertaining to Sir Thomas Bodley, or to the\n   celebrated library at Oxford, founded by him in the sixteenth century.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodock', 'n.', 'The Osage orange.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodrage', 'n.', 'A raid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodies', 'pl. ', 'of Body');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'The material organized substance of an animal, whether living\n   or dead, as distinguished from the spirit, or vital principle; the\n   physical person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'The trunk, or main part, of a person or animal, as\n   distinguished from the limbs and head; the main, central, or principal\n   part, as of a tree, army, country, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'The real, as opposed to the symbolical; the substance, as\n   opposed to the shadow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'A person; a human being; -- frequently in composition; as,\n   anybody, nobody.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'A number of individuals spoken of collectively, usually as\n   united by some common tie, or as organized for some purpose; a\n   collective whole or totality; a corporation; as, a legislative body; a\n   clerical body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'A number of things or particulars embodied in a system; a\n   general collection; as, a great body of facts; a body of laws or of\n   divinity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'Any mass or portion of matter; any substance distinct from\n   others; as, a metallic body; a moving body; an aeriform body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'Amount; quantity; extent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'That part of a garment covering the body, as distinguished\n   from the parts covering the limbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'The bed or box of a vehicle, on or in which the load is\n   placed; as, a wagon body; a cart body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'The shank of a type, or the depth of the shank (by which the\n   size is indicated); as, a nonpareil face on an agate body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'A figure that has length, breadth, and thickness; any solid\n   figure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'n.', 'Consistency; thickness; substance; strength; as, this color\n   has body; wine of a good body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodied', 'imp. & p. p.', 'of Body');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodying', 'p. pr. & vb. n.', 'of Body');
  INSERT INTO words (word, word_type, description)VALUES  (    'Body', 'v. t.', 'To furnish with, or as with, a body; to produce in\n   definite shape; to embody.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodyguard', 'n.', 'A guard to protect or defend the person; a lifeguard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bodyguard', 'n.', 'Retinue; attendance; following.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boeotian', 'a.', 'Of or pertaining to Boeotia; hence, stupid; dull; obtuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boeotian', 'n.', 'A native of Boeotia; also, one who is dull and ignorant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boer', 'n.', 'A colonist or farmer in South Africa of Dutch descent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boes', '3d sing. pr.', 'Behoves or behooves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bog', 'n.', 'A quagmire filled with decayed moss and other vegetable\n   matter; wet spongy ground where a heavy body is apt to sink; a marsh; a\n   morass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bog', 'n.', 'A little elevated spot or clump of earth, roots, and grass, in\n   a marsh or swamp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogged', 'imp. & p. p.', 'of Bog');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogging', 'p. pr. & vb. n.', 'of Bog');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bog', 'v. t.', 'To sink, as into a bog; to submerge in a bog; to cause to\n   sink and stick, as in mud and mire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogberry', 'n.', 'The small cranberry (Vaccinium oxycoccus); which grows in\n   boggy places.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogey', 'n.', 'A goblin; a bugbear. See Bogy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggard', 'n.', 'A bogey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggled', 'imp. & p. p.', 'of Boggle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggling', 'p. pr. & vb. n.', 'of Boggle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggle', 'n.', 'To stop or hesitate as if suddenly frightened, or in doubt,\n   or impeded by unforeseen difficulties; to take alarm; to exhibit\n   hesitancy and indecision.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggle', 'n.', 'To do anything awkwardly or unskillfully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggle', 'n.', 'To play fast and loose; to dissemble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggle', 'v. t.', 'To embarrass with difficulties; to make a bungle or\n   botch of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggler', 'n.', 'One who boggles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogglish', 'a.', 'Doubtful; skittish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boggy', 'a.', 'Consisting of, or containing, a bog or bogs; of the nature\n   of a bog; swampy; as, boggy land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogie', 'n.', 'A four-wheeled truck, having a certain amount of play around\n   a vertical axis, used to support in part a locomotive on a railway\n   track.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogle', 'n.', 'A goblin; a specter; a frightful phantom; a bogy; a bugbear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogsucker', 'n.', 'The American woodcock; -- so called from its feeding\n   among the bogs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogtrotter', 'n.', 'One who lives in a boggy country; -- applied in\n   derision to the lowest class of Irish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogtrotting', 'a.', 'Living among bogs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogue', 'v. i.', 'To fall off from the wind; to edge away to leeward; --\n   said only of inferior craft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogue', 'n.', 'The boce; -- called also bogue bream. See Boce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogus', 'a.', 'Spurious; fictitious; sham; -- a cant term originally\n   applied to counterfeit coin, and hence denoting anything counterfeit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogus', 'n.', 'A liquor made of rum and molasses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogwood', 'n.', 'The wood of trees, esp. of oaks, dug up from peat bogs. It\n   is of a shining black or ebony color, and is largely used for making\n   ornaments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogies', 'pl. ', 'of Bogy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bogy', 'n.', 'A specter; a hobgoblin; a bugbear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohea', 'n.', 'Bohea tea, an inferior kind of black tea. See under Tea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemia', 'n.', 'A country of central Europe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemia', 'n.', 'Fig.: The region or community of social Bohemians. See\n   Bohemian, n., 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemian', 'a.', 'Of or pertaining to Bohemia, or to the language of its\n   ancient inhabitants or their descendants. See Bohemian, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemian', 'n.', 'Of or pertaining to a social gypsy or \"Bohemian\" (see\n   Bohemian, n., 3); vagabond; unconventional; free and easy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemian', 'n.', 'A native of Bohemia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemian', 'n.', 'The language of the Czechs (the ancient inhabitants of\n   Bohemia); the richest and most developed of the dialects of the Slavic\n   family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemian', 'n.', 'A restless vagabond; -- originally, an idle stroller or\n   gypsy (as in France) thought to have come from Bohemia; in later times\n   often applied to an adventurer in art or literature, of irregular,\n   unconventional habits, questionable tastes, or free morals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohemianism', 'n.', 'The characteristic conduct or methods of a Bohemian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bohun upas', '', 'See Upas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiar', 'n.', 'See Boyar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiled', 'imp. & p. p.', 'of Boil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiling', 'p. pr. & vb. n.', 'of Boil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v.', 'To be agitated, or tumultuously moved, as a liquid by the\n   generation and rising of bubbles of steam (or vapor); or of currents\n   produced by heating it to the boiling point; to be in a state of\n   ebullition; as, the water boils.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v.', 'To be agitated like boiling water, by any other cause than\n   heat; to bubble; to effervesce; as, the boiling waves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v.', 'To pass from a liquid to an aeriform state or vapor when\n   heated; as, the water boils away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v.', 'To be moved or excited with passion; to be hot or fervid; as,\n   his blood boils with anger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v.', 'To be in boiling water, as in cooking; as, the potatoes are\n   boiling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v. t.', 'To heat to the boiling point, or so as to cause\n   ebullition; as, to boil water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v. t.', 'To form, or separate, by boiling or evaporation; as, to\n   boil sugar or salt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v. t.', 'To subject to the action of heat in a boiling liquid so as\n   to produce some specific effect, as cooking, cleansing, etc.; as, to\n   boil meat; to boil clothes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'v. t.', 'To steep or soak in warm water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'n.', 'Act or state of boiling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boil', 'n.', 'A hard, painful, inflamed tumor, which, on suppuration,\n   discharges pus, mixed with blood, and discloses a small fibrous mass of\n   dead tissue, called the core.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boilary', 'n.', 'See Boilery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiled', 'a.', 'Dressed or cooked by boiling; subjected to the action of a\n   boiling liquid; as, boiled meat; a boiled dinner; boiled clothes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiler', 'n.', 'One who boils.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiler', 'n.', 'A vessel in which any thing is boiled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiler', 'n.', 'A strong metallic vessel, usually of wrought iron plates\n   riveted together, or a composite structure variously formed, in which\n   steam is generated for driving engines, or for heating, cooking, or\n   other purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boilery', 'n.', 'A place and apparatus for boiling, as for evaporating\n   brine in salt making.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiling', 'a.', 'Heated to the point of bubbling; heaving with bubbles; in\n   tumultuous agitation, as boiling liquid; surging; seething; swelling\n   with heat, ardor, or passion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiling', 'n.', 'The act of ebullition or of tumultuous agitation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boiling', 'n.', 'Exposure to the action of a hot liquid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boilingly', 'adv.', 'With boiling or ebullition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bois darc', '', 'The Osage orange (Maclura aurantiaca).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bois durci', '', 'A hard, highly polishable composition, made of fine\n   sawdust from hard wood (as rosewood) mixed with blood, and pressed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boist', 'n.', 'A box.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterous', 'a.', 'Rough or rude; unbending; unyielding; strong; powerful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterous', 'a.', 'Exhibiting tumultuous violence and fury; acting with\n   noisy turbulence; violent; rough; stormy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterous', 'a.', 'Noisy; rough; turbulent; as, boisterous mirth;\n   boisterous behavior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterous', 'a.', 'Vehement; excessive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterously', 'adv.', 'In a boisterous manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boisterousness', 'n.', 'The state or quality of being boisterous;\n   turbulence; disorder; tumultuousness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boistous', 'a.', 'Rough or rude; coarse; strong; violent; boisterous;\n   noisy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bojanus organ', '', 'A glandular organ of bivalve mollusca, serving in part\n   as a kidney.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bokadam', 'n.', 'See Cerberus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boke', 'v. t. & i.', 'To poke; to thrust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolar', 'a.', 'Of or pertaining to bole or clay; partaking of the nature\n   and qualities of bole; clayey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolas', 'n. sing. & pl.', 'A kind of missile weapon consisting of one, two,\n   or more balls of stone, iron, or other material, attached to the ends\n   of a leather cord; -- used by the Gauchos of South America, and others,\n   for hurling at and entangling an animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'Forward to meet danger; venturesome; daring; not timorous or\n   shrinking from risk; brave; courageous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'Exhibiting or requiring spirit and contempt of danger;\n   planned with courage; daring; vigorous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'In a bad sense, too forward; taking undue liberties; over\n   assuming or confident; lacking proper modesty or restraint; rude;\n   impudent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'Somewhat overstepping usual bounds, or conventional rules, as\n   in art, literature, etc.; taking liberties in composition or\n   expression; as, the figures of an author are bold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'Standing prominently out to view; markedly conspicuous;\n   striking the eye; in high relief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'n.', 'Steep; abrupt; prominent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold eagle', '', 'an Australian eagle (Aquila audax); which destroys lambs\n   and even the kangaroo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'v. t.', 'To make bold or daring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold', 'v. i.', 'To be or become bold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boldened', 'imp. & p. p.', 'of Bolden');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolden', 'v. t.', 'To make bold; to encourage; to embolden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold-faced', 'a.', 'Somewhat impudent; lacking modesty; as, a bold-faced\n   woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bold-faced', 'a.', 'Having a conspicuous or heavy face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boldly', 'adv.', 'In a bold manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boldness', 'n.', 'The state or quality of being bold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boldo', 'n.', 'Alt. of Boldu');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boldu', 'n.', 'A fragrant evergreen shrub of Chili (Peumus Boldus). The\n   bark is used in tanning, the wood for making charcoal, the leaves in\n   medicine, and the drupes are eaten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bole', 'n.', 'The trunk or stem of a tree, or that which is like it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bole', 'n.', 'An aperture, with a wooden shutter, in the wall of a house,\n   for giving, occasionally, air or light; also, a small closet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bole', 'n.', 'A measure. See Boll, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bole', 'n.', 'Any one of several varieties of friable earthy clay, usually\n   colored more or less strongly red by oxide of iron, and used to color\n   and adulterate various substances. It was formerly used in medicine. It\n   is composed essentially of hydrous silicates of alumina, or more rarely\n   of magnesia. See Clay, and Terra alba.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bole', 'n.', 'A bolus; a dose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolection', 'n.', 'A projecting molding round a panel. Same as Bilection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolero', 'n.', 'A Spanish dance, or the lively music which accompanies it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'bolete', 'n.', 'any fungus of the family Boletaceae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boletic', 'a.', 'Pertaining to, or obtained from, the Boletus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boletus', 'n.', 'A genus of fungi having the under side of the pileus or\n   cap composed of a multitude of fine separate tubes. A few are edible,\n   and others very poisonous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boley', 'n.', 'Alt. of Bolye');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolye', 'n.', 'Same as Booly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolide', 'n.', 'A kind of bright meteor; a bolis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolis', 'n.', 'A meteor or brilliant shooting star, followed by a train of\n   light or sparks; esp. one which explodes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolivian', 'a.', 'Of or pertaining to Bolivia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolivian', 'n.', 'A native of Bolivia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boll', 'n.', 'The pod or capsule of a plant, as of flax or cotton; a\n   pericarp of a globular form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boll', 'n.', 'A Scotch measure, formerly in use: for wheat and beans it\n   contained four Winchester bushels; for oats, barley, and potatoes, six\n   bushels. A boll of meal is 140 lbs. avoirdupois. Also, a measure for\n   salt of two bushels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolled', 'imp. & p. p.', 'of Boll');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boll', 'v. i.', 'To form a boll or seed vessel; to go to seed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bollandists', 'n. pl.', 'The Jesuit editors of the \"Acta Sanctorum\", or\n   Lives of the Saints; -- named from John Bolland, who began the work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bollard', 'n.', 'An upright wooden or iron post in a boat or on a dock,\n   used in veering or fastening ropes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bollen', 'a.', 'See Boln, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolling', 'v. t.', 'A tree from which the branches have been cut; a\n   pollard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bollworm', 'n.', 'The larva of a moth (Heliothis armigera) which devours\n   the bolls or unripe pods of the cotton plant, often doing great damage\n   to the crops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boln', 'v. i.', 'To swell; to puff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boln', 'a.', 'Alt. of Bollen');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bollen', 'a.', 'Swollen; puffed out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bologna', 'n.', 'A city of Italy which has given its name to various\n   objects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bologna', 'n.', 'A Bologna sausage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolognese', 'a.', 'Of or pertaining to Bologna.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolognese', 'n.', 'A native of Bologna.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolognian', 'a. & n.', 'Bolognese.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolometer', 'n.', 'An instrument for measuring minute quantities of radiant\n   heat, especially in different parts of the spectrum; -- called also\n   actinic balance, thermic balance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A long pillow or cushion, used to support the head of a\n   person lying on a bed; -- generally laid under the pillows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A pad, quilt, or anything used to hinder pressure, support\n   any part of the body, or make a bandage sit easy upon a wounded part; a\n   compress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'Anything arranged to act as a support, as in various forms\n   of mechanism, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A cushioned or a piece part of a saddle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A cushioned or a piece of soft wood covered with tarred\n   canvas, placed on the trestletrees and against the mast, for the\n   collars of the shrouds to rest on, to prevent chafing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'Anything used to prevent chafing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A plate of iron or a mass of wood under the end of a\n   bridge girder, to keep the girder from resting directly on the\n   abutment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A transverse bar above the axle of a wagon, on which the\n   bed or body rests.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'The crossbeam forming the bearing piece of the body of a\n   railway car; the central and principal cross beam of a car truck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'the perforated plate in a punching machine on which\n   anything rests when being punched.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'That part of a knife blade which abuts upon the end of the\n   handle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'The metallic end of a pocketknife handle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'The rolls forming the ends or sides of the Ionic capital.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'n.', 'A block of wood on the carriage of a siege gun, upon which\n   the breech of the gun rests when arranged for transportation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolstered', 'imp. & p. p.', 'of Bolster');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolstering', 'p. pr. & vb. n.', 'of Bolster');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'v. t.', 'To support with a bolster or pillow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolster', 'v. t.', 'To support, hold up, or maintain with difficulty or\n   unusual effort; -- often with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolstered', 'a.', 'Supported; upheld.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolstered', 'a.', 'Swelled out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolsterer', 'n.', 'A supporter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A shaft or missile intended to be shot from a crossbow or\n   catapult, esp. a short, stout, blunt-headed arrow; a quarrel; an arrow,\n   or that which resembles an arrow; a dart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'Lightning; a thunderbolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A strong pin, of iron or other material, used to fasten or\n   hold something in place, often having a head at one end and screw\n   thread cut upon the other end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A sliding catch, or fastening, as for a door or gate; the\n   portion of a lock which is shot or withdrawn by the action of the key.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'An iron to fasten the legs of a prisoner; a shackle; a\n   fetter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A compact package or roll of cloth, as of canvas or silk,\n   often containing about forty yards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A bundle, as of oziers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolted', 'imp. & p. p.', 'of Bolt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolting', 'p. pr. & vb. n.', 'of Bolt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To shoot; to discharge or drive forth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To utter precipitately; to blurt or throw out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To swallow without chewing; as, to bolt food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To refuse to support, as a nomination made by a party to\n   which one has belonged or by a caucus in which one has taken part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To cause to start or spring forth; to dislodge, as conies,\n   rabbits, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To fasten or secure with, or as with, a bolt or bolts, as\n   a door, a timber, fetters; to shackle; to restrain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'To start forth like a bolt or arrow; to spring abruptly;\n   to come or go suddenly; to dart; as, to bolt out of the room.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'To strike or fall suddenly like a bolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'To spring suddenly aside, or out of the regular path; as,\n   the horse bolted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'To refuse to support a nomination made by a party or a\n   caucus with which one has been connected; to break away from a party.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'adv.', 'In the manner of a bolt; suddenly; straight; unbendingly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'A sudden spring or start; a sudden spring aside; as, the\n   horse made a bolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'A sudden flight, as to escape creditors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. i.', 'A refusal to support a nomination made by the party with\n   which one has been connected; a breaking away from ones party.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolted', 'imp. & p. p.', 'of Bolt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolting', 'p. pr. & vb. n.', 'of Bolt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To sift or separate the coarser from the finer particles\n   of, as bran from flour, by means of a bolter; to separate, assort,\n   refine, or purify by other means.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To separate, as if by sifting or bolting; -- with out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'v. t.', 'To discuss or argue privately, and for practice, as cases\n   at law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolt', 'n.', 'A sieve, esp. a long fine sieve used in milling for bolting\n   flour and meal; a bolter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boltel', 'n.', 'See Boultel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolter', 'n.', 'One who bolts; esp.: (a) A horse which starts suddenly\n   aside. (b) A man who breaks away from his party.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolter', 'n.', 'One who sifts flour or meal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolter', 'n.', 'An instrument or machine for separating bran from flour, or\n   the coarser part of meal from the finer; a sieve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolter', 'n.', 'A kind of fishing line. See Boulter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolthead', 'n.', 'A long, straight-necked, glass vessel for chemical\n   distillations; -- called also a matrass or receiver.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolthead', 'n.', 'The head of a bolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolting', 'n.', 'A darting away; a starting off or aside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolting', 'n.', 'A sifting, as of flour or meal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolting', 'n.', 'A private arguing of cases for practice by students, as in\n   the Inns of Court.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boltonite', 'n.', 'A granular mineral of a grayish or yellowish color,\n   found in Bolton, Massachusetts. It is a silicate of magnesium,\n   belonging to the chrysolite family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boltrope', 'n.', 'A rope stitched to the edges of a sail to strengthen the\n   sail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boltsprit', 'n.', 'See Bowsprit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolty', 'n.', 'An edible fish of the Nile (genus Chromis).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boluses', 'pl. ', 'of Bolus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bolus', 'n.', 'A rounded mass of anything, esp. a large pill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bom', 'n.', 'A large American serpent, so called from the sound it makes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomb', 'n.', 'A great noise; a hollow sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomb', 'n.', 'A shell; esp. a spherical shell, like those fired from\n   mortars. See Shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomb', 'n.', 'A bomb ketch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomb', 'v. t.', 'To bombard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomb', 'v. i.', 'To sound; to boom; to make a humming or buzzing sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombace', 'n.', 'Cotton; padding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'n.', 'A piece of heavy ordnance formerly used for throwing\n   stones and other ponderous missiles. It was the earliest kind of\n   cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'n.', 'A bombardment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'n.', 'A large drinking vessel or can, or a leather bottle, for\n   carrying liquor or beer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'n.', 'Padded breeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'n.', 'See Bombardo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombarded', 'imp. & p. p.', 'of Bombard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombarding', 'p. pr. & vb. n.', 'of Bombard');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombard', 'v. t.', 'To attack with bombards or with artillery; especially,\n   to throw shells, hot shot, etc., at or into.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardier', 'n.', 'One who used or managed a bombard; an artilleryman; a\n   gunner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardier', 'n.', 'A noncommissioned officer in the British artillery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardman', 'n.', 'One who carried liquor or beer in a can or bombard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardment', 'n.', 'An attack upon a fortress or fortified town, with\n   shells, hot shot, rockets, etc.; the act of throwing bombs and shot\n   into a town or fortified place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardo', 'n.', 'Alt. of Bombardon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombardon', 'n.', 'Originally, a deep-toned instrument of the oboe or\n   bassoon family; thence, a bass reed stop on the organ. The name\n   bombardon is now given to a brass instrument, the lowest of the\n   saxhorns, in tone resembling the ophicleide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombasine', 'n.', 'Same as Bombazine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombast', 'n.', 'Originally, cotton, or cotton wool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombast', 'n.', 'Cotton, or any soft, fibrous material, used as stuffing\n   for garments; stuffing; padding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombast', 'n.', 'Fig.: High-sounding words; an inflated style; language\n   above the dignity of the occasion; fustian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombast', 'a.', 'High-sounding; inflated; big without meaning;\n   magniloquent; bombastic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombast', 'v. t.', 'To swell or fill out; to pad; to inflate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombastic', 'a.', 'Alt. of Bombastical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombastical', 'a.', 'Characterized by bombast; high-sounding; inflated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombastry', 'n.', 'Swelling words without much meaning; bombastic language;\n   fustian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombax', 'n.', 'A genus of trees, called also the silkcotton tree; also, a\n   tree of the genus Bombax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombazet Bombazette', 'n.', 'A sort of thin woolen cloth. It is of various\n   colors, and may be plain or twilled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombazine', 'n.', 'A twilled fabric for dresses, of which the warp is silk,\n   and the weft worsted. Black bombazine has been much used for mourning\n   garments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombic', 'a.', 'Pertaining to, or obtained from, the silkworm; as, bombic\n   acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombilate', 'n.', 'To hum; to buzz.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombilation', 'n.', 'A humming sound; a booming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombinate', 'v. i.', 'To hum; to boom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombination', 'n.', 'A humming or buzzing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bomboloes', 'pl. ', 'of Bombolo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombolo', 'n.', 'A thin spheroidal glass retort or flask, used in the\n   sublimation of camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombproof', 'a.', 'Secure against the explosive force of bombs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombproof', 'n.', 'A structure which heavy shot and shell will not\n   penetrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombshell', 'n.', 'A bomb. See Bomb, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombycid', 'a.', 'Like or pertaining to the genus Bombyx, or the family\n   Bombycidae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombycinous', 'a.', 'Silken; made of silk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombycinous', 'a.', 'Being of the color of the silkworm; transparent with a\n   yellow tint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombylious', 'a.', 'Buzzing, like a bumblebee; as, the bombylious noise of\n   the horse fly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bombyx', 'n.', 'A genus of moths, which includes the silkworm moth. See\n   Silkworm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bon', 'a.', 'Good; valid as security for something.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bon-accord', 'n.', 'Good will; good fellowship; agreement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bona fide', '', 'In or with good faith; without fraud or deceit; real or\n   really; actual or actually; genuine or genuinely; as, you must proceed\n   bona fide; a bona fide purchaser or transaction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonair', 'a.', 'Gentle; courteous; complaisant; yielding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonanza', 'n.', 'In mining, a rich mine or vein of silver or gold; hence,\n   anything which is a mine of wealth or yields a large income.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonapartean', 'a.', 'Of or pertaining to Napoleon Bonaparte or his family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonapartism', 'n.', 'The policy of Bonaparte or of the Bonapartes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonapartist', 'n.', 'One attached to the policy or family of Bonaparte, or\n   of the Bonapartes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bona peritura', '', 'Perishable goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bona roba', '', 'A showy wanton; a courtesan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonasus', 'n.', 'Alt. of Bonassus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonassus', 'n.', 'The aurochs or European bison. See Aurochs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonbon', 'n.', 'Sugar confectionery; a sugarplum; hence, any dainty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonce', 'n.', 'A boys game played with large marbles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonchretien', 'n.', 'A name given to several kinds of pears. See Bartlett.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boncilate', 'n.', 'A substance composed of ground bone, mineral matters,\n   etc., hardened by pressure, and used for making billiard balls, boxes,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'That which binds, ties, fastens, or confines, or by which\n   anything is fastened or bound, as a cord, chain, etc.; a band; a\n   ligament; a shackle or a manacle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'The state of being bound; imprisonment; captivity, restraint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'A binding force or influence; a cause of union; a uniting\n   tie; as, the bonds of fellowship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'Moral or political duty or obligation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'A writing under seal, by which a person binds himself, his\n   heirs, executors, and administrators, to pay a certain sum on or before\n   a future day appointed. This is a single bond. But usually a condition\n   is added, that, if the obligor shall do a certain act, appear at a\n   certain place, conform to certain rules, faithfully perform certain\n   duties, or pay a certain sum of money, on or before a time specified,\n   the obligation shall be void; otherwise it shall remain in full force.\n   If the condition is not performed, the bond becomes forfeited, and the\n   obligor and his heirs are liable to the payment of the whole sum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'An instrument (of the nature of the ordinary legal bond) made\n   by a government or a corporation for purpose of borrowing money; as, a\n   government, city, or railway bond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'The state of goods placed in a bonded warehouse till the\n   duties are paid; as, merchandise in bond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'The union or tie of the several stones or bricks forming a\n   wall. The bricks may be arranged for this purpose in several different\n   ways, as in English or block bond (Fig. 1); where one course consists\n   of bricks with their ends toward the face of the wall, called headers,\n   and the next course of bricks with their lengths parallel to the face\n   of the wall, called stretchers; Flemish bond (Fig.2); where each course\n   consists of headers and stretchers alternately, so laid as always to\n   break joints; Cross bond, which differs from the English by the change\n   of the second stretcher line so that its joints come in the middle of\n   the first, and the same position of stretchers comes back every fifth\n   line; Combined cross and English bond, where the inner part of the wall\n   is laid in the one method, the outer in the other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'A unit of chemical attraction; as, oxygen has two bonds of\n   affinity. It is often represented in graphic formulae by a short line\n   or dash. See Diagram of Benzene nucleus, and Valence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonded', 'imp. & p. p.', 'of Bond');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonding', 'p. pr. & vb. n.', 'of Bond');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'v. t.', 'To place under the conditions of a bond; to mortgage; to\n   secure the payment of the duties on (goods or merchandise) by giving a\n   bond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'v. t.', 'To dispose in building, as the materials of a wall, so as\n   to secure solidity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'n.', 'A vassal or serf; a slave.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond', 'a.', 'In a state of servitude or slavery; captive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondage', 'a.', 'The state of being bound; condition of being under\n   restraint; restraint of personal liberty by compulsion; involuntary\n   servitude; slavery; captivity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondage', 'a.', 'Obligation; tie of duty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondage', 'a.', 'Villenage; tenure of land on condition of doing the\n   meanest services for the owner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondager', 'n.', 'A field worker, esp. a woman who works in the field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondar', 'n.', 'A small quadruped of Bengal (Paradoxurus bondar); allied to\n   the genet; -- called also musk cat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonded', 'a.', 'Placed under, or covered by, a bond, as for the payment of\n   duties, or for conformity to certain regulations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonder', 'n.', 'One who places goods under bond or in a bonded warehouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonder', 'n.', 'A bonding stone or brick; a bondstone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonder', 'n.', 'A freeholder on a small scale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondholder', 'n.', 'A person who holds the bonds of a public or private\n   corporation for the payment of money at a certain time.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondmaid', 'n.', 'A female slave, or one bound to service without wages, as\n   distinguished from a hired servant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondmen', 'pl. ', 'of Bondman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondman', 'n.', 'A man slave, or one bound to service without wages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondman', 'n.', 'A villain, or tenant in villenage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond servant', '', 'A slave; one who is bound to service without wages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bond service', '', 'The condition of a bond servant; service without wages;\n   slavery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondslave', 'n.', 'A person in a state of slavery; one whose person and\n   liberty are subjected to the authority of a master.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondsmen', 'pl. ', 'of Bondsman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondsman', 'n.', 'A slave; a villain; a serf; a bondman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondsman', 'n.', 'A surety; one who is bound, or who gives security, for\n   another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondstone', 'n.', 'A stone running through a wall from one face to another,\n   to bind it together; a binding stone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondswoman', 'n.', 'See Bondwoman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonduc', 'n.', 'See Nicker tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondwomen', 'pl. ', 'of Bondwoman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bondwoman', 'n.', 'A woman who is a slave, or in bondage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'The hard, calcified tissue of the skeleton of vertebrate\n   animals, consisting very largely of calcic carbonate, calcic phosphate,\n   and gelatine; as, blood and bone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'One of the pieces or parts of an animal skeleton; as, a rib\n   or a thigh bone; a bone of the arm or leg; also, any fragment of bony\n   substance. (pl.) The frame or skeleton of the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'Anything made of bone, as a bobbin for weaving bone lace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'Two or four pieces of bone held between the fingers and\n   struck together to make a kind of music.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'Dice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'Whalebone; hence, a piece of whalebone or of steel for a\n   corset.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'n.', 'Fig.: The framework of anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boned', 'imp. & p. p.', 'of Bone');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boning', 'p. pr. & vb. n.', 'of Bone');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'v. t.', 'To withdraw bones from the flesh of, as in cookery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'v. t.', 'To put whalebone into; as, to bone stays.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'v. t.', 'To fertilize with bone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'v. t.', 'To steal; to take possession of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bone', 'v. t.', 'To sight along an object or set of objects, to see if it\n   or they be level or in line, as in carpentry, masonry, and surveying.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boneache', 'n.', 'Pain in the bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boneblack', 'n.', 'See Bone black, under Bone, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boned', 'a.', 'Having (such) bones; -- used in composition; as, big-boned;\n   strong-boned.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boned', 'a.', 'Deprived of bones; as, boned turkey or codfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boned', 'a.', 'Manured with bone; as, boned land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonedog', 'n.', 'The spiny dogfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonefish', 'n.', 'See Ladyfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boneless', 'a.', 'Without bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boneset', 'n.', 'A medicinal plant, the thoroughwort (Eupatorium\n   perfoliatum). Its properties are diaphoretic and tonic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonesetter', 'n.', 'One who sets broken or dislocated bones; -- commonly\n   applied to one, not a regular surgeon, who makes an occupation of\n   setting bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boneshaw', 'n.', 'Sciatica.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonetta', 'n.', 'See Bonito.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonfire', 'n.', 'A large fire built in the open air, as an expression of\n   public joy and exultation, or for amusement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bongrace', 'n.', 'A projecting bonnet or shade to protect the complexion;\n   also, a wide-brimmed hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonhomie', 'n.', 'Alt. of Bonhommie');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonhommie', 'n.', 'good nature; pleasant and easy manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonibell', 'n.', 'See Bonnibel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boniface', 'n.', 'An innkeeper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boniform', 'a.', 'Sensitive or responsive to moral excellence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonify', 'v. t.', 'To convert into, or make, good.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boniness', 'n.', 'The condition or quality of being bony.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boning', 'n.', 'The clearing of bones from fish or meat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boning', 'n.', 'The manuring of land with bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boning', 'n.', 'A method of leveling a line or surface by sighting along\n   the tops of two or more straight edges, or a range of properly spaced\n   poles. See 3d Bone, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonitary', 'a.', 'Beneficial, as opposed to statutory or civil; as,\n   bonitary dominion of land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonitoes', 'pl. ', 'of Bonito');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonito', 'n.', 'A large tropical fish (Orcynus pelamys) allied to the\n   tunny. It is about three feet long, blue above, with four brown stripes\n   on the sides. It is sometimes found on the American coast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonito', 'n.', 'The skipjack (Sarda Mediterranea) of the Atlantic, an\n   important and abundant food fish on the coast of the United States, and\n   (S. Chilensis) of the Pacific, and other related species. They are\n   large and active fishes, of a blue color with black oblique stripes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonito', 'n.', 'The medregal (Seriola fasciata); an edible fish of the\n   southern of the United States and the West Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonito', 'n.', 'The cobia or crab eater (Elacate canada); an edible fish of\n   the Middle and Southern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonsmots', 'pl. ', 'of Bonmot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonmot', 'n.', 'A witty repartee; a jest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonne', 'n.', 'A female servant charged with the care of a young child.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnes bouches', 'pl. ', 'of Bonne bouche');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonne bouche', '', 'A delicious morsel or mouthful; a tidbit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A headdress for men and boys; a cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A soft, elastic, very durable cap, made of thick, seamless\n   woolen stuff, and worn by men in Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A covering for the head, worn by women, usually protecting\n   more or less the back and sides of the head, but no part of the\n   forehead. The shape of the bonnet varies greatly at different times;\n   formerly the front part projected, and spread outward, like the mouth\n   of a funnel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'Anything resembling a bonnet in shape or use');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A small defense work at a salient angle; or a part of a\n   parapet elevated to screen the other part from enfilade fire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A metallic canopy, or projection, over an opening, as a\n   fireplace, or a cowl or hood to increase the draught of a chimney, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A frame of wire netting over a locomotive chimney, to\n   prevent escape of sparks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'A roofing over the cage of a mine, to protect its occupants\n   from objects falling down the shaft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'In pumps, a metal covering for the openings in the valve\n   chambers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'An additional piece of canvas laced to the foot of a jib or\n   foresail in moderate winds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'The second stomach of a ruminating animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'n.', 'An accomplice of a gambler, auctioneer, etc., who entices\n   others to bet or to bid; a decoy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnet', 'v. i.', 'To take off the bonnet or cap as a mark of respect; to\n   uncover.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonneted', 'a.', 'Wearing a bonnet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonneted', 'a.', 'Protected by a bonnet. See Bonnet, 4 (a).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnetless', 'a.', 'Without a bonnet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnibel', 'n.', 'A handsome girl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnie', 'a.', 'See Bonny, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnilass', 'n.', 'A \"bonny lass\"; a beautiful girl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnily', 'adv.', 'Gayly; handsomely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonniness', 'n.', 'The quality of being bonny; gayety; handsomeness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonny', 'a.', 'Handsome; beautiful; pretty; attractively lively and\n   graceful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonny', 'a.', 'Gay; merry; frolicsome; cheerful; blithe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonny', 'n.', 'A round and compact bed of ore, or a distinct bed, not\n   communicating with a vein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonnyclabber', 'n.', 'Coagulated sour milk; loppered milk; curdled milk; --\n   sometimes called simply clabber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bon Silene', '', 'A very fragrant tea rose with petals of various shades of\n   pink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonspiel', 'n.', 'A cur/ing match between clubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bontebok', 'n.', 'The pied antelope of South Africa (Alcelaphus pygarga).\n   Its face and rump are white. Called also nunni.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bon ton', '', 'The height of the fashion; fashionable society.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonuses', 'pl. ', 'of Bonus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonus', 'n.', 'A premium given for a loan, or for a charter or other\n   privilege granted to a company; as the bank paid a bonus for its\n   charter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonus', 'n.', 'An extra dividend to the shareholders of a joint stock\n   company, out of accumulated profits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonus', 'n.', 'Money paid in addition to a stated compensation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bons vivants', 'pl. ', 'of Bon vivant');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bon vivant', 'p. pr.', 'A good fellow; a jovial companion; a free liver.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bony', 'a.', 'Consisting of bone, or of bones; full of bones; pertaining to\n   bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bony', 'a.', 'Having large or prominent bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bonze', 'n.', 'A Buddhist or Fohist priest, monk, or nun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boobies', 'pl. ', 'of Booby');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booby', 'n.', 'A dunce; a stupid fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booby', 'n.', 'A swimming bird (Sula fiber or S. sula) related to the\n   common gannet, and found in the West Indies, nesting on the bare rocks.\n   It is so called on account of its apparent stupidity. The name is also\n   sometimes applied to other species of gannets; as, S. piscator, the\n   red-footed booby.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booby', 'n.', 'A species of penguin of the antarctic seas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booby', 'a.', 'Having the characteristics of a booby; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boobyish', 'a.', 'Stupid; dull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boodh', 'n.', 'Same as Buddha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boodhism', 'n.', 'Same as Buddhism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boodhist', 'n.', 'Same as Buddhist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boodle', 'n.', 'The whole collection or lot; caboodle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boodle', 'n.', 'Money given in payment for votes or political influence;\n   bribe money; swag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boohooed', 'imp. & p. p.', 'of Boohoe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boohooing', 'p. pr. & vb. n.', 'of Boohoe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boohoe', 'v. i.', 'To bawl; to cry loudly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boohoo', 'n.', 'The sailfish; -- called also woohoo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'n.', 'A collection of sheets of paper, or similar material, blank,\n   written, or printed, bound together; commonly, many folded and bound\n   sheets containing continuous printing or writing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'n.', 'A composition, written or printed; a treatise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'n.', 'A part or subdivision of a treatise or literary work; as, the\n   tenth book of \"Paradise Lost.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'n.', 'A volume or collection of sheets in which accounts are kept;\n   a register of debts and credits, receipts and expenditures, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'n.', 'Six tricks taken by one side, in the game of whist; in\n   certain other games, two or more corresponding cards, forming a set.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booked', 'imp. & p. p.', 'of Book');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booking', 'p. pr. & vb. n.', 'of Book');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'v. t.', 'To enter, write, or register in a book or list.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'v. t.', 'To enter the name of (any one) in a book for the purpose\n   of securing a passage, conveyance, or seat; as, to be booked for\n   Southampton; to book a seat in a theater.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book', 'v. t.', 'To mark out for; to destine or assign for; as, he is\n   booked for the valedictory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookbinder', 'n.', 'One whose occupation is to bind books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookbindery', 'n.', 'A bookbinders shop; a place or establishment for\n   binding books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookbinding', 'n.', 'The art, process, or business of binding books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookcase', 'n.', 'A case with shelves for holding books, esp. one with\n   glazed doors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookcraft', 'n.', 'Authorship; literary skill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booked', 'a.', 'Registered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booked', 'a.', 'On the way; destined.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booker', 'n.', 'One who enters accounts or names, etc., in a book; a\n   bookkeeper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookful', 'n.', 'As much as will fill a book; a book full.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookful', 'a.', 'Filled with book learning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookholder', 'n.', 'A prompter at a theater.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookholder', 'n.', 'A support for a book, holding it open, while one reads\n   or copies from it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booking clerk', '', 'A clerk who registers passengers, baggage, etc., for\n   conveyance, as by railway or steamship, or who sells passage tickets at\n   a booking office.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booking office', '', 'An office where passengers, baggage, etc., are\n   registered for conveyance, as by railway or steamship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booking office', '', 'An office where passage tickets are sold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookish', 'a.', 'Given to reading; fond of study; better acquainted with\n   books than with men; learned from books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookish', 'a.', 'Characterized by a method of expression generally found in\n   books; formal; labored; pedantic; as, a bookish way of talking; bookish\n   sentences.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookkeeper', 'n.', 'One who keeps accounts; one who has the charge of\n   keeping the books and accounts in an office.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookkeeping', 'n.', 'The art of recording pecuniary or business\n   transactions in a regular and systematic manner, so as to show their\n   relation to each other, and the state of the business in which they\n   occur; the art of keeping accounts. The books commonly used are a\n   daybook, cashbook, journal, and ledger. See Daybook, Cashbook, Journal,\n   and Ledger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookland', 'n.', 'Alt. of Bockland');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bockland', 'n.', 'Charter land held by deed under certain rents and free\n   services, which differed in nothing from free socage lands. This\n   species of tenure has given rise to the modern freeholds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book-learned', 'a.', 'Versed in books; having knowledge derived from books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookless', 'a.', 'Without books; unlearned.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booklet', 'n.', 'A little book.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmaker', 'n.', 'One who writes and publishes books; especially, one who\n   gathers his materials from other books; a compiler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmaker', 'n.', 'A betting man who \"makes a book.\" See To make a book,\n   under Book, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmen', 'pl. ', 'of Bookman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookman', 'n.', 'A studious man; a scholar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmark', 'n.', 'Something placed in a book to guide in finding a\n   particular page or passage; also, a label in a book to designate the\n   owner; a bookplate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmate', 'n.', 'A schoolfellow; an associate in study.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookmonger', 'n.', 'A dealer in books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book muslin', '', 'A kind of muslin used for the covers of books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Book muslin', '', 'A kind of thin white muslin for ladies dresses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookplate', 'n.', 'A label, placed upon or in a book, showing its ownership\n   or its position in a library.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookseller', 'n.', 'One who sells books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookselling', 'n.', 'The employment of selling books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookshelves', 'pl. ', 'of Bookshelf');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookshelf', 'n.', 'A shelf to hold books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookshop', 'n.', 'A booksellers shop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookstall', 'n.', 'A stall or stand where books are sold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookstand', 'n.', 'A place or stand for the sale of books in the streets; a\n   bookstall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookstand', 'n.', 'A stand to hold books for reading or reference.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookstore', 'n.', 'A store where books are kept for sale; -- called in\n   England a booksellers shop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookwork', 'n.', 'Work done upon a book or books (as in a printing office);\n   in distinction from newspaper or job work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookwork', 'n.', 'Study; application to books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookworm', 'n.', 'Any larva of a beetle or moth, which is injurious to\n   books. Many species are known.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bookworm', 'n.', 'A student closely attached to books or addicted to study;\n   a reader without appreciation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booky', 'a.', 'Bookish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boolies', 'pl. ', 'of Booly');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booly', 'n.', 'A company of Irish herdsmen, or a single herdsman, wandering\n   from place to place with flocks and herds, and living on their milk,\n   like the Tartars; also, a place in the mountain pastures inclosed for\n   the shelter of cattle or their keepers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A long pole or spar, run out for the purpose of extending the\n   bottom of a particular sail; as, the jib boom, the studding-sail boom,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A long spar or beam, projecting from the mast of a derrick,\n   from the outer end of which the body to be lifted is suspended.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A pole with a conspicuous top, set up to mark the channel in\n   a river or harbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A strong chain cable, or line of spars bound together,\n   extended across a river or the mouth of a harbor, to obstruct\n   navigation or passage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A line of connected floating timbers stretched across a\n   river, or inclosing an area of water, to keep saw logs, etc., from\n   floating away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. t.', 'To extend, or push, with a boom or pole; as, to boom out a\n   sail; to boom off a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomed', 'imp. & p. p.', 'of Boom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booming', 'p. pr. & vb. n.', 'of Boom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. i.', 'To cry with a hollow note; to make a hollow sound, as the\n   bittern, and some insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. i.', 'To make a hollow sound, as of waves or cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. i.', 'To rush with violence and noise, as a ship under a press\n   of sail, before a free wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. i.', 'To have a rapid growth in market value or in popular\n   favor; to go on rushingly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A hollow roar, as of waves or cannon; also, the hollow cry of\n   the bittern; a booming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'n.', 'A strong and extensive advance, with more or less noisy\n   excitement; -- applied colloquially or humorously to market prices, the\n   demand for stocks or commodities and to political chances of aspirants\n   to office; as, a boom in the stock market; a boom in coffee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boom', 'v. t.', 'To cause to advance rapidly in price; as, to boom railroad\n   or mining shares; to create a \"boom\" for; as to boom Mr. C. for\n   senator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomdas', 'n.', 'A small African hyracoid mammal (Dendrohyrax arboreus)\n   resembling the daman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomer', 'n.', 'One who, or that which, booms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomer', 'n.', 'A North American rodent, so named because it is said to\n   make a booming noise. See Sewellel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomer', 'n.', 'A large male kangaroo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomer', 'n.', 'One who works up a \"boom\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomerang', 'n.', 'A very singular missile weapon used by the natives of\n   Australia and in some parts of India. It is usually a curved stick of\n   hard wood, from twenty to thirty inches in length, from two to three\n   inches wide, and half or three quarters of an inch thick. When thrown\n   from the hand with a quick rotary motion, it describes very remarkable\n   curves, according to the shape of the instrument and the manner of\n   throwing it, often moving nearly horizontally a long distance, then\n   curving upward to a considerable height, and finally taking a\n   retrograde direction, so as to fall near the place from which it was\n   thrown, or even far in the rear of it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booming', 'a.', 'Rushing with violence; swelling with a hollow sound;\n   making a hollow sound or note; roaring; resounding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booming', 'a.', 'Advancing or increasing amid noisy excitement; as, booming\n   prices; booming popularity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booming', 'n.', 'The act of producing a hollow or roaring sound; a violent\n   rushing with heavy roar; as, the booming of the sea; a deep, hollow\n   sound; as, the booming of bitterns.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomkin', 'n.', 'Same as Bumkin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomorah', 'n.', 'A small West African chevrotain (Hyaemoschus aquaticus);\n   resembling the musk deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boomslange', 'n.', 'A large South African tree snake (Bucephalus Capensis).\n   Although considered venomous by natives, it has no poison fangs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'A prayer or petition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'That which is asked or granted as a benefit or favor; a gift;\n   a benefaction; a grant; a present.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'Good; prosperous; as, boon voyage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'Kind; bountiful; benign.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'Gay; merry; jovial; convivial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boon', 'n.', 'The woody portion flax, which is separated from the fiber as\n   refuse matter by retting, braking, and scutching.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boor', 'n.', 'A husbandman; a peasant; a rustic; esp. a clownish or\n   unrefined countryman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boor', 'n.', 'A Dutch, German, or Russian peasant; esp. a Dutch colonist in\n   South Africa, Guiana, etc.: a boer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boor', 'n.', 'A rude ill-bred person; one who is clownish in manners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boorish', 'a.', 'Like a boor; clownish; uncultured; unmannerly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boort', 'n.', 'See Bort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boose', 'n.', 'A stall or a crib for an ox, cow, or other animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boose', 'v. i.', 'To drink excessively. See Booze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booser', 'n.', 'A toper; a guzzler. See Boozer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boosted', 'imp. & p. p.', 'of Boost');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boosting', 'p. pr. & vb. n.', 'of Boost');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boost', 'v. i.', 'To lift or push from behind (one who is endeavoring to\n   climb); to push up; hence, to assist in overcoming obstacles, or in\n   making advancement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boost', 'n.', 'A push from behind, as to one who is endeavoring to climb;\n   help.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'Remedy; relief; amends; reparation; hence, one who brings\n   relief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'That which is given to make an exchange equal, or to make up\n   for the deficiency of value in one of the things exchanged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'Profit; gain; advantage; use.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booted', 'imp. & p. p.', 'of Boot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booting', 'p. pr. & vb. n.', 'of Boot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'v. t.', 'To profit; to advantage; to avail; -- generally followed\n   by it; as, what boots it?');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'v. t.', 'To enrich; to benefit; to give in addition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'A covering for the foot and lower part of the leg, ordinarily\n   made of leather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'An instrument of torture for the leg, formerly used to extort\n   confessions, particularly in Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'A place at the side of a coach, where attendants rode; also,\n   a low outside place before and behind the body of the coach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'A place for baggage at either end of an old-fashioned\n   stagecoach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'An apron or cover (of leather or rubber cloth) for the\n   driving seat of a vehicle, to protect from rain and mud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'The metal casing and flange fitted about a pipe where it\n   passes through a roof.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booted', 'imp. & p. p.', 'of Boot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booting', 'p. pr. & vb. n.', 'of Boot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'v. t.', 'To put boots on, esp. for riding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'v. t.', 'To punish by kicking with a booted foot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'v. i.', 'To boot ones self; to put on ones boots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boot', 'n.', 'Booty; spoil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootblack', 'n.', 'One who blacks boots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booted', 'a.', 'Wearing boots, especially boots with long tops, as for\n   riding; as, a booted squire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booted', 'a.', 'Having an undivided, horny, bootlike covering; -- said of\n   the tarsus of some birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootee', 'n.', 'A half boot or short boot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootes', 'n.', 'A northern constellation, containing the bright star\n   Arcturus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booth', 'n.', 'A house or shed built of boards, boughs, or other slight\n   materials, for temporary occupation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booth', 'n.', 'A covered stall or temporary structure in a fair or market,\n   or at a polling place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boothale', 'v. t. & i.', 'To forage for booty; to plunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boothose', 'n.', 'Stocking hose, or spatterdashes, in lieu of boots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boothose', 'n.', 'Hose made to be worn with boots, as by travelers on\n   horseback.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boothy', 'n.', 'See Bothy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootikin', 'n.', 'A little boot, legging, or gaiter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootikin', 'n.', 'A covering for the foot or hand, worn as a cure for the\n   gout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booting', 'n.', 'Advantage; gain; gain by plunder; booty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booting', 'n.', 'A kind of torture. See Boot, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booting', 'n.', 'A kicking, as with a booted foot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootjack', 'n.', 'A device for pulling off boots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootless', 'a.', 'Unavailing; unprofitable; useless; without advantage or\n   success.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootlick', 'n.', 'A toady.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bootmaker', 'n.', 'One who makes boots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boots', 'n.', 'A servant at a hotel or elsewhere, who cleans and blacks the\n   boots and shoes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boottopping', 'n.', 'The act or process of daubing a vessels bottom near\n   the surface of the water with a mixture of tallow, sulphur, and resin,\n   as a temporary protection against worms, after the slime, shells, etc.,\n   have been scraped off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boottopping', 'n.', 'Sheathing a vessel with planking over felt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boottree', 'n.', 'An instrument to stretch and widen the leg of a boot,\n   consisting of two pieces, together shaped like a leg, between which,\n   when put into the boot, a wedge is driven.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booty', 'n.', 'That which is seized by violence or obtained by robbery,\n   especially collective spoil taken in war; plunder; pillage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boozed', 'imp. & p. p.', 'of Booze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boozing', 'p. pr. & vb. n.', 'of Booze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booze', 'v. i.', 'To drink greedily or immoderately, esp. alcoholic liquor;\n   to tipple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Booze', 'n.', 'A carouse; a drinking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boozer', 'n.', 'One who boozes; a toper; a guzzler of alcoholic liquors; a\n   bouser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boozy', 'a.', 'A little intoxicated; fuddled; stupid with liquor; bousy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bopeep', 'n.', 'The act of looking out suddenly, as from behind a screen,\n   so as to startle some one (as by children in play); or of looking out\n   and drawing suddenly back, as if frightened.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borable', 'a.', 'Capable of being bored.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borachte', 'n.', 'A large leather bottle for liquors, etc., made of the\n   skin of a goat or other animal. Hence: A drunkard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boracic', 'a.', 'Pertaining to, or produced from, borax; containing boron;\n   boric; as, boracic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boracite', 'n.', 'A mineral of a white or gray color occurring massive and\n   in isometric crystals; in composition it is a magnesium borate with\n   magnesium chloride.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boracous', 'a.', 'Relating to, or obtained from, borax; containing borax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borage', 'n.', 'A mucilaginous plant of the genus Borago (B. officinalis);\n   which is used, esp. in France, as a demulcent and diaphoretic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boragewort', 'n.', 'Plant of the Borage family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boraginaceous', 'a.', 'Of, pertaining to, or resembling, a family of plants\n   (Boraginaceae) which includes the borage, heliotrope, beggars lice,\n   and many pestiferous plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boragineous', 'a.', 'Relating to the Borage tribe; boraginaceous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boramez', 'n.', 'See Barometz.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borate', 'n.', 'A salt formed by the combination of boric acid with a base\n   or positive radical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borax', 'n.', 'A white or gray crystalline salt, with a slight alkaline\n   taste, used as a flux, in soldering metals, making enamels, fixing\n   colors on porcelain, and as a soap. It occurs native in certain mineral\n   springs, and is made from the boric acid of hot springs in Tuscany. It\n   was originally obtained from a lake in Thibet, and was sent to Europe\n   under the name of tincal. Borax is a pyroborate or tetraborate of\n   sodium, Na2B4O7.10H2O.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borborygm', 'n.', 'A rumbling or gurgling noise produced by wind in the\n   bowels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bord', 'n.', 'A board; a table.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bord', 'n.', 'The face of coal parallel to the natural fissures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bord', 'n.', 'See Bourd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordage', 'n.', 'The base or servile tenure by which a bordar held his\n   cottage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordar', 'n.', 'A villein who rendered menial service for his cottage; a\n   cottier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordeaux', 'a.', 'Pertaining to Bordeaux in the south of France.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordeaux', 'n.', 'A claret wine from Bordeaux.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordel', 'n.', 'Alt. of Bordello');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordello', 'n.', 'A brothel; a bawdyhouse; a house devoted to prostitution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordelais', 'a.', 'Of or pertaining to Bordeaux, in France, or to the\n   district around Bordeaux.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordeller', 'n.', 'A keeper or a frequenter of a brothel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'n.', 'The outer part or edge of anything, as of a garment, a\n   garden, etc.; margin; verge; brink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'n.', 'A boundary; a frontier of a state or of the settled part of\n   a country; a frontier district.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'n.', 'A strip or stripe arranged along or near the edge of\n   something, as an ornament or finish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'n.', 'A narrow flower bed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordered', 'imp. & p. p.', 'of Border');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordering', 'p. pr. & vb. n.', 'of Border');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'v. i.', 'To touch at the edge or boundary; to be contiguous or\n   adjacent; -- with on or upon as, Connecticut borders on Massachusetts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'v. i.', 'To approach; to come near to; to verge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'v. t.', 'To make a border for; to furnish with a border, as for\n   ornament; as, to border a garment or a garden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'v. t.', 'To be, or to have, contiguous to; to touch, or be\n   touched, as by a border; to be, or to have, near the limits or\n   boundary; as, the region borders a forest, or is bordered on the north\n   by a forest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Border', 'v. t.', 'To confine within bounds; to limit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borderer', 'n.', 'One who dwells on a border, or at the extreme part or\n   confines of a country, region, or tract of land; one who dwells near to\n   a place or region.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordland', 'n.', 'Either land held by a bordar, or the land which a lord\n   kept for the maintenance of his board, or table.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordlode', 'n.', 'The service formerly required of a tenant, to carry\n   timber from the woods to the lords house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordman', 'n.', 'A bordar; a tenant in bordage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordrag', 'n.', 'Alt. of Bordraging');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordraging', 'n.', 'An incursion upon the borders of a country; a raid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bord service', '', 'Service due from a bordar; bordage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bordure', 'n.', 'A border one fifth the width of the shield, surrounding\n   the field. It is usually plain, but may be charged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bored', 'imp. & p. p.', 'of Bore');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boring', 'p. pr. & vb. n.', 'of Bore');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. t.', 'To perforate or penetrate, as a solid body, by turning an\n   auger, gimlet, drill, or other instrument; to make a round hole in or\n   through; to pierce; as, to bore a plank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. t.', 'To form or enlarge by means of a boring instrument or\n   apparatus; as, to bore a steam cylinder or a gun barrel; to bore a\n   hole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. t.', 'To make (a passage) by laborious effort, as in boring; as,\n   to bore ones way through a crowd; to force a narrow and difficult\n   passage through.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. t.', 'To weary by tedious iteration or by dullness; to tire; to\n   trouble; to vex; to annoy; to pester.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. t.', 'To befool; to trick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. i.', 'To make a hole or perforation with, or as with, a boring\n   instrument; to cut a circular hole by the rotary motion of a tool; as,\n   to bore for water or oil (i. e., to sink a well by boring for water or\n   oil); to bore with a gimlet; to bore into a tree (as insects).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. i.', 'To be pierced or penetrated by an instrument that cuts as\n   it turns; as, this timber does not bore well, or is hard to bore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. i.', 'To push forward in a certain direction with laborious\n   effort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'v. i.', 'To shoot out the nose or toss it in the air; -- said of a\n   horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'A hole made by boring; a perforation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'The internal cylindrical cavity of a gun, cannon, pistol, or\n   other firearm, or of a pipe or tube.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'The size of a hole; the interior diameter of a tube or gun\n   barrel; the caliber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'A tool for making a hole by boring, as an auger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'Caliber; importance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'A person or thing that wearies by prolixity or dullness; a\n   tiresome person or affair; any person or thing which causes ennui.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'A tidal flood which regularly or occasionally rushes into\n   certain rivers of peculiar configuration or location, in one or more\n   waves which present a very abrupt front of considerable height,\n   dangerous to shipping, as at the mouth of the Amazon, in South America,\n   the Hoogly and Indus, in India, and the Tsien-tang, in China.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', 'n.', 'Less properly, a very high and rapid tidal flow, when not so\n   abrupt, such as occurs at the Bay of Fundy and in the British Channel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bore', '', 'imp. of 1st & 2d Bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boreal', 'a.', 'Northern; pertaining to the north, or to the north wind;\n   as, a boreal bird; a boreal blast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boreas', 'n.', 'The north wind; -- usually a personification.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borecole', 'n.', 'A brassicaceous plant of many varieties, cultivated for\n   its leaves, which are not formed into a compact head like the cabbage,\n   but are loose, and are generally curled or wrinkled; kale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boredom', 'n.', 'The state of being bored, or pestered; a state of ennui.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boredom', 'n.', 'The realm of bores; bores, collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boree', 'n.', 'Same as BourrEe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borel', 'n.', 'See Borrel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borele', 'n.', 'The smaller two-horned rhinoceros of South Africa (Atelodus\n   bicornis).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borer', 'n.', 'One that bores; an instrument for boring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borer', 'n.', 'A marine, bivalve mollusk, of the genus Teredo and allies,\n   which burrows in wood. See Teredo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borer', 'n.', 'Any bivalve mollusk (Saxicava, Lithodomus, etc.) which bores\n   into limestone and similar substances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borer', 'n.', 'One of the larvae of many species of insects, which\n   penetrate trees, as the apple, peach, pine, etc. See Apple borer, under\n   Apple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borer', 'n.', 'The hagfish (Myxine).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boric', 'a.', 'Of, pertaining to, or containing, boron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boride', 'n.', 'A binary compound of boron with a more positive or basic\n   element or radical; -- formerly called boruret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boring', 'n.', 'The act or process of one who, or that which, bores; as,\n   the boring of cannon; the boring of piles and ship timbers by certain\n   marine mollusks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boring', 'n.', 'A hole made by boring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boring', 'n.', 'The chips or fragments made by boring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Born', 'v. t.', 'Brought forth, as an animal; brought into life; introduced\n   by birth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Born', 'v. t.', 'Having from birth a certain character; by or from birth;\n   by nature; innate; as, a born liar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borne', 'p. p.', 'Carried; conveyed; supported; defrayed. See Bear, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borneol', 'n.', 'A rare variety of camphor, C10H17.OH, resembling ordinary\n   camphor, from which it can be produced by reduction. It is said to\n   occur in the camphor tree of Borneo and Sumatra (Dryobalanops\n   camphora); but the natural borneol is rarely found in European or\n   American commerce, being in great request by the Chinese. Called also\n   Borneo camphor, Malay camphor, and camphol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bornite', 'n.', 'A valuable ore of copper, containing copper, iron, and\n   sulphur; -- also called purple copper ore (or erubescite); in allusion\n   to the colors shown upon the slightly tarnished surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borofluoride', 'n.', 'A double fluoride of boron and hydrogen, or some\n   other positive element, or radical; -- called also fluoboride, and\n   formerly fluoborate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroglyceride', 'n.', 'A compound of boric acid and glycerin, used as an\n   antiseptic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boron', 'n.', 'A nonmetallic element occurring abundantly in borax. It is\n   reduced with difficulty to the free state, when it can be obtained in\n   several different forms; viz., as a substance of a deep olive color, in\n   a semimetallic form, and in colorless quadratic crystals similar to the\n   diamond in hardness and other properties. It occurs in nature also in\n   boracite, datolite, tourmaline, and some other minerals. Atomic weight\n   10.9. Symbol B.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borosilicate', 'n.', 'A double salt of boric and silicic acids, as in the\n   natural minerals tourmaline, datolite, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borough', 'n.', 'In England, an incorporated town that is not a city; also,\n   a town that sends members to parliament; in Scotland, a body corporate,\n   consisting of the inhabitants of a certain district, erected by the\n   sovereign, with a certain jurisdiction; in America, an incorporated\n   town or village, as in Pennsylvania and Connecticut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borough', 'n.', 'The collective body of citizens or inhabitants of a\n   borough; as, the borough voted to lay a tax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borough', 'n.', 'An association of men who gave pledges or sureties to the\n   king for the good behavior of each other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borough', 'n.', 'The pledge or surety thus given.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borough-English', 'n.', 'A custom, as in some ancient boroughs, by which\n   lands and tenements descend to the youngest son, instead of the eldest;\n   or, if the owner have no issue, to the youngest brother.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughhead', 'n.', 'See Headborough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughholder', 'n.', 'A headborough; a borsholder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughmaster', 'n.', 'The mayor, governor, or bailiff of a borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughmonger', 'n.', 'One who buys or sells the parliamentary seats of\n   boroughs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughmongering', 'n.', 'Alt. of Boroughmongery');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boroughmongery', 'n.', 'The practices of a boroughmonger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borracho', 'n.', 'See Borachio.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrage', 'a.', 'Alt. of Borraginaceous');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borraginaceous', 'a.', 'See Borage, n., etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrel', 'n.', 'Coarse woolen cloth; hence, coarse clothing; a garment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrel', 'n.', 'A kind of light stuff, of silk and wool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrel', 'n.', 'Ignorant, unlearned; belonging to the laity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrowed', 'imp. & p. p.', 'of Borrow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrowing', 'p. pr. & vb. n.', 'of Borrow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'v. t.', 'To receive from another as a loan, with the implied or\n   expressed intention of returning the identical article or its\n   equivalent in kind; -- the opposite of lend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'v. t.', 'To take (one or more) from the next higher denomination\n   in order to add it to the next lower; -- a term of subtraction when the\n   figure of the subtrahend is larger than the corresponding one of the\n   minuend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'v. t.', 'To copy or imitate; to adopt; as, to borrow the style,\n   manner, or opinions of another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'v. t.', 'To feign or counterfeit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'v. t.', 'To receive; to take; to derive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'n.', 'Something deposited as security; a pledge; a surety; a\n   hostage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrow', 'n.', 'The act of borrowing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borrower', 'n.', 'One who borrows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borsholder', 'a.', 'The head or chief of a tithing, or borough (see 2d\n   Borough); the headborough; a parish constable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bort', 'n.', 'Imperfectly crystallized or coarse diamonds, or fragments\n   made in cutting good diamonds which are reduced to powder and used in\n   lapidary work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boruret', 'n.', 'A boride.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Borwe', 'n.', 'Pledge; borrow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bos', 'n.', 'A genus of ruminant quadrupeds, including the wild and\n   domestic cattle, distinguished by a stout body, hollow horns, and a\n   large fold of skin hanging from the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosa', 'n.', 'A drink, used in the East. See Boza.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boscage', 'n.', 'A growth of trees or shrubs; underwood; a thicket; thick\n   foliage; a wooded landscape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boscage', 'n.', 'Food or sustenance for cattle, obtained from bushes and\n   trees; also, a tax on wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosh', 'n.', 'Figure; outline; show.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosh', 'n.', 'Empty talk; contemptible nonsense; trash; humbug.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boshes', 'pl. ', 'of Bosh');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosh', 'n.', 'One of the sloping sides of the lower part of a blast\n   furnace; also, one of the hollow iron or brick sides of the bed of a\n   puddling or boiling furnace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosh', 'n.', 'The lower part of a blast furnace, which slopes inward, or\n   the widest space at the top of this part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosh', 'n.', 'In forging and smelting, a trough in which tools and ingots\n   are cooled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boshbok', 'n.', 'A kind of antelope. See Bush buck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boshvark', 'n.', 'The bush hog. See under Bush, a thicket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosjesman', 'n.', 'See Bushman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosk', 'n.', 'A thicket; a small wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boskage', 'n.', 'Same as Boscage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosket', 'n.', 'Alt. of Bosquet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosquet', 'n.', 'A grove; a thicket; shrubbery; an inclosure formed by\n   branches of trees, regularly or irregularly disposed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boskiness', 'n.', 'Boscage; also, the state or quality of being bosky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosky', 'a.', 'Woody or bushy; covered with boscage or thickets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosky', 'a.', 'Caused by boscage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'The breast of a human being; the part, between the arms, to\n   which anything is pressed when embraced by them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'The breast, considered as the seat of the passions,\n   affections, and operations of the mind; consciousness; secret thoughts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'Embrace; loving or affectionate inclosure; fold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'Any thing or place resembling the breast; a supporting\n   surface; an inner recess; the interior; as, the bosom of the earth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'The part of the dress worn upon the breast; an article, or a\n   portion of an article, of dress to be worn upon the breast; as, the\n   bosom of a shirt; a linen bosom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'Inclination; desire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'n.', 'A depression round the eye of a millstone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'a.', 'Of or pertaining to the bosom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'a.', 'Intimate; confidential; familiar; trusted; cherished;\n   beloved; as, a bosom friend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosomed', 'imp. & p. p.', 'of Bosom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosoming', 'p. pr. & vb. n.', 'of Bosom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'v. t.', 'To inclose or carry in the bosom; to keep with care; to\n   take to heart; to cherish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosom', 'v. t.', 'To conceal; to hide from view; to embosom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosomed', 'a.', 'Having, or resembling, bosom; kept in the bosom; hidden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosomy', 'a.', 'Characterized by recesses or sheltered hollows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boson', 'n.', 'See Boatswain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosporian', 'a.', 'Of or pertaining to the Thracian or the Cimmerian\n   Bosporus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosporus', 'n.', 'A strait or narrow sea between two seas, or a lake and a\n   seas; as, the Bosporus (formerly the Thracian Bosporus) or Strait of\n   Constantinople, between the Black Sea and Sea of Marmora; the Cimmerian\n   Bosporus, between the Black Sea and Sea of Azof.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosquet', 'n.', 'See Bosket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosses', 'pl. ', 'of Boss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'Any protuberant part; a round, swelling part or body; a\n   knoblike process; as, a boss of wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A protuberant ornament on any work, either of different\n   material from that of the work or of the same, as upon a buckler or\n   bridle; a stud; a knob; the central projection of a shield. See\n   Umbilicus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A projecting ornament placed at the intersection of the ribs\n   of ceilings, whether vaulted or flat, and in other situations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A wooden vessel for the mortar used in tiling or masonry,\n   hung by a hook from the laths, or from the rounds of a ladder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'The enlarged part of a shaft, on which a wheel is keyed, or\n   at the end, where it is coupled to another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A swage or die used for shaping metals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A head or reservoir of water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossed', 'imp. & p. p.', 'of Boss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossing', 'p. pr. & vb. n.', 'of Boss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'v. t.', 'To ornament with bosses; to stud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boss', 'n.', 'A master workman or superintendent; a director or manager; a\n   political dictator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossage', 'n.', 'A stone in a building, left rough and projecting, to be\n   afterward carved into shape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossage', 'n.', 'Rustic work, consisting of stones which seem to advance\n   beyond the level of the building, by reason of indentures or channels\n   left in the joinings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossed', 'a.', 'Embossed; also, bossy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bosset', 'n.', 'A rudimental antler of a young male of the red deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossism', 'n.', 'The rule or practices of bosses, esp. political bosses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossy', 'a.', 'Ornamented with bosses; studded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bossy', 'n.', 'A cow or calf; -- familiarly so called.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boston', 'n.', 'A game at cards, played by four persons, with two packs of\n   fifty-two cards each; -- said to be so called from Boston,\n   Massachusetts, and to have been invented by officers of the French army\n   in America during the Revolutionary war.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boswellian', 'a.', 'Relating to, or characteristic of, Boswell, the\n   biographer of Dr. Johnson.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boswellism', 'n.', 'The style of Boswell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bot', 'n.', 'See Bots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanic', 'a.', 'Alt. of Botanical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanical', 'a.', 'Of or pertaining to botany; relating to the study of\n   plants; as, a botanical system, arrangement, textbook, expedition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanist', 'n.', 'One skilled in botany; one versed in the knowledge of\n   plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanized', 'imp. & p. p.', 'of Botanize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanizing', 'p. pr. & vb. n.', 'of Botanize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanize', 'v. i.', 'To seek after plants for botanical investigation; to\n   study plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanize', 'v. t.', 'To explore for botanical purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanizer', 'n.', 'One who botanizes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanologer', 'n.', 'A botanist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanology', 'n.', 'The science of botany.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanomancy', 'n.', 'An ancient species of divination by means of plants,\n   esp. sage and fig leaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botanies', 'pl. ', 'of Botany');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botany', 'a. & n.', 'The science which treats of the structure of plants,\n   the functions of their parts, their places of growth, their\n   classification, and the terms which are employed in their description\n   and denomination. See Plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botany', 'a. & n.', 'A book which treats of the science of botany.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botany Bay', '', 'A harbor on the east coast of Australia, and an English\n   convict settlement there; -- so called from the number of new plants\n   found on its shore at its discovery by Cook in 1770.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botargo', 'n.', 'A sort of cake or sausage, made of the salted roes of the\n   mullet, much used on the coast of the Mediterranean as an incentive to\n   drink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botches', 'pl. ', 'of Botch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'A swelling on the skin; a large ulcerous affection; a boil;\n   an eruptive disease.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'A patch put on, or a part of a garment patched or mended in\n   a clumsy manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'Work done in a bungling manner; a clumsy performance; a\n   piece of work, or a place in work, marred in the doing, or not properly\n   finished; a bungle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botched', 'imp. & p. p.', 'of Botch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botching', 'p. pr. & vb. n.', 'of Botch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'To mark with, or as with, botches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'To repair; to mend; esp. to patch in a clumsy or imperfect\n   manner, as a garment; -- sometimes with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botch', 'n.', 'To put together unsuitably or unskillfully; to express or\n   perform in a bungling manner; to spoil or mar, as by unskillful work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botchedly', 'adv.', 'In a clumsy manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botcher', 'n.', 'One who mends or patches, esp. a tailor or cobbler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botcher', 'n.', 'A clumsy or careless workman; a bungler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botcher', 'n.', 'A young salmon; a grilse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botcherly', 'a.', 'Bungling; awkward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botchery', 'n.', 'A botching, or that which is done by botching; clumsy or\n   careless workmanship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botchy', 'a.', 'Marked with botches; full of botches; poorly done.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bote', 'n.', 'Compensation; amends; satisfaction; expiation; as, man bote,\n   a compensation or a man slain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bote', 'n.', 'Payment of any kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bote', 'n.', 'A privilege or allowance of necessaries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boteless', 'a.', 'Unavailing; in vain. See Bootless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botfly', 'n.', 'A dipterous insect of the family (Estridae, of many\n   different species, some of which are particularly troublesome to\n   domestic animals, as the horse, ox, and sheep, on which they deposit\n   their eggs. A common species is one of the botflies of the horse\n   (Gastrophilus equi); the larvae of which (bots) are taken into the\n   stomach of the animal, where they live several months and pass through\n   their larval states. In tropical America one species sometimes lives\n   under the human skin, and another in the stomach. See Gadfly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Both', 'a. or pron.', 'The one and the other; the two; the pair, without\n   exception of either.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Both', 'conj.', 'As well; not only; equally.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothered', 'imp. & p. p.', 'of Bother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothering', 'p. pr. & vb. n.', 'of Bother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bother', 'v. t.', 'To annoy; to trouble; to worry; to perplex. See Pother.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bother', 'v. i.', 'To feel care or anxiety; to make or take trouble; to be\n   troublesome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bother', 'n.', 'One who, or that which, bothers; state of perplexity or\n   annoyance; embarrassment; worry; disturbance; petty trouble; as, to be\n   in a bother.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botheration', 'n.', 'The act of bothering, or state of being bothered;\n   cause of trouble; perplexity; annoyance; vexation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botherer', 'n.', 'One who bothers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothersome', 'a.', 'Vexatious; causing bother; causing trouble or\n   perplexity; troublesome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Both-hands', 'n.', 'A factotum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothie', 'n.', 'Same as Bothy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothnian', 'a.', 'Alt. of Bothnic');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothnic', 'a.', 'Of or pertaining to Bothnia, a country of northern Europe,\n   or to a gulf of the same name which forms the northern part of the\n   Baltic sea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothrenchyma', 'n.', 'Dotted or pitted ducts or vessels forming the pores\n   seen in many kinds of wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-ies', 'pl. ', 'of Boothy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bothy', 'n.', 'Alt. of Boothy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boothy', 'n.', 'A wooden hut or humble cot, esp. a rude hut or barrack for\n   unmarried farm servants; a shepherds or hunters hut; a booth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botocudos', 'n. pl.', 'A Brazilian tribe of Indians, noted for their use of\n   poisons; -- also called Aymbores.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bo tree', '', 'The peepul tree; esp., the very ancient tree standing at\n   Anurajahpoora in Ceylon, grown from a slip of the tree under which\n   Gautama is said to have received the heavenly light and so to have\n   become Buddha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryogen', 'n.', 'A hydrous sulphate of iron of a deep red color. It often\n   occurs in botryoidal form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryoid', 'a.', 'Alt. of Botryoidal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryoidal', 'a.', 'Having the form of a bunch of grapes; like a cluster of\n   grapes, as a mineral presenting an aggregation of small spherical or\n   spheroidal prominences.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryolite', 'n.', 'A variety of datolite, usually having a botryoidal\n   structure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryose', 'a.', 'Having the form of a cluster of grapes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Botryose', 'a.', 'Of the racemose or acropetal type of inflorescence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bots', 'n. pl.', 'The larvae of several species of botfly, especially those\n   larvae which infest the stomach, throat, or intestines of the horse,\n   and are supposed to be the cause of various ailments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottine', 'n.', 'A small boot; a ladys boot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottine', 'n.', 'An appliance resembling a small boot furnished with\n   straps, buckles, etc., used to correct or prevent distortions in the\n   lower extremities of children.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle', 'n.', 'A hollow vessel, usually of glass or earthenware (but\n   formerly of leather); with a narrow neck or mouth, for holding liquids.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle', 'n.', 'The contents of a bottle; as much as a bottle contains; as,\n   to drink a bottle of wine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle', 'n.', 'Fig.: Intoxicating liquor; as, to drown ones reason in the\n   bottle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottled', 'imp. & p. p.', 'of Bottle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottling', 'p. pr. & vb. n.', 'of Bottle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle', 'v. t.', 'To put into bottles; to inclose in, or as in, a bottle\n   or bottles; to keep or restrain as in a bottle; as, to bottle wine or\n   porter; to bottle up ones wrath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle', 'n.', 'A bundle, esp. of hay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottled', 'a.', 'Put into bottles; inclosed in bottles; pent up in, or as\n   in, a bottle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottled', 'a.', 'Having the shape of a bottle; protuberant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle green', '', 'A dark shade of green, like that of bottle glass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottlehead', 'n.', 'A cetacean allied to the grampus; -- called also\n   bottle-nosed whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottleholder', 'n.', 'One who attends a pugilist in a prize fight; -- so\n   called from the bottle of water of which he has charge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottleholder', 'n.', 'One who assists or supports another in a contest; an\n   abettor; a backer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle-nose', 'n.', 'A cetacean of the Dolphin family, of several species,\n   as Delphinus Tursio and Lagenorhyncus leucopleurus, of Europe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle-nose', 'n.', 'The puffin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottle-nosed', 'a.', 'Having the nose bottle-shaped, or large at the end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottler', 'n.', 'One who bottles wine, beer, soda water, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottlescrew', 'n.', 'A corkscrew.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottling', 'n.', 'The act or the process of putting anything into bottles\n   (as beer, mineral water, etc.) and corking the bottles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'The lowest part of anything; the foot; as, the bottom of a\n   tree or well; the bottom of a hill, a lane, or a page.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'The part of anything which is beneath the contents and\n   supports them, as the part of a chair on which a person sits, the\n   circular base or lower head of a cask or tub, or the plank floor of a\n   ships hold; the under surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'That upon which anything rests or is founded, in a literal\n   or a figurative sense; foundation; groundwork.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'The bed of a body of water, as of a river, lake, sea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'The fundament; the buttocks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'An abyss.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'Low land formed by alluvial deposits along a river;\n   low-lying ground; a dale; a valley.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'The part of a ship which is ordinarily under water; hence,\n   the vessel itself; a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'Power of endurance; as, a horse of a good bottom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'Dregs or grounds; lees; sediment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'a.', 'Of or pertaining to the bottom; fundamental; lowest; under;\n   as, bottom rock; the bottom board of a wagon box; bottom prices.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottomed', 'imp. & p. p.', 'of Bottom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottoming', 'p. pr. & vb. n.', 'of Bottom');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'v. t.', 'To found or build upon; to fix upon as a support; --\n   followed by on or upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'v. t.', 'To furnish with a bottom; as, to bottom a chair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'v. t.', 'To reach or get to the bottom of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'v. i.', 'To rest, as upon an ultimate support; to be based or\n   grounded; -- usually with on or upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'v. i.', 'To reach or impinge against the bottom, so as to impede\n   free action, as when the point of a cog strikes the bottom of a space\n   between two other cogs, or a piston the end of a cylinder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bottom', 'n.', 'A ball or skein of thread; a cocoon.');
