  INSERT INTO words (word, word_type, description)VALUES  (    'Boycotted', 'imp. & p. p.', 'of Boycott');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boycotting', 'p. pr. & vb. n.', 'of Boycott');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boycott', 'v. t.', 'To combine against (a landlord, tradesman, employer, or\n   other person); to withhold social or business relations from him, and\n   to deter others from holding such relations; to subject to a boycott.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boycott', 'n.', 'The process, fact, or pressure of boycotting; a combining\n   to withhold or prevent dealing or social intercourse with a tradesman,\n   employer, etc.; social and business interdiction for the purpose of\n   coercion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boycotter', 'n.', 'A participant in boycotting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boycottism', 'n.', 'Methods of boycotters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boydekin', 'n.', 'A dagger; a bodkin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyer', 'n.', 'A Flemish sloop with a castle at each end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyhood', 'n.', 'The state of being a boy; the time during which one is a\n   boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyish', 'a.', 'Resembling a boy in a manners or opinions; belonging to a\n   boy; childish; trifling; puerile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyishly', 'adv.', 'In a boyish manner; like a boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyishness', 'n.', 'The manners or behavior of a boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyism', 'n.', 'Boyhood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Boyism', 'n.', 'The nature of a boy; childishness.');
