  INSERT INTO words (word, word_type, description)VALUES  (    'Boza', 'n.', 'An acidulated fermented drink of the Arabs and Egyptians,\n   made from millet seed and various astringent substances; also, an\n   intoxicating beverage made from hemp seed, darnel meal, and water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brabantine', 'a.', 'Pertaining to Brabant, an ancient province of the\n   Netherlands.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brabble', 'v. i.', 'To clamor; to contest noisily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brabble', 'n.', 'A broil; a noisy contest; a wrangle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brabblement', 'n.', 'A brabble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brabbler', 'n.', 'A clamorous, quarrelsome, noisy fellow; a wrangler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braccate', 'a.', 'Furnished with feathers which conceal the feet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'That which holds anything tightly or supports it firmly; a\n   bandage or a prop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A cord, ligament, or rod, for producing or maintaining\n   tension, as a cord on the side of a drum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'The state of being braced or tight; tension.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A piece of material used to transmit, or change the\n   direction of, weight or pressure; any one of the pieces, in a frame or\n   truss, which divide the structure into triangular parts. It may act as\n   a tie, or as a strut, and serves to prevent distortion of the\n   structure, and transverse strains in its members. A boiler brace is a\n   diagonal stay, connecting the head with the shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A vertical curved line connecting two or more words or\n   lines, which are to be taken together; thus, boll, bowl; or, in music,\n   used to connect staves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A rope reeved through a block at the end of a yard, by which\n   the yard is moved horizontally; also, a rudder gudgeon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A curved instrument or handle of iron or wood, for holding\n   and turning bits, etc.; a bitstock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'A pair; a couple; as, a brace of ducks; now rarely applied\n   to persons, except familiarly or with some contempt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'Straps or bands to sustain trousers; suspenders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'Harness; warlike preparation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'Armor for the arm; vantbrace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'n.', 'The mouth of a shaft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braced', 'imp. & p. p.', 'of Brace');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracing', 'p. pr. & vb. n.', 'of Brace');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. t.', 'To furnish with braces; to support; to prop; as, to brace\n   a beam in a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. t.', 'To draw tight; to tighten; to put in a state of tension;\n   to strain; to strengthen; as, to brace the nerves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. t.', 'To bind or tie closely; to fasten tightly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. t.', 'To place in a position for resisting pressure; to hold\n   firmly; as, he braced himself against the crowd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. t.', 'To move around by means of braces; as, to brace the\n   yards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brace', 'v. i.', 'To get tone or vigor; to rouse ones energies; -- with\n   up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracelet', 'n.', 'An ornamental band or ring, for the wrist or the arm; in\n   modern times, an ornament encircling the wrist, worn by women or girls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracelet', 'n.', 'A piece of defensive armor for the arm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracer', 'n.', 'That which braces, binds, or makes firm; a band or bandage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracer', 'n.', 'A covering to protect the arm of the bowman from the\n   vibration of the string; also, a brassart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracer', 'n.', 'A medicine, as an astringent or a tonic, which gives\n   tension or tone to any part of the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brach', 'n.', 'A bitch of the hound kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachelytra', 'n. pl.', 'A group of beetles having short elytra, as the\n   rove beetles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachia', 'n. pl.', 'See Brachium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachial', 'a.', 'Pertaining or belonging to the arm; as, the brachial\n   artery; the brachial nerve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachial', 'a.', 'Of the nature of an arm; resembling an arm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachiata', 'n. pl.', 'A division of the Crinoidea, including those\n   furnished with long jointed arms. See Crinoidea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachiate', 'a.', 'Having branches in pairs, decussated, all nearly\n   horizontal, and each pair at right angles with the next, as in the\n   maple and lilac.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachioganoid', 'n.', 'One of the Brachioganoidei.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachioganoidei', 'n. pl.', 'An order of ganoid fishes of which the bichir\n   of Africa is a living example. See Crossopterygii.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachiolaria', 'n. pl.', 'A peculiar early larval stage of certain\n   starfishes, having a bilateral structure, and swimming by means of\n   bands of vibrating cilia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachiopod', 'n.', 'One of the Brachiopoda, or its shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachiopoda', 'n.', 'A class of Molluscoidea having a symmetrical bivalve\n   shell, often attached by a fleshy peduncle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracchia', 'pl. ', 'of Brachium');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachium', 'n.', 'The upper arm; the segment of the fore limb between the\n   shoulder and the elbow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachman', 'n.', 'See Brahman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachycatalectic', 'n.', 'A verse wanting two syllables at its termination.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachycephalic', 'a.', 'Alt. of Brachycephalous');
