  INSERT INTO words (word, word_type, description)VALUES  (    'Brachycephaly', 'n.', 'Alt. of Brachycephalism');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachycephalism', 'n.', 'The state or condition of being brachycephalic;\n   shortness of head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyceral', 'a.', 'Having short antennae, as certain insects.');
