  INSERT INTO words (word, word_type, description)VALUES  (    'Brachydiagonal', 'n.', 'The shorter of the diagonals in a rhombic prism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachydome', 'n.', 'A dome parallel to the shorter lateral axis. See Dome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachygrapher', 'n.', 'A writer in short hand; a stenographer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachygraphy', 'n.', 'Stenography.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachylogy', 'n.', 'Conciseness of expression; brevity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachypinacoid', 'n.', 'A plane of an orthorhombic crystal which is\n   parallel both to the vertical axis and to the shorter lateral\n   (brachydiagonal) axis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyptera', 'n. pl.', 'A group of Coleoptera having short wings; the rove\n   beetles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachypteres', 'n.pl.', 'A group of birds, including auks, divers, and\n   penguins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachypterous', 'a.', 'Having short wings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachystochrone', 'n.', 'A curve, in which a body, starting from a given\n   point, and descending solely by the force of gravity, will reach\n   another given point in a shorter time than it could by any other path.\n   This curve of quickest descent, as it is sometimes called, is, in a\n   vacuum, the same as the cycloid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachytypous', 'a.', 'Of a short form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyura', 'n. pl.', 'A group of decapod Crustacea, including the common\n   crabs, characterized by a small and short abdomen, which is bent up\n   beneath the large cephalo-thorax. [Also spelt Brachyoura.] See Crab,\n   and Illustration in Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyural', 'a.', 'Alt. of Brachyurous');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyurous', 'a.', 'Of or pertaining to the Brachyura.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brachyuran', 'n.', 'One of the Brachyura.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracing', 'a.', 'Imparting strength or tone; strengthening; invigorating;\n   as, a bracing north wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracing', 'n.', 'The act of strengthening, supporting, or propping, with a\n   brace or braces; the state of being braced.');
