  INSERT INTO words (word, word_type, description)VALUES  (    'Bracket', 'n.', 'An architectural member, plain or ornamental, projecting\n   from a wall or pier, to support weight falling outside of the same;\n   also, a decorative feature seeming to discharge such an office.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracket', 'n.', 'A piece or combination of pieces, usually triangular in\n   general shape, projecting from, or fastened to, a wall, or other\n   surface, to support heavy bodies or to strengthen angles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracket', 'n.', 'A shot, crooked timber, resembling a knee, used as a\n   support.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracket', 'n.', 'The cheek or side of an ordnance carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bracket', 'n.', 'One of two characters [], used to inclose a reference,\n   explanation, or note, or a part to be excluded from a sentence, to\n   indicate an interpolation, to rectify a mistake, or to supply an\n   omission, and for certain other purposes; -- called also crotchet.');
