  INSERT INTO words (word, word_type, description)VALUES  (    'Brad', 'n.', 'A thin nail, usually small, with a slight projection at the\n   top on one side instead of a head; also, a small wire nail, with a flat\n   circular head; sometimes, a small, tapering, square-bodied finishing\n   nail, with a countersunk head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brad awl', '', 'A straight awl with chisel edge, used to make holes for\n   brads, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bradoon', 'n.', 'Same as Bridoon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brae', 'n.', 'A hillside; a slope; a bank; a hill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bragged', 'imp. & p. p.', 'of Brag');
