  INSERT INTO words (word, word_type, description)VALUES  (    'Brag', 'n.', 'The thing which is boasted of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brag', 'n.', 'A game at cards similar to bluff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brag', 'v. i.', 'Brisk; full of spirits; boasting; pretentious; conceited.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brag', 'adv.', 'Proudly; boastfully.');
