  INSERT INTO words (word, word_type, description)VALUES  (    'Bragger', 'n.', 'One who brags; a boaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bragget', 'n.', 'A liquor made of ale and honey fermented, with spices,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braggingly', 'adv.', 'Boastingly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bragless', 'a.', 'Without bragging.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bragly', 'adv.', 'In a manner to be bragged of; finely; proudly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brahma', 'n.', 'The One First Cause; also, one of the triad of Hindoo gods.\n   The triad consists of Brahma, the Creator, Vishnu, the Preserver, and\n   Siva, the Destroyer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brahma', 'n.', 'A valuable variety of large, domestic fowl, peculiar in\n   having the comb divided lengthwise into three parts, and the legs well\n   feathered. There are two breeds, the dark or penciled, and the light;\n   -- called also Brahmapootra.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brahmans', 'pl. ', 'of Brahmin');
