  INSERT INTO words (word, word_type, description)VALUES  (    'Braiding', 'n.', 'Braids, collectively; trimming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brail', 'n.', 'A thong of soft leather to bind up a hawks wing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brail', 'n.', 'Ropes passing through pulleys, and used to haul in or up the\n   leeches, bottoms, or corners of sails, preparatory to furling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brail', 'n.', 'A stock at each end of a seine to keep it stretched.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brail', 'v. t.', 'To haul up by the brails; -- used with up; as, to brail\n   up a sail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'n.', 'The whitish mass of soft matter (the center of the nervous\n   system, and the seat of consciousness and volition) which is inclosed\n   in the cartilaginous or bony cranium of vertebrate animals. It is\n   simply the anterior termination of the spinal cord, and is developed\n   from three embryonic vesicles, whose cavities are connected with the\n   central canal of the cord; the cavities of the vesicles become the\n   central cavities, or ventricles, and the walls thicken unequally and\n   become the three segments, the fore-, mid-, and hind-brain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'n.', 'The anterior or cephalic ganglion in insects and other\n   invertebrates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'n.', 'The organ or seat of intellect; hence, the understanding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'n.', 'The affections; fancy; imagination.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brained', 'imp. & p. p.', 'of Brain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braining', 'p. pr. & vb. n.', 'of Brain');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'v. t.', 'To dash out the brains of; to kill by beating out the\n   brains. Hence, Fig.: To destroy; to put an end to; to defeat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brain', 'v. t.', 'To conceive; to understand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brained', 'p.a.', 'Supplied with brains.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainish', 'a.', 'Hot-headed; furious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainless', 'a.', 'Without understanding; silly; thoughtless; witless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainpan', 'n.', 'The bones which inclose the brain; the skull; the\n   cranium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainsick', 'a.', 'Disordered in the understanding; giddy; thoughtless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainsickly', 'adv.', 'In a brainsick manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brainy', 'a.', 'Having an active or vigorous mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braise', 'n.', 'Alt. of Braize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braize', 'n.', 'A European marine fish (Pagrus vulgaris) allied to the\n   American scup; the becker. The name is sometimes applied to the related\n   species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braise', 'n.', 'Alt. of Braize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braize', 'n.', 'Charcoal powder; breeze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braize', 'n.', 'Braised meat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braise', 'v. t.', 'To stew or broil in a covered kettle or pan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braiser', 'n.', 'A kettle or pan for braising.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brait', 'n.', 'A rough diamond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braize', 'n.', 'See Braise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', '', 'imp. of Break.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'n.', 'A fern of the genus Pteris, esp. the P. aquilina, common in\n   almost all countries. It has solitary stems dividing into three\n   principal branches. Less properly: Any fern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'n.', 'A thicket; a place overgrown with shrubs and brambles, with\n   undergrowth and ferns, or with canes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'An instrument or machine to break or bruise the woody\n   part of flax or hemp so that it may be separated from the fiber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'An extended handle by means of which a number of men can\n   unite in working a pump, as in a fire engine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A bakers kneading though.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A sharp bit or snaffle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A frame for confining a refractory horse while the smith\n   is shoeing him; also, an inclosure to restrain cattle, horses, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'That part of a carriage, as of a movable battery, or\n   engine, which enables it to turn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'An ancient engine of war analogous to the crossbow and\n   ballista.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A large, heavy harrow for breaking clods after plowing; a\n   drag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A piece of mechanism for retarding or stopping motion by\n   friction, as of a carriage or railway car, by the pressure of rubbers\n   against the wheels, or of clogs or ratchets against the track or\n   roadway, or of a pivoted lever against a wheel or drum in a machine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'An apparatus for testing the power of a steam engine, or\n   other motor, by weighing the amount of friction that the motor will\n   overcome; a friction brake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'A cart or carriage without a body, used in breaking in\n   horses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', 'v. t.', 'An ancient instrument of torture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brakemen', 'pl. ', 'of Brakeman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brakeman', 'n.', 'A man in charge of a brake or brakes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brakeman', 'n.', 'The man in charge of the winding (or hoisting) engine for\n   a mine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braky', 'a.', 'Full of brakes; abounding with brambles, shrubs, or ferns;\n   rough; thorny.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brama', 'n.', 'See Brahma.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramah press', '', 'A hydrostatic press of immense power, invented by\n   Joseph Bramah of London. See under Hydrostatic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramble', 'n.', 'Any plant of the genus Rubus, including the raspberry and\n   blackberry. Hence: Any rough, prickly shrub.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramble', 'n.', 'The brambling or bramble finch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramble bush', '', 'The bramble, or a collection of brambles growing\n   together.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brambled', 'a.', 'Overgrown with brambles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramble net', '', 'A net to catch birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brambling', 'n.', 'The European mountain finch (Fringilla montifringilla);\n   -- called also bramble finch and bramble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brambly', 'a.', 'Pertaining to, resembling, or full of, brambles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brame', 'n.', 'Sharp passion; vexation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bramin', '', 'Alt. of Braminic');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braminic', '', 'See Brahman, Brachmanic, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bran', 'n.', 'The broken coat of the seed of wheat, rye, or other cereal\n   grain, separated from the flour or meal by sifting or bolting; the\n   coarse, chaffy part of ground grain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bran', 'n.', 'The European carrion crow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brancard', 'n.', 'A litter on which a person may be carried.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branches', 'pl. ', 'of Branch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'A shoot or secondary stem growing from the main stem, or\n   from a principal limb or bough of a tree or other plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'Any division extending like a branch; any arm or part\n   connected with the main body of thing; ramification; as, the branch of\n   an antler; the branch of a chandelier; a branch of a river; a branch of\n   a railway.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'Any member or part of a body or system; a distinct article;\n   a section or subdivision; a department.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'One of the portions of a curve that extends outwards to an\n   indefinitely great distance; as, the branches of an hyperbola.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'A line of family descent, in distinction from some other\n   line or lines from the same stock; any descendant in such a line; as,\n   the English branch of a family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'n.', 'A warrant or commission given to a pilot, authorizing him\n   to pilot vessels in certain waters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'a.', 'Diverging from, or tributary to, a main stock, line, way,\n   theme, etc.; as, a branch vein; a branch road or line; a branch topic;\n   a branch store.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branched', 'imp. & p. p.', 'of Branch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branching', 'p. pr. & vb. n.', 'of Branch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'v. i.', 'To shoot or spread in branches; to separate into\n   branches; to ramify.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'v. i.', 'To divide into separate parts or subdivision.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'v. t.', 'To divide as into branches; to make subordinate division\n   in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch', 'v. t.', 'To adorn with needlework representing branches, flowers,\n   or twigs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brancher', 'n.', 'That which shoots forth branches; one who shows growth in\n   various directions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brancher', 'n.', 'A young hawk when it begins to leave the nest and take to\n   the branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchery', 'n.', 'A system of branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiae', 'pl. ', 'of Branchia');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchia', 'n.', 'A gill; a respiratory organ for breathing the air\n   contained in water, such as many aquatic and semiaquatic animals have.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchial', 'a.', 'Of or pertaining to branchiae or gills.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiate', 'a.', 'Furnished with branchiae; as, branchiate segments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiferous', 'a.', 'Having gills; branchiate; as, branchiferous\n   gastropods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiness', 'n.', 'Fullness of branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branching', 'a.', 'Furnished with branches; shooting our branches;\n   extending in a branch or branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branching', 'n.', 'The act or state of separation into branches; division\n   into branches; a division or branch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiogastropoda', 'n. pl.', 'Those Gastropoda that breathe by branchiae,\n   including the Prosobranchiata and Opisthobranchiata.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiomerism', 'n.', 'The state of being made up of branchiate segments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiopod', 'n.', 'One of the Branchiopoda.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiopoda', 'n. pl.', 'An order of Entomostraca; -- so named from the\n   feet of branchiopods having been supposed to perform the function of\n   gills. It includes the fresh-water genera Branchipus, Apus, and\n   Limnadia, and the genus Artemia found in salt lakes. It is also called\n   Phyllopoda. See Phyllopoda, Cladocera. It is sometimes used in a\n   broader sense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiostegal', 'a.', 'Pertaining to the membrane covering the gills of\n   fishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiostegal', 'n.', 'A branchiostegal ray. See Illustration of Branchial\n   arches in Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiostege', '', 'The branchiostegal membrane. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiostegous', 'a.', 'Branchiostegal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiostoma', 'n.', 'The lancelet. See Amphioxus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchiura', 'n. pl.', 'A group of Entomostraca, with suctorial mouths,\n   including species parasitic on fishes, as the carp lice (Argulus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchless', 'a.', 'Destitute of branches or shoots; without any valuable\n   product; barren; naked.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchlet', 'n.', 'A little branch; a twig.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branch pilot', '', 'A pilot who has a branch or commission, as from Trinity\n   House, England, for special navigation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branchy', 'a.', 'Full of branches; having wide-spreading branches;\n   consisting of branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'A burning piece of wood; or a stick or piece of wood\n   partly burnt, whether burning or after the fire is extinct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'A sword, so called from its glittering or flashing\n   brightness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'A mark made by burning with a hot iron, as upon a cask,\n   to designate the quality, manufacturer, etc., of the contents, or upon\n   an animal, to designate ownership; -- also, a mark for a similar\n   purpose made in any other way, as with a stencil. Hence, figurately:\n   Quality; kind; grade; as, a good brand of flour.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'A mark put upon criminals with a hot iron. Hence: Any\n   mark of infamy or vice; a stigma.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'An instrument to brand with; a branding iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'Any minute fungus which produces a burnt appearance in\n   plants. The brands are of many species and several genera of the order\n   Pucciniaei.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branded', 'imp. & p. p.', 'of Brand');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branding', 'p. pr. & vb. n.', 'of Brand');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'To burn a distinctive mark into or upon with a hot iron,\n   to indicate quality, ownership, etc., or to mark as infamous (as a\n   convict).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'To put an actual distinctive mark upon in any other way,\n   as with a stencil, to show quality of contents, name of manufacture,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'Fig.: To fix a mark of infamy, or a stigma, upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand', 'v. t.', 'To mark or impress indelibly, as with a hot iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brander', 'n.', 'One who, or that which, brands; a branding iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brander', 'n.', 'A gridiron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand goose', '', 'A species of wild goose (Branta bernicla) usually called\n   in America brant. See Brant.');
