  INSERT INTO words (word, word_type, description)VALUES  (    'Brandish', 'n.', 'A flourish, as with a weapon, whip, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandisher', 'n.', 'One who brandishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandle', 'v. t. & i.', 'To shake; to totter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandling', 'n.', 'Alt. of Brandlin');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandlin', 'n.', 'Same as Branlin, fish and worm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand-new', 'a.', 'Quite new; bright as if fresh from the forge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brand spore', '', 'One of several spores growing in a series or chain, and\n   produced by one of the fungi called brand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandies', 'pl. ', 'of Brandy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandy', 'n.', 'A strong alcoholic liquor distilled from wine. The name is\n   also given to spirit distilled from other liquors, and in the United\n   States to that distilled from cider and peaches. In northern Europe, it\n   is also applied to a spirit obtained from grain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brandywine', 'n.', 'Brandy.');
