  INSERT INTO words (word, word_type, description)VALUES  (    'Brant', 'n.', 'A species of wild goose (Branta bernicla) -- called also\n   brent and brand goose. The name is also applied to other related\n   species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brant', 'a.', 'Steep.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brantail', 'n.', 'The European redstart; -- so called from the red color of\n   its tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brant-fox', 'n.', 'A kind of fox found in Sweden (Vulpes alopex); smaller\n   than the common fox (V. vulgaris); but probably a variety of it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Branular', 'a.', 'Relating to the brain; cerebral.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brasen', 'a.', 'Same as Brazen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brash', 'a.', 'Hasty in temper; impetuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brash', 'a.', 'Brittle, as wood or vegetables.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brash', 'n.', 'A rash or eruption; a sudden or transient fit of sickness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brash', 'n.', 'Refuse boughs of trees; also, the clippings of hedges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brash', 'n.', 'Broken and angular fragments of rocks underlying alluvial\n   deposits.');
