  INSERT INTO words (word, word_type, description)VALUES  (    'Brasses', 'pl. ', 'of Brass');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'An alloy (usually yellow) of copper and zinc, in variable\n   proportion, but often containing two parts of copper to one part of\n   zinc. It sometimes contains tin, and rarely other metals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'A journal bearing, so called because frequently made of\n   brass. A brass is often lined with a softer metal, when the latter is\n   generally called a white metal lining. See Axle box, Journal Box, and\n   Bearing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'Coin made of copper, brass, or bronze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'Impudence; a brazen face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'Utensils, ornaments, or other articles of brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'A brass plate engraved with a figure or device.\n   Specifically, one used as a memorial to the dead, and generally having\n   the portrait, coat of arms, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass', 'n.', 'Lumps of pyrites or sulphuret of iron, the color of which is\n   near to that of brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassage', 'n.', 'A sum formerly levied to pay the expense of coinage; --\n   now called seigniorage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassart', 'n.', 'Armor for the arm; -- generally used for the whole arm\n   from the shoulder to the wrist, and consisting, in the 15th and 16th\n   centuries, of many parts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brasse', 'n.', 'A spotted European fish of the genus Lucioperca, resembling\n   a perch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassets', 'n.', 'See Brassart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassica', 'n.', 'A genus of plants embracing several species and varieties\n   differing much in appearance and qualities: such as the common cabbage\n   (B. oleracea); broccoli, cauliflowers, etc.; the wild turnip (B.\n   campestris); the common turnip (B. rapa); the rape or coleseed (B.\n   napus); etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassicaceous', 'a.', 'Related to, or resembling, the cabbage, or plants of\n   the Cabbage family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassiness', 'n.', 'The state, condition, or quality of being brassy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brass-visaged', 'a.', 'Impudent; bold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassy', 'a.', 'Of or pertaining to brass; having the nature, appearance,\n   or hardness, of brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brassy', 'a.', 'Impudent; impudently bold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brast', 'v. t. & i.', 'To burst.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brat', 'n.', 'A coarse garment or cloak; also, coarse clothing, in general.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brat', 'n.', 'A coarse kind of apron for keeping the clothes clean; a bib.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brat', 'n.', 'A child; an offspring; -- formerly used in a good sense, but\n   now usually in a contemptuous sense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brat', 'n.', 'The young of an animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brat', 'n.', 'A thin bed of coal mixed with pyrites or carbonate of lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bratsche', 'n.', 'The tenor viola, or viola.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brattice', 'n.', 'A wall of separation in a shaft or gallery used for\n   ventilation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brattice', 'n.', 'Planking to support a roof or wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brattishing', 'n.', 'See Brattice, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brattishing', 'n.', 'Carved openwork, as of a shrine, battlement, or\n   parapet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braunite', 'n.', 'A native oxide of manganese, of dark brownish black\n   color. It was named from a Mr. Braun of Gotha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravade', 'n.', 'Bravado.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravadoes', 'pl. ', 'of Bravado');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravado', 'n.', 'Boastful and threatening behavior; a boastful menace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'superl.', 'Bold; courageous; daring; intrepid; -- opposed to\n   cowardly; as, a brave man; a brave act.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'superl.', 'Having any sort of superiority or excellence; --\n   especially such as in conspicuous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'superl.', 'Making a fine show or display.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'n.', 'A brave person; one who is daring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'n.', 'Specifically, an Indian warrior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'n.', 'A man daring beyond discretion; a bully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'n.', 'A challenge; a defiance; bravado.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braved', 'imp. & p. p.', 'of Brave');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braving', 'p. pr. & vb. n.', 'of Brave');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'v. t.', 'To encounter with courage and fortitude; to set at\n   defiance; to defy; to dare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brave', 'v. t.', 'To adorn; to make fine or showy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravely', 'adv.', 'In a brave manner; courageously; gallantly; valiantly;\n   splendidly; nobly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravely', 'adv.', 'Finely; gaudily; gayly; showily.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravely', 'adv.', 'Well; thrivingly; prosperously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braveness', 'n.', 'The quality of state or being brave.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravery', 'n.', 'The quality of being brave; fearless; intrepidity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravery', 'n.', 'The act of braving; defiance; bravado.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravery', 'n.', 'Splendor; magnificence; showy appearance; ostentation;\n   fine dress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravery', 'n.', 'A showy person; a fine gentleman; a beau.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braving', 'n.', 'A bravado; a boast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravingly', 'adv.', 'In a defiant manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravoes', 'pl. ', 'of Bravo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravo', 'a.', 'A daring villain; a bandit; one who sets law at defiance; a\n   professional assassin or murderer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravo', 'interj.', 'Well done! excellent! an exclamation expressive of\n   applause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bravura', 'n.', 'A florid, brilliant style of music, written for effect, to\n   show the range and flexibility of a singers voice, or the technical\n   force and skill of a performer; virtuoso music.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawled', 'imp. & p. p.', 'of Brawl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawling', 'p. pr. & vb. n.', 'of Brawl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawl', 'v. i.', 'To quarrel noisily and outrageously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawl', 'v. i.', 'To complain loudly; to scold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawl', 'v. i.', 'To make a loud confused noise, as the water of a rapid\n   stream running over stones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawl', 'n.', 'A noisy quarrel; loud, angry contention; a wrangle; a\n   tumult; as, a drunken brawl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawler', 'n.', 'One that brawls; wrangler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawling', 'a.', 'Quarreling; quarrelsome; noisy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawling', 'a.', 'Making a loud confused noise. See Brawl, v. i., 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawlingly', 'adv.', 'In a brawling manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawn', 'n.', 'A muscle; flesh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawn', 'n.', 'Full, strong muscles, esp. of the arm or leg, muscular\n   strength; a protuberant muscular part of the body; sometimes, the arm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawn', 'n.', 'The flesh of a boar; also, the salted and prepared flesh of\n   a boar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawn', 'n.', 'A boar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawned', 'a.', 'Brawny; strong; muscular.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawner', 'n.', 'A boor killed for the table.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawniness', 'n.', 'The quality or state of being brawny.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brawny', 'a.', 'Having large, strong muscles; muscular; fleshy; strong.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braxy', 'n.', 'A disease of sheep. The term is variously applied in\n   different localities.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braxy', 'n.', 'A diseased sheep, or its mutton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brayed', 'imp. & p. p.', 'of Bray');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braying', 'p. pr. & vb. n.', 'of Bray');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'v. t.', 'To pound, beat, rub, or grind small or fine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'v. i.', 'To utter a loud, harsh cry, as an ass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'v. i.', 'To make a harsh, grating, or discordant noise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'v. t.', 'To make or utter with a loud, discordant, or harsh and\n   grating sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'n.', 'The harsh cry of an ass; also, any harsh, grating, or\n   discordant sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bray', 'n.', 'A bank; the slope of a hill; a hill. See Brae, which is now\n   the usual spelling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brayer', 'n.', 'An implement for braying and spreading ink in hand\n   printing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brayer', 'n.', 'One that brays like an ass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braying', 'a.', 'Making a harsh noise; blaring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazed', 'imp. & p. p.', 'of Braze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazing', 'p. pr. & vb. n.', 'of Braze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braze', 'v. i.', 'To solder with hard solder, esp. with an alloy of copper\n   and zinc; as, to braze the seams of a copper pipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braze', 'v. i.', 'To harden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braze', 'v. t.', 'To cover or ornament with brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazen', 'a.', 'Pertaining to, made of, or resembling, brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazen', 'a.', 'Sounding harsh and loud, like resounding brass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazen', 'a.', 'Impudent; immodest; shameless; having a front like brass;\n   as, a brazen countenance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazened', 'imp. & p. p.', 'of Brazen');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazening', 'p. pr. & vb. n.', 'of Brazen');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazen', 'v. t.', 'To carry through impudently or shamelessly; as, to\n   brazen the matter through.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazen-browed', 'a.', 'Shamelessly impudent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazenface', 'n.', 'An impudent or shameless person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazenfaced', 'a.', 'Impudent; shameless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazenly', 'adv.', 'In a bold, impudent manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazenness', 'n.', 'The quality or state of being brazen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazier', 'n.', 'Same as Brasier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Braziletto', 'n.', 'See Brazil wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazilian', 'a.', 'Of or pertaining to Brazil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazilian', 'n.', 'A native or an inhabitant of Brazil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazilin', 'n.', 'A substance contained in both Brazil wood and Sapan wood,\n   from which it is extracted as a yellow crystalline substance which is\n   white when pure. It is colored intensely red by alkalies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazil nut', '', 'An oily, three-sided nut, the seed of the Bertholletia\n   excelsa; the cream nut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazil wood', '', 'The wood of the oriental Caesalpinia Sapan; -- so called\n   before the discovery of America.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brazil wood', '', 'A very heavy wood of a reddish color, imported from\n   Brazil and other tropical countries, for cabinet-work, and for dyeing.\n   The best is the heartwood of Caesalpinia echinata, a leguminous tree;\n   but other trees also yield it. An inferior sort comes from Jamaica, the\n   timber of C. Braziliensis and C. crista. This is often distinguished as\n   Braziletto , but the better kind is also frequently so named.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'The act of breaking, in a figurative sense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'Specifically: A breaking or infraction of a law, or of any\n   obligation or tie; violation; non-fulfillment; as, a breach of\n   contract; a breach of promise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A gap or opening made made by breaking or battering, as in\n   a wall or fortification; the space between the parts of a solid body\n   rent by violence; a break; a rupture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A breaking of waters, as over a vessel; the waters\n   themselves; surge; surf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A breaking up of amicable relations; rupture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A bruise; a wound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A hernia; a rupture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'n.', 'A breaking out upon; an assault.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breached', 'imp. & p. p.', 'of Breach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaching', 'p. pr. & vb. n.', 'of Breach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'v. t.', 'To make a breach or opening in; as, to breach the walls\n   of a city.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breach', 'v. i.', 'To break the water, as by leaping out; -- said of a\n   whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breachy', 'a.', 'Apt to break fences or to break out of pasture; unruly;\n   as, breachy cattle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bread', 'a.', 'To spread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bread', 'n.', 'An article of food made from flour or meal by moistening,\n   kneading, and baking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bread', 'n.', 'Food; sustenance; support of life, in general.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bread', 'v. t.', 'To cover with bread crumbs, preparatory to cooking; as,\n   breaded cutlets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadbasket', 'n.', 'The stomach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadcorn', '', 'Corn of grain of which bread is made, as wheat, rye, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaded', 'a.', 'Braided');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaden', 'a.', 'Made of bread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadfruit', 'n.', 'The fruit of a tree (Artocarpus incisa) found in the\n   islands of the Pacific, esp. the South Sea islands. It is of a roundish\n   form, from four to six or seven inches in diameter, and, when baked,\n   somewhat resembles bread, and is eaten as food, whence the name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadfruit', 'n.', 'The tree itself, which is one of considerable size,\n   with large, lobed leaves. Cloth is made from the bark, and the timber\n   is used for many purposes. Called also breadfruit tree and bread tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadless', 'a.', 'Without bread; destitute of food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadroot', 'n.', 'The root of a leguminous plant (Psoralea esculenta);\n   found near the Rocky Mountains. It is usually oval in form, and abounds\n   in farinaceous matter, affording sweet and palatable food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadstuff', 'n.', 'Grain, flour, or meal of which bread is made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadth', 'a.', 'Distance from side to side of any surface or thing;\n   measure across, or at right angles to the length; width.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadthless', 'a.', 'Without breadth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadthways', 'ads.', 'Breadthwise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadthwise', 'ads.', 'In the direction of the breadth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breadthwinner', 'n.', 'The member of a family whose labor supplies the food\n   of the family; one who works for his living.');
  INSERT INTO words (word, word_type, description)VALUES  (    'broke', 'imp.', 'of Break');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brake', '', 'of Break');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'p. p.', 'of Break');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broke', '', 'of Break');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaking', 'p. pr. & vb. n.', 'of Break');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To strain apart; to sever by fracture; to divide with\n   violence; as, to break a rope or chain; to break a seal; to break an\n   axle; to break rocks or coal; to break a lock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To lay open as by breaking; to divide; as, to break a\n   package of goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To lay open, as a purpose; to disclose, divulge, or\n   communicate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To infringe or violate, as an obligation, law, or\n   promise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To interrupt; to destroy the continuity of; to dissolve\n   or terminate; as, to break silence; to break ones sleep; to break\n   ones journey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To destroy the completeness of; to remove a part from;\n   as, to break a set.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To destroy the arrangement of; to throw into disorder; to\n   pierce; as, the cavalry were not able to break the British squares.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To shatter to pieces; to reduce to fragments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To exchange for other money or currency of smaller\n   denomination; as, to break a five dollar bill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To destroy the strength, firmness, or consistency of; as,\n   to break flax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To weaken or impair, as health, spirit, or mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To diminish the force of; to lessen the shock of, as a\n   fall or blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To impart, as news or information; to broach; -- with to,\n   and often with a modified word implying some reserve; as, to break the\n   news gently to the widow; to break a purpose cautiously to a friend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To tame; to reduce to subjection; to make tractable; to\n   discipline; as, to break a horse to the harness or saddle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To destroy the financial credit of; to make bankrupt; to\n   ruin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'To destroy the official character and standing of; to\n   cashier; to dismiss.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To come apart or divide into two or more pieces, usually\n   with suddenness and violence; to part; to burst asunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To open spontaneously, or by pressure from within, as a\n   bubble, a tumor, a seed vessel, a bag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To burst forth; to make its way; to come to view; to\n   appear; to dawn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To burst forth violently, as a storm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To open up; to be scattered; to be dissipated; as, the\n   clouds are breaking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To become weakened in constitution or faculties; to lose\n   health or strength.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To be crushed, or overwhelmed with sorrow or grief; as,\n   my heart is breaking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To fall in business; to become bankrupt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To make an abrupt or sudden change; to change the gait;\n   as, to break into a run or gallop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To fail in musical quality; as, a singers voice breaks\n   when it is strained beyond its compass and a tone or note is not\n   completed, but degenerates into an unmusical sound instead. Also, to\n   change in tone, as a boys voice at puberty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. i.', 'To fall out; to terminate friendship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'An opening made by fracture or disruption.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'An interruption of continuity; change of direction; as, a\n   break in a wall; a break in the deck of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'A projection or recess from the face of a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'An opening or displacement in the circuit, interrupting\n   the electrical current.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'An interruption; a pause; as, a break in friendship; a\n   break in the conversation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'An interruption in continuity in writing or printing, as\n   where there is an omission, an unfilled line, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'The first appearing, as of light in the morning; the\n   dawn; as, the break of day; the break of dawn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'A large four-wheeled carriage, having a straight body and\n   calash top, with the drivers seat in front and the footmans behind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'v. t.', 'A device for checking motion, or for measuring friction.\n   See Brake, n. 9 & 10.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break', 'n.', 'See Commutator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakable', 'a.', 'Capable of being broken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakage', 'n.', 'The act of breaking; a break; a breaking; also, articles\n   broken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakage', 'n.', 'An allowance or compensation for things broken\n   accidentally, as in transportation or use.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakbone fever', '', 'See Dengue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break-circuit', 'n.', 'A key or other device for breaking an electrical\n   circuit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakdown', 'n.', 'The act or result of breaking down, as of a carriage;\n   downfall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakdown', 'n.', 'A noisy, rapid, shuffling dance engaged in competitively\n   by a number of persons or pairs in succession, as among the colored\n   people of the Southern United States, and so called, perhaps, because\n   the exercise is continued until most of those who take part in it break\n   down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakdown', 'n.', 'Any rude, noisy dance performed by shuffling the feet,\n   usually by one person at a time.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaker', 'n.', 'One who, or that which, breaks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaker', 'n.', 'Specifically: A machine for breaking rocks, or for\n   breaking coal at the mines; also, the building in which such a machine\n   is placed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaker', 'n.', 'A small water cask.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaker', 'n.', 'A wave breaking into foam against the shore, or against a\n   sand bank, or a rock or reef near the surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakfast', 'n.', 'The first meal in the day, or that which is eaten at the\n   first meal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakfast', 'n.', 'A meal after fasting, or food in general.');
  INSERT INTO words (word, word_type, description)VALUES  (    'breakfasted', 'imp. & p. p.', 'of Breakfast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakfasting', 'p. pr. & vb. n.', 'of Breakfast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakfast', 'v. i.', 'To break ones fast in the morning; too eat the first\n   meal in the day.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakfast', 'v. t.', 'To furnish with breakfast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakman', 'n.', 'See Brakeman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakneck', 'n.', 'A fall that breaks the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakneck', 'n.', 'A steep place endangering the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakneck', 'a.', 'Producing danger of a broken neck; as, breakneck speed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Break-up', 'n.', 'Disruption; a separation and dispersion of the parts or\n   members; as, a break-up of an assembly or dinner party; a break-up of\n   the government.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breakwater', 'n.', 'Any structure or contrivance, as a mole, or a wall at\n   the mouth of a harbor, to break the force of waves, and afford\n   protection from their violence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bream', 'n.', 'A European fresh-water cyprinoid fish of the genus Abramis,\n   little valued as food. Several species are known.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bream', 'n.', 'An American fresh-water fish, of various species of Pomotis\n   and allied genera, which are also called sunfishes and pondfishes. See\n   Pondfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bream', 'n.', 'A marine sparoid fish of the genus Pagellus, and allied\n   genera. See Sea Bream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breamed', 'imp. & p. p.', 'of Bream');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breaming', 'p. pr. & vb. n.', 'of Bream');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bream', 'v. t.', 'To clean, as a ships bottom of adherent shells, seaweed,\n   etc., by the application of fire and scraping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'The fore part of the body, between the neck and the belly;\n   the chest; as, the breast of a man or of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'Either one of the protuberant glands, situated on the front\n   of the chest or thorax in the female of man and of some other mammalia,\n   in which milk is secreted for the nourishment of the young; a mamma; a\n   teat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'Anything resembling the human breast, or bosom; the front\n   or forward part of anything; as, a chimney breast; a plow breast; the\n   breast of a hill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'The face of a coal working.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'The front of a furnace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'The seat of consciousness; the repository of thought and\n   self-consciousness, or of secrets; the seat of the affections and\n   passions; the heart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'n.', 'The power of singing; a musical voice; -- so called,\n   probably, from the connection of the voice with the lungs, which lie\n   within the breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breasted', 'imp. & p. p.', 'of Breast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breasting', 'p. pr. & vb. n.', 'of Breast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast', 'v. t.', 'To meet, with the breast; to struggle with or oppose\n   manfully; as, to breast the storm or waves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastband', 'n.', 'A band for the breast. Specifically: (Naut.) A band of\n   canvas, or a rope, fastened at both ends to the rigging, to support the\n   man who heaves the lead in sounding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastbeam', 'n.', 'The front transverse beam of a locomotive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastbone', 'n.', 'The bone of the breast; the sternum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast-deep', 'a.', 'Deep as from the breast to the feet; as high as the\n   breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breasted', 'a.', 'Having a breast; -- used in composition with qualifying\n   words, in either a literal or a metaphorical sense; as, a\n   single-breasted coat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastfast', 'n.', 'A large rope to fasten the midship part of a ship to a\n   wharf, or to another vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastheight', 'n.', 'The interior slope of a fortification, against which\n   the garrison lean in firing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breast-high', 'a.', 'High as the breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breasthook', 'n.', 'A thick piece of timber in the form of a knee, placed\n   across the stem of a ship to strengthen the fore part and unite the\n   bows on each side.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breasting', 'n.', 'The curved channel in which a breast wheel turns. It is\n   closely adapted to the curve of the wheel through about a quarter of\n   its circumference, and prevents the escape of the water until it has\n   spent its force upon the wheel. See Breast wheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastknot', 'n.', 'A knot of ribbons worn on the breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastpin', 'n.', 'A pin worn on the breast for a fastening, or for\n   ornament; a brooch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplate', 'n.', 'A plate of metal covering the breast as defensive\n   armor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplate', 'n.', 'A piece against which the workman presses his breast\n   in operating a breast drill, or other similar tool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplate', 'n.', 'A strap that runs across a horses breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplate', 'n.', 'A part of the vestment of the high priest, worn upon\n   the front of the ephod. It was a double piece of richly embroidered\n   stuff, a span square, set with twelve precious stones, on which were\n   engraved the names of the twelve tribes of Israel. See Ephod.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplow', 'n.', 'Alt. of Breastplough');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastplough', 'n.', 'A kind of plow, driven by the breast of the workman;\n   -- used to cut or pare turf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastrail', 'n.', 'The upper rail of any parapet of ordinary height, as of\n   a balcony; the railing of a quarter-deck, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastrope', 'n.', 'See Breastband.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastsummer', 'n.', 'A summer or girder extending across a building flush\n   with, and supporting, the upper part of a front or external wall; a\n   long lintel; a girder; -- used principally above shop windows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastwheel', 'n.', 'A water wheel, on which the stream of water strikes\n   neither so high as in the overshot wheel, nor so low as in the\n   undershot, but generally at about half the height of the wheel, being\n   kept in contact with it by the breasting. The water acts on the float\n   boards partly by impulse, partly by its weight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastwork', 'n.', 'A defensive work of moderate height, hastily thrown up,\n   of earth or other material.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breastwork', 'n.', 'A railing on the quarter-deck and forecastle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'The air inhaled and exhaled in respiration; air which, in\n   the process of respiration, has parted with oxygen and has received\n   carbonic acid, aqueous vapor, warmth, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'The act of breathing naturally or freely; the power or\n   capacity to breathe freely; as, I am out of breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'The power of respiration, and hence, life.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'Time to breathe; respite; pause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'A single respiration, or the time of making it; a single\n   act; an instant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'Fig.: That which gives or strengthens life.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'A single word; the slightest effort; a trifle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'A very slight breeze; air in gentle motion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'Fragrance; exhalation; odor; perfume.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breath', 'n.', 'Gentle exercise, causing a quicker respiration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathable', 'a.', 'Such as can be breathed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathableness', 'n.', 'State of being breathable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathed', 'imp. & p. p.', 'of Breathe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'p. pr. & vb. n.', 'of Breathe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. i.', 'To respire; to inhale and exhale air; hence;, to live.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. i.', 'To take breath; to rest from action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. i.', 'To pass like breath; noiselessly or gently; to exhale;\n   to emanate; to blow gently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To inhale and exhale in the process of respiration; to\n   respire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To inject by breathing; to infuse; -- with into.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To emit or utter by the breath; to utter softly; to\n   whisper; as, to breathe a vow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To exhale; to emit, as breath; as, the flowers breathe\n   odors or perfumes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To express; to manifest; to give forth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To act upon by the breath; to cause to sound by\n   breathing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To promote free respiration in; to exercise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To suffer to take breath, or recover the natural\n   breathing; to rest; as, to breathe a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To put out of breath; to exhaust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathe', 'v. t.', 'To utter without vocality, as the nonvocal consonants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breather', 'n.', 'One who breathes. Hence: (a) One who lives.(b) One who\n   utters. (c) One who animates or inspires.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breather', 'n.', 'That which puts one out of breath, as violent exercise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathful', 'a.', 'Full of breath; full of odor; fragrant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Respiration; the act of inhaling and exhaling air.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Air in gentle motion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Any gentle influence or operation; inspiration; as, the\n   breathings of the Spirit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Aspiration; secret prayer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Exercising; promotion of respiration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Utterance; communication or publicity by words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Breathing place; vent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Stop; pause; delay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'Also, in a wider sense, the sound caused by the friction\n   of the outgoing breath in the throat, mouth, etc., when the glottis is\n   wide open; aspiration; the sound expressed by the letter h.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathing', 'n.', 'A mark to indicate aspiration or its absence. See Rough\n   breathing, Smooth breathing, below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathless', 'a.', 'Spent with labor or violent action; out of breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathless', 'a.', 'Not breathing; holding the breath, on account of fear,\n   expectation, or intense interest; attended with a holding of the\n   breath; as, breathless attention.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathless', 'a.', 'Dead; as, a breathless body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathlessly', 'adv.', 'In a breathless manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breathlessness', 'n.', 'The state of being breathless or out of breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breccia', 'n.', 'A rock composed of angular fragments either of the same\n   mineral or of different minerals, etc., united by a cement, and\n   commonly presenting a variety of colors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brecciated', 'a.', 'Consisting of angular fragments cemented together;\n   resembling breccia in appearance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bred', '', 'imp. & p. p. of Breed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brede', 'n.', 'Alt. of Breede');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breede', 'n.', 'Breadth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brede', 'n.', 'A braid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'n.', 'The lower part of the body behind; the buttocks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'n.', 'Breeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'n.', 'The hinder part of anything; esp., the part of a cannon, or\n   other firearm, behind the chamber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'n.', 'The external angle of knee timber, the inside of which is\n   called the throat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeched', 'imp. & p. p.', 'of Breech');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeching', 'p. pr. & vb. n.', 'of Breech');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'v. t.', 'To put into, or clothe with, breeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'v. t.', 'To cover as with breeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'v. t.', 'To fit or furnish with a breech; as, to breech a gun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'v. t.', 'To whip on the breech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech', 'v. t.', 'To fasten with breeching.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breechblock', 'n.', 'The movable piece which closes the breech of a\n   breech-loading firearm, and resists the backward force of the\n   discharge. It is withdrawn for the insertion of a cartridge, and closed\n   again before the gun is fired.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breechcloth', 'n.', 'A cloth worn around the breech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeches', 'n. pl.', 'A garment worn by men, covering the hips and thighs;\n   smallclothes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeches', 'n. pl.', 'Trousers; pantaloons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeching', 'n.', 'A whipping on the breech, or the act of whipping on the\n   breech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeching', 'n.', 'That part of a harness which passes round the breech of\n   a horse, enabling him to hold back a vehicle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeching', 'n.', 'A strong rope rove through the cascabel of a cannon and\n   secured to ringbolts in the ships side, to limit the recoil of the gun\n   when it is discharged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeching', 'n.', 'The sheet iron casing at the end of boilers to convey\n   the smoke from the flues to the smokestack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breechloader', 'n.', 'A firearm which receives its load at the breech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech-loading', 'a.', 'Receiving the charge at the breech instead of at\n   the muzzle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech pin', '', 'Alt. of Breech screw');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech screw', '', 'A strong iron or steel plug screwed into the breech of\n   a musket or other firearm, to close the bottom of the bore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breech sight', '', 'A device attached to the breech of a firearm, to guide\n   the eye, in conjunction with the front sight, in taking aim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bred', 'imp. & p. p.', 'of Breed');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breeding', 'p. pr. & vb. n.', 'of Breed');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To produce as offspring; to bring forth; to bear; to\n   procreate; to generate; to beget; to hatch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To take care of in infancy, and through the age of youth;\n   to bring up; to nurse and foster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To educate; to instruct; to form by education; to train;\n   -- sometimes followed by up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To engender; to cause; to occasion; to originate; to\n   produce; as, to breed a storm; to breed disease.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To give birth to; to be the native place of; as, a pond\n   breeds fish; a northern country breeds stout men.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Breed', 'v. t.', 'To raise, as any kind of stock.');
